INSERT INTO `bible_verses_tr` VALUES ('5899', '47', '12', '21', 'μη παλιν ελθοντα με ταπεινωση ο θεος μου προς υμας και πενθησω πολλους των προημαρτηκοτων και μη μετανοησαντων επι τη ακαθαρσια και πορνεια και ασελγεια η επραξαν');
INSERT INTO `bible_verses_tr` VALUES ('5900', '47', '13', '1', 'τριτον τουτο ερχομαι προς υμας επι στοματος δυο μαρτυρων και τριων σταθησεται παν ρημα');
