INSERT INTO `bible_verses_tr` VALUES ('5908', '47', '13', '9', 'χαιρομεν γαρ οταν ημεις ασθενωμεν υμεις δε δυνατοι ητε τουτο δε και ευχομεθα την υμων καταρτισιν');
INSERT INTO `bible_verses_tr` VALUES ('5909', '47', '13', '10', 'δια τουτο ταυτα απων γραφω ινα παρων μη αποτομως χρησωμαι κατα την εξουσιαν ην εδωκεν μοι ο κυριος εις οικοδομην και ουκ εις καθαιρεσιν');
INSERT INTO `bible_verses_tr` VALUES ('5910', '47', '13', '11', 'λοιπον αδελφοι χαιρετε καταρτιζεσθε παρακαλεισθε το αυτο φρονειτε ειρηνευετε και ο θεος της αγαπης και ειρηνης εσται μεθ υμων');
INSERT INTO `bible_verses_tr` VALUES ('5911', '47', '13', '12', 'ασπασασθε αλληλους εν αγιω φιληματι ασπαζονται υμας οι αγιοι παντες');
INSERT INTO `bible_verses_tr` VALUES ('5912', '47', '13', '13', 'η χαρις του κυριου ιησου χριστου και η αγαπη του θεου και η κοινωνια του αγιου πνευματος μετα παντων υμων αμην [προς κορινθιους δευτερα εγραφη απο φιλιππων της μακεδονιας δια τιτου και λουκα]');
INSERT INTO `bible_verses_tr` VALUES ('5913', '48', '1', '1', 'παυλος αποστολος ουκ απ ανθρωπων ουδε δι ανθρωπου αλλα δια ιησου χριστου και θεου πατρος του εγειραντος αυτον εκ νεκρων');
