INSERT INTO `bible_verses_tr` VALUES ('5918', '48', '1', '6', 'θαυμαζω οτι ουτως ταχεως μετατιθεσθε απο του καλεσαντος υμας εν χαριτι χριστου εις ετερον ευαγγελιον');
