INSERT INTO `bible_verses_tr` VALUES ('5921', '48', '1', '9', 'ως προειρηκαμεν και αρτι παλιν λεγω ει τις υμας ευαγγελιζεται παρ ο παρελαβετε αναθεμα εστω');
