INSERT INTO `bible_verses_tr` VALUES ('5926', '48', '1', '14', 'και προεκοπτον εν τω ιουδαισμω υπερ πολλους συνηλικιωτας εν τω γενει μου περισσοτερως ζηλωτης υπαρχων των πατρικων μου παραδοσεων');
