INSERT INTO `bible_verses_tr` VALUES ('5929', '48', '1', '17', 'ουδε ανηλθον εις ιεροσολυμα προς τους προ εμου αποστολους αλλ απηλθον εις αραβιαν και παλιν υπεστρεψα εις δαμασκον');
INSERT INTO `bible_verses_tr` VALUES ('5930', '48', '1', '18', 'επειτα μετα ετη τρια ανηλθον εις ιεροσολυμα ιστορησαι πετρον και επεμεινα προς αυτον ημερας δεκαπεντε');
