INSERT INTO `bible_verses_tr` VALUES ('6320', '50', '4', '23', 'η χαρις του κυριου ημων ιησου χριστου μετα παντων υμων αμην [προς φιλιππησιους εγραφη απο ρωμης δι επαφροδιτου]');
INSERT INTO `bible_verses_tr` VALUES ('6321', '51', '1', '1', 'παυλος αποστολος ιησου χριστου δια θεληματος θεου και τιμοθεος ο αδελφος');
INSERT INTO `bible_verses_tr` VALUES ('6322', '51', '1', '2', 'τοις εν  {VAR1: κολασσαις } {VAR2: κολοσσαις } αγιοις και πιστοις αδελφοις εν χριστω χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6323', '51', '1', '3', 'ευχαριστουμεν τω θεω και πατρι του κυριου ημων ιησου χριστου παντοτε περι υμων προσευχομενοι');
INSERT INTO `bible_verses_tr` VALUES ('6324', '51', '1', '4', 'ακουσαντες την πιστιν υμων εν χριστω ιησου και την αγαπην την εις παντας τους αγιους');
INSERT INTO `bible_verses_tr` VALUES ('6325', '51', '1', '5', 'δια την ελπιδα την αποκειμενην υμιν εν τοις ουρανοις ην προηκουσατε εν τω λογω της αληθειας του ευαγγελιου');
INSERT INTO `bible_verses_tr` VALUES ('6326', '51', '1', '6', 'του παροντος εις υμας καθως και εν παντι τω κοσμω και εστιν καρποφορουμενον καθως και εν υμιν αφ ης ημερας ηκουσατε και επεγνωτε την χαριν του θεου εν αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('6327', '51', '1', '7', 'καθως και εμαθετε απο επαφρα του αγαπητου συνδουλου ημων ος εστιν πιστος υπερ υμων διακονος του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6328', '51', '1', '8', 'ο και δηλωσας ημιν την υμων αγαπην εν πνευματι');
INSERT INTO `bible_verses_tr` VALUES ('6329', '51', '1', '9', 'δια τουτο και ημεις αφ ης ημερας ηκουσαμεν ου παυομεθα υπερ υμων προσευχομενοι και αιτουμενοι ινα πληρωθητε την επιγνωσιν του θεληματος αυτου εν παση σοφια και συνεσει πνευματικη');
INSERT INTO `bible_verses_tr` VALUES ('6330', '51', '1', '10', 'περιπατησαι υμας αξιως του κυριου εις πασαν αρεσκειαν εν παντι εργω αγαθω καρποφορουντες και αυξανομενοι εις την επιγνωσιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6331', '51', '1', '11', 'εν παση δυναμει δυναμουμενοι κατα το κρατος της δοξης αυτου εις πασαν υπομονην και μακροθυμιαν μετα χαρας');
INSERT INTO `bible_verses_tr` VALUES ('6332', '51', '1', '12', 'ευχαριστουντες τω πατρι τω ικανωσαντι ημας εις την μεριδα του κληρου των αγιων εν τω φωτι');
INSERT INTO `bible_verses_tr` VALUES ('6333', '51', '1', '13', 'ος ερρυσατο ημας εκ της εξουσιας του σκοτους και μετεστησεν εις την βασιλειαν του υιου της αγαπης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6334', '51', '1', '14', 'εν ω εχομεν την απολυτρωσιν δια του αιματος αυτου την αφεσιν των αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('6335', '51', '1', '15', 'ος εστιν εικων του θεου του αορατου πρωτοτοκος πασης κτισεως');
INSERT INTO `bible_verses_tr` VALUES ('6336', '51', '1', '16', 'οτι εν αυτω εκτισθη τα παντα τα εν τοις ουρανοις και τα επι της γης τα ορατα και τα αορατα ειτε θρονοι ειτε κυριοτητες ειτε αρχαι ειτε εξουσιαι τα παντα δι αυτου και εις αυτον εκτισται');
INSERT INTO `bible_verses_tr` VALUES ('6337', '51', '1', '17', 'και αυτος εστιν προ παντων και τα παντα εν αυτω συνεστηκεν');
INSERT INTO `bible_verses_tr` VALUES ('6338', '51', '1', '18', 'και αυτος εστιν η κεφαλη του σωματος της εκκλησιας ος εστιν αρχη πρωτοτοκος εκ των νεκρων ινα γενηται εν πασιν αυτος πρωτευων');
INSERT INTO `bible_verses_tr` VALUES ('6339', '51', '1', '19', 'οτι εν αυτω ευδοκησεν παν το πληρωμα κατοικησαι');
INSERT INTO `bible_verses_tr` VALUES ('6340', '51', '1', '20', 'και δι αυτου αποκαταλλαξαι τα παντα εις αυτον ειρηνοποιησας δια του αιματος του σταυρου αυτου δι αυτου ειτε τα επι της γης ειτε τα εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('6341', '51', '1', '21', 'και υμας ποτε οντας απηλλοτριωμενους και εχθρους τη διανοια εν τοις εργοις τοις πονηροις νυνι δε αποκατηλλαξεν');
INSERT INTO `bible_verses_tr` VALUES ('6342', '51', '1', '22', 'εν τω σωματι της σαρκος αυτου δια του θανατου παραστησαι υμας αγιους και αμωμους και ανεγκλητους κατενωπιον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6343', '51', '1', '23', 'ειγε επιμενετε τη πιστει τεθεμελιωμενοι και εδραιοι και μη μετακινουμενοι απο της ελπιδος του ευαγγελιου ου ηκουσατε του κηρυχθεντος εν παση τη κτισει τη υπο τον ουρανον ου εγενομην εγω παυλος διακονος');
INSERT INTO `bible_verses_tr` VALUES ('6344', '51', '1', '24', '{VAR2: ος } νυν χαιρω εν τοις παθημασιν μου υπερ υμων και ανταναπληρω τα υστερηματα των θλιψεων του χριστου εν τη σαρκι μου υπερ του σωματος αυτου ο εστιν η εκκλησια');
INSERT INTO `bible_verses_tr` VALUES ('6345', '51', '1', '25', 'ης εγενομην εγω διακονος κατα την οικονομιαν του θεου την δοθεισαν μοι εις υμας πληρωσαι τον λογον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6346', '51', '1', '26', 'το μυστηριον το αποκεκρυμμενον απο των αιωνων και απο των γενεων νυνι δε εφανερωθη τοις αγιοις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6347', '51', '1', '27', 'οις ηθελησεν ο θεος γνωρισαι τις ο πλουτος της δοξης του μυστηριου τουτου εν τοις εθνεσιν ος εστιν χριστος εν υμιν η ελπις της δοξης');
INSERT INTO `bible_verses_tr` VALUES ('6348', '51', '1', '28', 'ον ημεις καταγγελλομεν νουθετουντες παντα ανθρωπον και διδασκοντες παντα ανθρωπον εν παση σοφια ινα παραστησωμεν παντα ανθρωπον τελειον εν χριστω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('6349', '51', '1', '29', 'εις ο και κοπιω αγωνιζομενος κατα την ενεργειαν αυτου την ενεργουμενην εν εμοι εν δυναμει');
INSERT INTO `bible_verses_tr` VALUES ('6350', '51', '2', '1', 'θελω γαρ υμας ειδεναι ηλικον αγωνα εχω περι υμων και των εν λαοδικεια και οσοι ουχ εωρακασιν το προσωπον μου εν σαρκι');
INSERT INTO `bible_verses_tr` VALUES ('6351', '51', '2', '2', 'ινα παρακληθωσιν αι καρδιαι αυτων συμβιβασθεντων εν αγαπη και εις παντα πλουτον της πληροφοριας της συνεσεως εις επιγνωσιν του μυστηριου του θεου και πατρος και του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6352', '51', '2', '3', 'εν ω εισιν παντες οι θησαυροι της σοφιας και της γνωσεως αποκρυφοι');
INSERT INTO `bible_verses_tr` VALUES ('6353', '51', '2', '4', 'τουτο δε λεγω ινα μη τις υμας παραλογιζηται εν πιθανολογια');
INSERT INTO `bible_verses_tr` VALUES ('6354', '51', '2', '5', 'ει γαρ και τη σαρκι απειμι αλλα τω πνευματι συν υμιν ειμι χαιρων και βλεπων υμων την ταξιν και το στερεωμα της εις χριστον πιστεως υμων');
INSERT INTO `bible_verses_tr` VALUES ('6355', '51', '2', '6', 'ως ουν παρελαβετε τον χριστον ιησουν τον κυριον εν αυτω περιπατειτε');
INSERT INTO `bible_verses_tr` VALUES ('6356', '51', '2', '7', 'ερριζωμενοι και εποικοδομουμενοι εν αυτω και βεβαιουμενοι εν τη πιστει καθως εδιδαχθητε περισσευοντες εν αυτη εν ευχαριστια');
INSERT INTO `bible_verses_tr` VALUES ('6357', '51', '2', '8', 'βλεπετε μη τις υμας εσται ο συλαγωγων δια της φιλοσοφιας και κενης απατης κατα την παραδοσιν των ανθρωπων κατα τα στοιχεια του κοσμου και ου κατα χριστον');
INSERT INTO `bible_verses_tr` VALUES ('6358', '51', '2', '9', 'οτι εν αυτω κατοικει παν το πληρωμα της θεοτητος σωματικως');
INSERT INTO `bible_verses_tr` VALUES ('6359', '51', '2', '10', 'και εστε εν αυτω πεπληρωμενοι ος εστιν η κεφαλη πασης αρχης και εξουσιας');
INSERT INTO `bible_verses_tr` VALUES ('6360', '51', '2', '11', 'εν ω και περιετμηθητε περιτομη αχειροποιητω εν τη απεκδυσει του σωματος των αμαρτιων της σαρκος εν τη περιτομη του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6361', '51', '2', '12', 'συνταφεντες αυτω εν τω βαπτισματι εν ω και συνηγερθητε δια της πιστεως της ενεργειας του θεου του εγειραντος αυτον εκ των νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('6362', '51', '2', '13', 'και υμας νεκρους οντας εν τοις παραπτωμασιν και τη ακροβυστια της σαρκος υμων  {VAR1: συνεζωποιησεν } {VAR2: συνεζωοποιησεν } συν αυτω χαρισαμενος  {VAR1: ημιν } {VAR2: υμιν } παντα τα παραπτωματα');
INSERT INTO `bible_verses_tr` VALUES ('6363', '51', '2', '14', 'εξαλειψας το καθ ημων χειρογραφον τοις δογμασιν ο ην υπεναντιον ημιν και αυτο ηρκεν εκ του μεσου προσηλωσας αυτο τω σταυρω');
INSERT INTO `bible_verses_tr` VALUES ('6364', '51', '2', '15', 'απεκδυσαμενος τας αρχας και τας εξουσιας εδειγματισεν εν παρρησια θριαμβευσας αυτους εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('6365', '51', '2', '16', 'μη ουν τις υμας κρινετω εν βρωσει η εν ποσει η εν μερει εορτης η νουμηνιας η σαββατων');
INSERT INTO `bible_verses_tr` VALUES ('6366', '51', '2', '17', 'α εστιν σκια των μελλοντων το δε σωμα του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6367', '51', '2', '18', 'μηδεις υμας καταβραβευετω θελων εν ταπεινοφροσυνη και θρησκεια των αγγελων α μη εωρακεν εμβατευων εικη φυσιουμενος υπο του νοος της σαρκος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6368', '51', '2', '19', 'και ου κρατων την κεφαλην εξ ου παν το σωμα δια των αφων και συνδεσμων επιχορηγουμενον και συμβιβαζομενον αυξει την αυξησιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6369', '51', '2', '20', 'ει ουν απεθανετε συν τω χριστω απο των στοιχειων του κοσμου τι ως ζωντες εν κοσμω δογματιζεσθε');
INSERT INTO `bible_verses_tr` VALUES ('6370', '51', '2', '21', 'μη αψη μηδε γευση μηδε θιγης');
INSERT INTO `bible_verses_tr` VALUES ('6371', '51', '2', '22', 'α εστιν παντα εις φθοραν τη αποχρησει κατα τα ενταλματα και διδασκαλιας των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('6372', '51', '2', '23', 'ατινα εστιν λογον μεν εχοντα σοφιας εν εθελοθρησκεια και ταπεινοφροσυνη και αφειδια σωματος ουκ εν τιμη τινι προς πλησμονην της σαρκος');
INSERT INTO `bible_verses_tr` VALUES ('6373', '51', '3', '1', 'ει ουν συνηγερθητε τω χριστω τα ανω ζητειτε ου ο χριστος εστιν εν δεξια του θεου καθημενος');
INSERT INTO `bible_verses_tr` VALUES ('6374', '51', '3', '2', 'τα ανω φρονειτε μη τα επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('6375', '51', '3', '3', 'απεθανετε γαρ και η ζωη υμων κεκρυπται συν τω χριστω εν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('6376', '51', '3', '4', 'οταν ο χριστος φανερωθη η ζωη ημων τοτε και υμεις συν αυτω φανερωθησεσθε εν δοξη');
INSERT INTO `bible_verses_tr` VALUES ('6377', '51', '3', '5', 'νεκρωσατε ουν τα μελη υμων τα επι της γης πορνειαν ακαθαρσιαν παθος επιθυμιαν κακην και την πλεονεξιαν ητις εστιν ειδωλολατρεια');
INSERT INTO `bible_verses_tr` VALUES ('6378', '51', '3', '6', 'δι α ερχεται η οργη του θεου επι τους υιους της απειθειας');
INSERT INTO `bible_verses_tr` VALUES ('6379', '51', '3', '7', 'εν οις και υμεις περιεπατησατε ποτε οτε εζητε εν αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('6380', '51', '3', '8', 'νυνι δε αποθεσθε και υμεις τα παντα οργην θυμον κακιαν βλασφημιαν αισχρολογιαν εκ του στοματος υμων');
INSERT INTO `bible_verses_tr` VALUES ('6381', '51', '3', '9', 'μη ψευδεσθε εις αλληλους απεκδυσαμενοι τον παλαιον ανθρωπον συν ταις πραξεσιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6382', '51', '3', '10', 'και ενδυσαμενοι τον νεον τον ανακαινουμενον εις επιγνωσιν κατ εικονα του κτισαντος αυτον');
INSERT INTO `bible_verses_tr` VALUES ('6383', '51', '3', '11', 'οπου ουκ ενι ελλην και ιουδαιος περιτομη και ακροβυστια βαρβαρος σκυθης δουλος ελευθερος αλλα τα παντα και εν πασιν χριστος');
INSERT INTO `bible_verses_tr` VALUES ('6384', '51', '3', '12', 'ενδυσασθε ουν ως εκλεκτοι του θεου αγιοι και ηγαπημενοι σπλαγχνα οικτιρμων χρηστοτητα ταπεινοφροσυνην πραοτητα μακροθυμιαν');
INSERT INTO `bible_verses_tr` VALUES ('6385', '51', '3', '13', 'ανεχομενοι αλληλων και χαριζομενοι εαυτοις εαν τις προς τινα εχη μομφην καθως και ο χριστος εχαρισατο υμιν ουτως και υμεις');
INSERT INTO `bible_verses_tr` VALUES ('6386', '51', '3', '14', 'επι πασιν δε τουτοις την αγαπην ητις εστιν συνδεσμος της τελειοτητος');
INSERT INTO `bible_verses_tr` VALUES ('6387', '51', '3', '15', 'και η ειρηνη του θεου βραβευετω εν ταις καρδιαις υμων εις ην και εκληθητε εν ενι σωματι και ευχαριστοι γινεσθε');
INSERT INTO `bible_verses_tr` VALUES ('6388', '51', '3', '16', 'ο λογος του χριστου ενοικειτω εν υμιν πλουσιως εν παση σοφια διδασκοντες και νουθετουντες εαυτους ψαλμοις και υμνοις και ωδαις πνευματικαις εν χαριτι αδοντες εν τη καρδια υμων τω κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6389', '51', '3', '17', 'και παν ο τι αν ποιητε εν λογω η εν εργω παντα εν ονοματι κυριου ιησου ευχαριστουντες τω θεω και πατρι δι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6390', '51', '3', '18', 'αι γυναικες υποτασσεσθε τοις ιδιοις ανδρασιν ως ανηκεν εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6391', '51', '3', '19', 'οι ανδρες αγαπατε τας γυναικας και μη πικραινεσθε προς αυτας');
INSERT INTO `bible_verses_tr` VALUES ('6392', '51', '3', '20', 'τα τεκνα υπακουετε τοις γονευσιν κατα παντα τουτο γαρ εστιν ευαρεστον τω κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6393', '51', '3', '21', 'οι πατερες μη ερεθιζετε τα τεκνα υμων ινα μη αθυμωσιν');
INSERT INTO `bible_verses_tr` VALUES ('6394', '51', '3', '22', 'οι δουλοι υπακουετε κατα παντα τοις κατα σαρκα κυριοις μη εν οφθαλμοδουλειαις ως ανθρωπαρεσκοι αλλ εν απλοτητι καρδιας φοβουμενοι τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('6395', '51', '3', '23', 'και παν ο τι εαν ποιητε εκ ψυχης εργαζεσθε ως τω κυριω και ουκ ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('6396', '51', '3', '24', 'ειδοτες οτι απο κυριου αποληψεσθε την ανταποδοσιν της κληρονομιας τω γαρ κυριω χριστω δουλευετε');
INSERT INTO `bible_verses_tr` VALUES ('6397', '51', '3', '25', 'ο δε αδικων κομιειται ο ηδικησεν και ουκ εστιν προσωποληψια');
INSERT INTO `bible_verses_tr` VALUES ('6398', '51', '4', '1', 'οι κυριοι το δικαιον και την ισοτητα τοις δουλοις παρεχεσθε ειδοτες οτι και υμεις εχετε κυριον εν ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('6399', '51', '4', '2', 'τη προσευχη προσκαρτερειτε γρηγορουντες εν αυτη εν ευχαριστια');
INSERT INTO `bible_verses_tr` VALUES ('6400', '51', '4', '3', 'προσευχομενοι αμα και περι ημων ινα ο θεος ανοιξη ημιν θυραν του λογου λαλησαι το μυστηριον του χριστου δι ο και δεδεμαι');
INSERT INTO `bible_verses_tr` VALUES ('6401', '51', '4', '4', 'ινα φανερωσω αυτο ως δει με λαλησαι');
INSERT INTO `bible_verses_tr` VALUES ('6402', '51', '4', '5', 'εν σοφια περιπατειτε προς τους εξω τον καιρον εξαγοραζομενοι');
INSERT INTO `bible_verses_tr` VALUES ('6403', '51', '4', '6', 'ο λογος υμων παντοτε εν χαριτι αλατι ηρτυμενος ειδεναι πως δει υμας ενι εκαστω αποκρινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('6404', '51', '4', '7', 'τα κατ εμε παντα γνωρισει υμιν τυχικος ο αγαπητος αδελφος και πιστος διακονος και συνδουλος εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6405', '51', '4', '8', 'ον επεμψα προς υμας εις αυτο τουτο ινα γνω τα περι υμων και παρακαλεση τας καρδιας υμων');
INSERT INTO `bible_verses_tr` VALUES ('6406', '51', '4', '9', 'συν ονησιμω τω πιστω και αγαπητω αδελφω ος εστιν εξ υμων παντα υμιν γνωριουσιν τα ωδε');
INSERT INTO `bible_verses_tr` VALUES ('6407', '51', '4', '10', 'ασπαζεται υμας αρισταρχος ο συναιχμαλωτος μου και μαρκος ο ανεψιος βαρναβα περι ου ελαβετε εντολας εαν ελθη προς υμας δεξασθε αυτον');
INSERT INTO `bible_verses_tr` VALUES ('6408', '51', '4', '11', 'και ιησους ο λεγομενος ιουστος οι οντες εκ περιτομης ουτοι μονοι συνεργοι εις την βασιλειαν του θεου οιτινες εγενηθησαν μοι παρηγορια');
INSERT INTO `bible_verses_tr` VALUES ('6409', '51', '4', '12', 'ασπαζεται υμας επαφρας ο εξ υμων δουλος χριστου παντοτε αγωνιζομενος υπερ υμων εν ταις προσευχαις ινα στητε τελειοι και πεπληρωμενοι εν παντι θεληματι του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6410', '51', '4', '13', 'μαρτυρω γαρ αυτω οτι εχει ζηλον πολυν υπερ υμων και των εν λαοδικεια και των εν ιεραπολει');
INSERT INTO `bible_verses_tr` VALUES ('6411', '51', '4', '14', 'ασπαζεται υμας λουκας ο ιατρος ο αγαπητος και δημας');
INSERT INTO `bible_verses_tr` VALUES ('6412', '51', '4', '15', 'ασπασασθε τους εν λαοδικεια αδελφους και νυμφαν και την κατ οικον αυτου εκκλησιαν');
INSERT INTO `bible_verses_tr` VALUES ('6413', '51', '4', '16', 'και οταν αναγνωσθη παρ υμιν η επιστολη ποιησατε ινα και εν τη λαοδικεων εκκλησια αναγνωσθη και την εκ λαοδικειας ινα και υμεις αναγνωτε');
INSERT INTO `bible_verses_tr` VALUES ('6414', '51', '4', '17', 'και ειπατε αρχιππω βλεπε την διακονιαν ην παρελαβες εν κυριω ινα αυτην πληροις');
INSERT INTO `bible_verses_tr` VALUES ('6415', '51', '4', '18', 'ο ασπασμος τη εμη χειρι παυλου μνημονευετε μου των δεσμων η χαρις μεθ υμων αμην [προς κολασσαεις εγραφη απο ρωμης δια τυχικου και ονησιμου]');
INSERT INTO `bible_verses_tr` VALUES ('6416', '52', '1', '1', 'παυλος και σιλουανος και τιμοθεος τη εκκλησια θεσσαλονικεων εν θεω πατρι και κυριω ιησου χριστω χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6417', '52', '1', '2', 'ευχαριστουμεν τω θεω παντοτε περι παντων υμων μνειαν υμων ποιουμενοι επι των προσευχων ημων');
INSERT INTO `bible_verses_tr` VALUES ('6418', '52', '1', '3', 'αδιαλειπτως μνημονευοντες υμων του εργου της πιστεως και του κοπου της αγαπης και της υπομονης της ελπιδος του κυριου ημων ιησου χριστου εμπροσθεν του θεου και πατρος ημων');
INSERT INTO `bible_verses_tr` VALUES ('6419', '52', '1', '4', 'ειδοτες αδελφοι ηγαπημενοι υπο θεου την εκλογην υμων');
INSERT INTO `bible_verses_tr` VALUES ('6420', '52', '1', '5', 'οτι το ευαγγελιον ημων ουκ εγενηθη εις υμας εν λογω μονον αλλα και εν δυναμει και εν πνευματι αγιω και εν πληροφορια πολλη καθως οιδατε οιοι εγενηθημεν εν υμιν δι υμας');
INSERT INTO `bible_verses_tr` VALUES ('6421', '52', '1', '6', 'και υμεις μιμηται ημων εγενηθητε και του κυριου δεξαμενοι τον λογον εν θλιψει πολλη μετα χαρας πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('6422', '52', '1', '7', 'ωστε γενεσθαι υμας τυπους πασιν τοις πιστευουσιν εν τη μακεδονια και τη αχαια');
INSERT INTO `bible_verses_tr` VALUES ('6423', '52', '1', '8', 'αφ υμων γαρ εξηχηται ο λογος του κυριου ου μονον εν τη μακεδονια και αχαια αλλα και εν παντι τοπω η πιστις υμων η προς τον θεον εξεληλυθεν ωστε μη χρειαν ημας εχειν λαλειν τι');
INSERT INTO `bible_verses_tr` VALUES ('6424', '52', '1', '9', 'αυτοι γαρ περι ημων απαγγελλουσιν οποιαν εισοδον  {VAR1: εχομεν } {VAR2: εσχομεν } προς υμας και πως επεστρεψατε προς τον θεον απο των ειδωλων δουλευειν θεω ζωντι και αληθινω');
INSERT INTO `bible_verses_tr` VALUES ('6425', '52', '1', '10', 'και αναμενειν τον υιον αυτου εκ των ουρανων ον ηγειρεν εκ νεκρων ιησουν τον ρυομενον ημας απο της οργης της ερχομενης');
INSERT INTO `bible_verses_tr` VALUES ('6426', '52', '2', '1', 'αυτοι γαρ οιδατε αδελφοι την εισοδον ημων την προς υμας οτι ου κενη γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('6427', '52', '2', '2', 'αλλα και προπαθοντες και υβρισθεντες καθως οιδατε εν φιλιπποις επαρρησιασαμεθα εν τω θεω ημων λαλησαι προς υμας το ευαγγελιον του θεου εν πολλω αγωνι');
INSERT INTO `bible_verses_tr` VALUES ('6428', '52', '2', '3', 'η γαρ παρακλησις ημων ουκ εκ πλανης ουδε εξ ακαθαρσιας ουτε εν δολω');
INSERT INTO `bible_verses_tr` VALUES ('6429', '52', '2', '4', 'αλλα καθως δεδοκιμασμεθα υπο του θεου πιστευθηναι το ευαγγελιον ουτως λαλουμεν ουχ ως ανθρωποις αρεσκοντες αλλα τω θεω τω δοκιμαζοντι τας καρδιας ημων');
INSERT INTO `bible_verses_tr` VALUES ('6430', '52', '2', '5', 'ουτε γαρ ποτε εν λογω κολακειας εγενηθημεν καθως οιδατε ουτε εν προφασει πλεονεξιας θεος μαρτυς');
INSERT INTO `bible_verses_tr` VALUES ('6431', '52', '2', '6', 'ουτε ζητουντες εξ ανθρωπων δοξαν ουτε αφ υμων ουτε απ αλλων δυναμενοι εν βαρει ειναι ως χριστου αποστολοι');
INSERT INTO `bible_verses_tr` VALUES ('6432', '52', '2', '7', 'αλλ εγενηθημεν ηπιοι εν μεσω υμων ως αν τροφος θαλπη τα εαυτης τεκνα');
INSERT INTO `bible_verses_tr` VALUES ('6433', '52', '2', '8', 'ουτως ιμειρομενοι υμων ευδοκουμεν μεταδουναι υμιν ου μονον το ευαγγελιον του θεου αλλα και τας εαυτων ψυχας διοτι αγαπητοι ημιν γεγενησθε');
INSERT INTO `bible_verses_tr` VALUES ('6434', '52', '2', '9', 'μνημονευετε γαρ αδελφοι τον κοπον ημων και τον μοχθον νυκτος γαρ και ημερας εργαζομενοι προς το μη επιβαρησαι τινα υμων εκηρυξαμεν εις υμας το ευαγγελιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6435', '52', '2', '10', 'υμεις μαρτυρες και ο θεος ως οσιως και δικαιως και αμεμπτως υμιν τοις πιστευουσιν εγενηθημεν');
INSERT INTO `bible_verses_tr` VALUES ('6436', '52', '2', '11', 'καθαπερ οιδατε ως ενα εκαστον υμων ως πατηρ τεκνα εαυτου παρακαλουντες υμας και παραμυθουμενοι');
INSERT INTO `bible_verses_tr` VALUES ('6437', '52', '2', '12', 'και μαρτυρουμενοι εις το περιπατησαι υμας αξιως του θεου του καλουντος υμας εις την εαυτου βασιλειαν και δοξαν');
INSERT INTO `bible_verses_tr` VALUES ('6438', '52', '2', '13', 'δια τουτο και ημεις ευχαριστουμεν τω θεω αδιαλειπτως οτι παραλαβοντες λογον ακοης παρ ημων του θεου εδεξασθε ου λογον ανθρωπων αλλα καθως εστιν αληθως λογον θεου ος και ενεργειται εν υμιν τοις πιστευουσιν');
INSERT INTO `bible_verses_tr` VALUES ('6439', '52', '2', '14', 'υμεις γαρ μιμηται εγενηθητε αδελφοι των εκκλησιων του θεου των ουσων εν τη ιουδαια εν χριστω ιησου οτι ταυτα επαθετε και υμεις υπο των ιδιων συμφυλετων καθως και αυτοι υπο των ιουδαιων');
INSERT INTO `bible_verses_tr` VALUES ('6440', '52', '2', '15', 'των και τον κυριον αποκτειναντων ιησουν και τους ιδιους προφητας και  {VAR1: υμας } {VAR2: ημας } εκδιωξαντων και θεω μη αρεσκοντων και πασιν ανθρωποις εναντιων');
INSERT INTO `bible_verses_tr` VALUES ('6441', '52', '2', '16', 'κωλυοντων ημας τοις εθνεσιν λαλησαι ινα σωθωσιν εις το αναπληρωσαι αυτων τας αμαρτιας παντοτε εφθασεν δε επ αυτους η οργη εις τελος');
INSERT INTO `bible_verses_tr` VALUES ('6442', '52', '2', '17', 'ημεις δε αδελφοι απορφανισθεντες αφ υμων προς καιρον ωρας προσωπω ου καρδια περισσοτερως εσπουδασαμεν το προσωπον υμων ιδειν εν πολλη επιθυμια');
INSERT INTO `bible_verses_tr` VALUES ('6443', '52', '2', '18', 'διο ηθελησαμεν ελθειν προς υμας εγω μεν παυλος και απαξ και δις και ενεκοψεν ημας ο σατανας');
INSERT INTO `bible_verses_tr` VALUES ('6444', '52', '2', '19', 'τις γαρ ημων ελπις η χαρα η στεφανος καυχησεως η ουχι και υμεις εμπροσθεν του κυριου ημων ιησου χριστου εν τη αυτου παρουσια');
INSERT INTO `bible_verses_tr` VALUES ('6445', '52', '2', '20', 'υμεις γαρ εστε η δοξα ημων και η χαρα');
INSERT INTO `bible_verses_tr` VALUES ('6446', '52', '3', '1', 'διο μηκετι στεγοντες ευδοκησαμεν καταλειφθηναι εν αθηναις μονοι');
INSERT INTO `bible_verses_tr` VALUES ('6447', '52', '3', '2', 'και επεμψαμεν τιμοθεον τον αδελφον ημων και διακονον του θεου και συνεργον ημων εν τω ευαγγελιω του χριστου εις το στηριξαι υμας και παρακαλεσαι υμας περι της πιστεως υμων');
INSERT INTO `bible_verses_tr` VALUES ('6448', '52', '3', '3', 'τω μηδενα σαινεσθαι εν ταις θλιψεσιν ταυταις αυτοι γαρ οιδατε οτι εις τουτο κειμεθα');
INSERT INTO `bible_verses_tr` VALUES ('6449', '52', '3', '4', 'και γαρ οτε προς υμας ημεν προελεγομεν υμιν οτι μελλομεν θλιβεσθαι καθως και εγενετο και οιδατε');
INSERT INTO `bible_verses_tr` VALUES ('6450', '52', '3', '5', 'δια τουτο καγω μηκετι στεγων επεμψα εις το γνωναι την πιστιν υμων μηπως επειρασεν υμας ο πειραζων και εις κενον γενηται ο κοπος ημων');
INSERT INTO `bible_verses_tr` VALUES ('6451', '52', '3', '6', 'αρτι δε ελθοντος τιμοθεου προς ημας αφ υμων και ευαγγελισαμενου ημιν την πιστιν και την αγαπην υμων και οτι εχετε μνειαν ημων αγαθην παντοτε επιποθουντες ημας ιδειν καθαπερ και ημεις υμας');
INSERT INTO `bible_verses_tr` VALUES ('6452', '52', '3', '7', 'δια τουτο παρεκληθημεν αδελφοι εφ υμιν επι παση τη θλιψει και αναγκη ημων δια της υμων πιστεως');
INSERT INTO `bible_verses_tr` VALUES ('6453', '52', '3', '8', 'οτι νυν ζωμεν εαν υμεις στηκητε εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6454', '52', '3', '9', 'τινα γαρ ευχαριστιαν δυναμεθα τω θεω ανταποδουναι περι υμων επι παση τη χαρα η χαιρομεν δι υμας εμπροσθεν του θεου ημων');
INSERT INTO `bible_verses_tr` VALUES ('6455', '52', '3', '10', 'νυκτος και ημερας υπερ εκπερισσου δεομενοι εις το ιδειν υμων το προσωπον και καταρτισαι τα υστερηματα της πιστεως υμων');
INSERT INTO `bible_verses_tr` VALUES ('6456', '52', '3', '11', 'αυτος δε ο θεος και πατηρ ημων και ο κυριος ημων ιησους χριστος κατευθυναι την οδον ημων προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('6457', '52', '3', '12', 'υμας δε ο κυριος πλεονασαι και περισσευσαι τη αγαπη εις αλληλους και εις παντας καθαπερ και ημεις εις υμας');
INSERT INTO `bible_verses_tr` VALUES ('6458', '52', '3', '13', 'εις το στηριξαι υμων τας καρδιας αμεμπτους εν αγιωσυνη εμπροσθεν του θεου και πατρος ημων εν τη παρουσια του κυριου ημων ιησου χριστου μετα παντων των αγιων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6459', '52', '4', '1', 'το λοιπον ουν αδελφοι ερωτωμεν υμας και παρακαλουμεν εν κυριω ιησου καθως παρελαβετε παρ ημων το πως δει υμας περιπατειν και αρεσκειν θεω ινα περισσευητε μαλλον');
INSERT INTO `bible_verses_tr` VALUES ('6460', '52', '4', '2', 'οιδατε γαρ τινας παραγγελιας εδωκαμεν υμιν δια του κυριου ιησου');
INSERT INTO `bible_verses_tr` VALUES ('6461', '52', '4', '3', 'τουτο γαρ εστιν θελημα του θεου ο αγιασμος υμων απεχεσθαι υμας απο της πορνειας');
INSERT INTO `bible_verses_tr` VALUES ('6462', '52', '4', '4', 'ειδεναι εκαστον υμων το εαυτου σκευος κτασθαι εν αγιασμω και τιμη');
INSERT INTO `bible_verses_tr` VALUES ('6463', '52', '4', '5', 'μη εν παθει επιθυμιας καθαπερ και τα εθνη τα μη ειδοτα τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('6464', '52', '4', '6', 'το μη υπερβαινειν και πλεονεκτειν εν τω πραγματι τον αδελφον αυτου διοτι εκδικος ο κυριος περι παντων τουτων καθως και προειπαμεν υμιν και διεμαρτυραμεθα');
INSERT INTO `bible_verses_tr` VALUES ('6465', '52', '4', '7', 'ου γαρ εκαλεσεν ημας ο θεος επι ακαθαρσια αλλ εν αγιασμω');
INSERT INTO `bible_verses_tr` VALUES ('6466', '52', '4', '8', 'τοιγαρουν ο αθετων ουκ ανθρωπον αθετει αλλα τον θεον τον και δοντα το πνευμα αυτου το αγιον εις ημας');
INSERT INTO `bible_verses_tr` VALUES ('6467', '52', '4', '9', 'περι δε της φιλαδελφιας ου χρειαν εχετε γραφειν υμιν αυτοι γαρ υμεις θεοδιδακτοι εστε εις το αγαπαν αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('6468', '52', '4', '10', 'και γαρ ποιειτε αυτο εις παντας τους αδελφους τους εν ολη τη μακεδονια παρακαλουμεν δε υμας αδελφοι περισσευειν μαλλον');
INSERT INTO `bible_verses_tr` VALUES ('6469', '52', '4', '11', 'και φιλοτιμεισθαι ησυχαζειν και πρασσειν τα ιδια και εργαζεσθαι ταις ιδιαις χερσιν υμων καθως υμιν παρηγγειλαμεν');
INSERT INTO `bible_verses_tr` VALUES ('6470', '52', '4', '12', 'ινα περιπατητε ευσχημονως προς τους εξω και μηδενος χρειαν εχητε');
INSERT INTO `bible_verses_tr` VALUES ('6471', '52', '4', '13', 'ου θελω δε υμας αγνοειν αδελφοι περι των κεκοιμημενων ινα μη λυπησθε καθως και οι λοιποι οι μη εχοντες ελπιδα');
INSERT INTO `bible_verses_tr` VALUES ('6472', '52', '4', '14', 'ει γαρ πιστευομεν οτι ιησους απεθανεν και ανεστη ουτως και ο θεος τους κοιμηθεντας δια του ιησου αξει συν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('6473', '52', '4', '15', 'τουτο γαρ υμιν λεγομεν εν λογω κυριου οτι ημεις οι ζωντες οι περιλειπομενοι εις την παρουσιαν του κυριου ου μη φθασωμεν τους κοιμηθεντας');
INSERT INTO `bible_verses_tr` VALUES ('6474', '52', '4', '16', 'οτι αυτος ο κυριος εν κελευσματι εν φωνη αρχαγγελου και εν σαλπιγγι θεου καταβησεται απ ουρανου και οι νεκροι εν χριστω αναστησονται πρωτον');
INSERT INTO `bible_verses_tr` VALUES ('6475', '52', '4', '17', 'επειτα ημεις οι ζωντες οι περιλειπομενοι αμα συν αυτοις αρπαγησομεθα εν νεφελαις εις απαντησιν του κυριου εις αερα και ουτως παντοτε συν κυριω εσομεθα');
INSERT INTO `bible_verses_tr` VALUES ('6476', '52', '4', '18', 'ωστε παρακαλειτε αλληλους εν τοις λογοις τουτοις');
INSERT INTO `bible_verses_tr` VALUES ('6477', '52', '5', '1', 'περι δε των χρονων και των καιρων αδελφοι ου χρειαν εχετε υμιν γραφεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('6478', '52', '5', '2', 'αυτοι γαρ ακριβως οιδατε οτι η ημερα κυριου ως κλεπτης εν νυκτι ουτως ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('6479', '52', '5', '3', 'οταν γαρ λεγωσιν ειρηνη και ασφαλεια τοτε αιφνιδιος αυτοις εφισταται ολεθρος ωσπερ η ωδιν τη εν γαστρι εχουση και ου μη εκφυγωσιν');
INSERT INTO `bible_verses_tr` VALUES ('6480', '52', '5', '4', 'υμεις δε αδελφοι ουκ εστε εν σκοτει ινα η ημερα υμας ως κλεπτης καταλαβη');
INSERT INTO `bible_verses_tr` VALUES ('6481', '52', '5', '5', 'παντες υμεις υιοι φωτος εστε και υιοι ημερας ουκ εσμεν νυκτος ουδε σκοτους');
INSERT INTO `bible_verses_tr` VALUES ('6482', '52', '5', '6', 'αρα ουν μη καθευδωμεν ως και οι λοιποι αλλα γρηγορωμεν και νηφωμεν');
INSERT INTO `bible_verses_tr` VALUES ('6483', '52', '5', '7', 'οι γαρ καθευδοντες νυκτος καθευδουσιν και οι μεθυσκομενοι νυκτος μεθυουσιν');
INSERT INTO `bible_verses_tr` VALUES ('6484', '52', '5', '8', 'ημεις δε ημερας οντες νηφωμεν ενδυσαμενοι θωρακα πιστεως και αγαπης και περικεφαλαιαν ελπιδα σωτηριας');
INSERT INTO `bible_verses_tr` VALUES ('6485', '52', '5', '9', 'οτι ουκ εθετο ημας ο θεος εις οργην αλλ εις περιποιησιν σωτηριας δια του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6486', '52', '5', '10', 'του αποθανοντος υπερ ημων ινα ειτε γρηγορωμεν ειτε καθευδωμεν αμα συν αυτω ζησωμεν');
INSERT INTO `bible_verses_tr` VALUES ('6487', '52', '5', '11', 'διο παρακαλειτε αλληλους και οικοδομειτε εις τον ενα καθως και ποιειτε');
INSERT INTO `bible_verses_tr` VALUES ('6488', '52', '5', '12', 'ερωτωμεν δε υμας αδελφοι ειδεναι τους κοπιωντας εν υμιν και προισταμενους υμων εν κυριω και νουθετουντας υμας');
INSERT INTO `bible_verses_tr` VALUES ('6489', '52', '5', '13', 'και ηγεισθαι αυτους υπερ εκπερισσου εν αγαπη δια το εργον αυτων ειρηνευετε εν εαυτοις');
INSERT INTO `bible_verses_tr` VALUES ('6490', '52', '5', '14', 'παρακαλουμεν δε υμας αδελφοι νουθετειτε τους ατακτους παραμυθεισθε τους ολιγοψυχους αντεχεσθε των ασθενων μακροθυμειτε προς παντας');
INSERT INTO `bible_verses_tr` VALUES ('6491', '52', '5', '15', 'ορατε μη τις κακον αντι κακου τινι αποδω αλλα παντοτε το αγαθον διωκετε και εις αλληλους και εις παντας');
INSERT INTO `bible_verses_tr` VALUES ('6492', '52', '5', '16', 'παντοτε χαιρετε');
INSERT INTO `bible_verses_tr` VALUES ('6493', '52', '5', '17', 'αδιαλειπτως προσευχεσθε');
INSERT INTO `bible_verses_tr` VALUES ('6494', '52', '5', '18', 'εν παντι ευχαριστειτε τουτο γαρ θελημα θεου εν χριστω ιησου εις υμας');
INSERT INTO `bible_verses_tr` VALUES ('6495', '52', '5', '19', 'το πνευμα μη σβεννυτε');
INSERT INTO `bible_verses_tr` VALUES ('6496', '52', '5', '20', 'προφητειας μη εξουθενειτε');
INSERT INTO `bible_verses_tr` VALUES ('6497', '52', '5', '21', 'παντα δοκιμαζετε το καλον κατεχετε');
INSERT INTO `bible_verses_tr` VALUES ('6498', '52', '5', '22', 'απο παντος ειδους πονηρου απεχεσθε');
INSERT INTO `bible_verses_tr` VALUES ('6499', '52', '5', '23', 'αυτος δε ο θεος της ειρηνης αγιασαι υμας ολοτελεις και ολοκληρον υμων το πνευμα και η ψυχη και το σωμα αμεμπτως εν τη παρουσια του κυριου ημων ιησου χριστου τηρηθειη');
INSERT INTO `bible_verses_tr` VALUES ('6500', '52', '5', '24', 'πιστος ο καλων υμας ος και ποιησει');
INSERT INTO `bible_verses_tr` VALUES ('6501', '52', '5', '25', 'αδελφοι προσευχεσθε περι ημων');
INSERT INTO `bible_verses_tr` VALUES ('6502', '52', '5', '26', 'ασπασασθε τους αδελφους παντας εν φιληματι αγιω');
INSERT INTO `bible_verses_tr` VALUES ('6503', '52', '5', '27', 'ορκιζω υμας τον κυριον αναγνωσθηναι την επιστολην πασιν τοις αγιοις αδελφοις');
INSERT INTO `bible_verses_tr` VALUES ('6504', '52', '5', '28', 'η χαρις του κυριου ημων ιησου χριστου μεθ υμων αμην [προς θεσσαλονικεις πρωτη εγραφη απο αθηνων]');
INSERT INTO `bible_verses_tr` VALUES ('6505', '53', '1', '1', 'παυλος και σιλουανος και τιμοθεος τη εκκλησια θεσσαλονικεων εν θεω πατρι ημων και κυριω ιησου χριστω');
INSERT INTO `bible_verses_tr` VALUES ('6506', '53', '1', '2', 'χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6507', '53', '1', '3', 'ευχαριστειν οφειλομεν τω θεω παντοτε περι υμων αδελφοι καθως αξιον εστιν οτι υπεραυξανει η πιστις υμων και πλεοναζει η αγαπη ενος εκαστου παντων υμων εις αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('6508', '53', '1', '4', 'ωστε ημας αυτους εν υμιν καυχασθαι εν ταις εκκλησιαις του θεου υπερ της υπομονης υμων και πιστεως εν πασιν τοις διωγμοις υμων και ταις θλιψεσιν αις ανεχεσθε');
INSERT INTO `bible_verses_tr` VALUES ('6509', '53', '1', '5', 'ενδειγμα της δικαιας κρισεως του θεου εις το καταξιωθηναι υμας της βασιλειας του θεου υπερ ης και πασχετε');
INSERT INTO `bible_verses_tr` VALUES ('6510', '53', '1', '6', 'ειπερ δικαιον παρα θεω ανταποδουναι τοις θλιβουσιν υμας θλιψιν');
INSERT INTO `bible_verses_tr` VALUES ('6511', '53', '1', '7', 'και υμιν τοις θλιβομενοις ανεσιν μεθ ημων εν τη αποκαλυψει του κυριου ιησου απ ουρανου μετ αγγελων δυναμεως αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6512', '53', '1', '8', 'εν πυρι φλογος διδοντος εκδικησιν τοις μη ειδοσιν θεον και τοις μη υπακουουσιν τω ευαγγελιω του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6513', '53', '1', '9', 'οιτινες δικην τισουσιν ολεθρον αιωνιον απο προσωπου του κυριου και απο της δοξης της ισχυος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6514', '53', '1', '10', 'οταν ελθη ενδοξασθηναι εν τοις αγιοις αυτου και θαυμασθηναι εν πασιν τοις πιστευουσιν οτι επιστευθη το μαρτυριον ημων εφ υμας εν τη ημερα εκεινη');
INSERT INTO `bible_verses_tr` VALUES ('6515', '53', '1', '11', 'εις ο και προσευχομεθα παντοτε περι υμων ινα υμας αξιωση της κλησεως ο θεος ημων και πληρωση πασαν ευδοκιαν αγαθωσυνης και εργον πιστεως εν δυναμει');
INSERT INTO `bible_verses_tr` VALUES ('6516', '53', '1', '12', 'οπως ενδοξασθη το ονομα του κυριου ημων ιησου χριστου εν υμιν και υμεις εν αυτω κατα την χαριν του θεου ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6517', '53', '2', '1', 'ερωτωμεν δε υμας αδελφοι υπερ της παρουσιας του κυριου ημων ιησου χριστου και ημων επισυναγωγης επ αυτον');
INSERT INTO `bible_verses_tr` VALUES ('6518', '53', '2', '2', 'εις το μη ταχεως σαλευθηναι υμας απο του νοος μητε θροεισθαι μητε δια πνευματος μητε δια λογου μητε δι επιστολης ως δι ημων ως οτι ενεστηκεν η ημερα του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6519', '53', '2', '3', 'μη τις υμας εξαπατηση κατα μηδενα τροπον οτι εαν μη ελθη η αποστασια πρωτον και αποκαλυφθη ο ανθρωπος της αμαρτιας ο υιος της απωλειας');
INSERT INTO `bible_verses_tr` VALUES ('6520', '53', '2', '4', 'ο αντικειμενος και υπεραιρομενος επι  {VAR1: παντα } {VAR2: παν το } λεγομενον θεον η σεβασμα ωστε αυτον εις τον ναον του θεου ως θεον καθισαι αποδεικνυντα εαυτον οτι εστιν θεος');
INSERT INTO `bible_verses_tr` VALUES ('6521', '53', '2', '5', 'ου μνημονευετε οτι ετι ων προς υμας ταυτα ελεγον υμιν');
INSERT INTO `bible_verses_tr` VALUES ('6522', '53', '2', '6', 'και νυν το κατεχον οιδατε εις το αποκαλυφθηναι αυτον εν τω εαυτου καιρω');
INSERT INTO `bible_verses_tr` VALUES ('6523', '53', '2', '7', 'το γαρ μυστηριον ηδη ενεργειται της ανομιας μονον ο κατεχων αρτι εως εκ μεσου γενηται');
INSERT INTO `bible_verses_tr` VALUES ('6524', '53', '2', '8', 'και τοτε αποκαλυφθησεται ο ανομος ον ο κυριος αναλωσει τω πνευματι του στοματος αυτου και καταργησει τη επιφανεια της παρουσιας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6525', '53', '2', '9', 'ου εστιν η παρουσια κατ ενεργειαν του σατανα εν παση δυναμει και σημειοις και τερασιν ψευδους');
INSERT INTO `bible_verses_tr` VALUES ('6526', '53', '2', '10', 'και εν παση απατη της αδικιας εν τοις απολλυμενοις ανθ ων την αγαπην της αληθειας ουκ εδεξαντο εις το σωθηναι αυτους');
INSERT INTO `bible_verses_tr` VALUES ('6527', '53', '2', '11', 'και δια τουτο πεμψει αυτοις ο θεος ενεργειαν πλανης εις το πιστευσαι αυτους τω ψευδει');
INSERT INTO `bible_verses_tr` VALUES ('6528', '53', '2', '12', 'ινα κριθωσιν παντες οι μη πιστευσαντες τη αληθεια αλλ ευδοκησαντες εν τη αδικια');
INSERT INTO `bible_verses_tr` VALUES ('6529', '53', '2', '13', 'ημεις δε οφειλομεν ευχαριστειν τω θεω παντοτε περι υμων αδελφοι ηγαπημενοι υπο κυριου οτι ειλετο υμας ο θεος απ αρχης εις σωτηριαν εν αγιασμω πνευματος και πιστει αληθειας');
INSERT INTO `bible_verses_tr` VALUES ('6530', '53', '2', '14', 'εις ο εκαλεσεν υμας δια του ευαγγελιου ημων εις περιποιησιν δοξης του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6531', '53', '2', '15', 'αρα ουν αδελφοι στηκετε και κρατειτε τας παραδοσεις ας εδιδαχθητε ειτε δια λογου ειτε δι επιστολης ημων');
INSERT INTO `bible_verses_tr` VALUES ('6532', '53', '2', '16', 'αυτος δε ο κυριος ημων ιησους χριστος και ο θεος και πατηρ ημων ο αγαπησας ημας και δους παρακλησιν αιωνιαν και ελπιδα αγαθην εν χαριτι');
INSERT INTO `bible_verses_tr` VALUES ('6533', '53', '2', '17', 'παρακαλεσαι υμων τας καρδιας και στηριξαι υμας εν παντι λογω και εργω αγαθω');
INSERT INTO `bible_verses_tr` VALUES ('6534', '53', '3', '1', 'το λοιπον προσευχεσθε αδελφοι περι ημων ινα ο λογος του κυριου τρεχη και δοξαζηται καθως και προς υμας');
INSERT INTO `bible_verses_tr` VALUES ('6535', '53', '3', '2', 'και ινα ρυσθωμεν απο των ατοπων και πονηρων ανθρωπων ου γαρ παντων η πιστις');
INSERT INTO `bible_verses_tr` VALUES ('6536', '53', '3', '3', 'πιστος δε εστιν ο κυριος ος στηριξει υμας και φυλαξει απο του πονηρου');
INSERT INTO `bible_verses_tr` VALUES ('6537', '53', '3', '4', 'πεποιθαμεν δε εν κυριω εφ υμας οτι α παραγγελλομεν υμιν και ποιειτε και ποιησετε');
INSERT INTO `bible_verses_tr` VALUES ('6538', '53', '3', '5', 'ο δε κυριος κατευθυναι υμων τας καρδιας εις την αγαπην του θεου και  {VAR1: εις υπομονην } {VAR2: εις την υπομονην } του χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6539', '53', '3', '6', 'παραγγελλομεν δε υμιν αδελφοι εν ονοματι του κυριου ημων ιησου χριστου στελλεσθαι υμας απο παντος αδελφου ατακτως περιπατουντος και μη κατα την παραδοσιν ην παρελαβεν παρ ημων');
INSERT INTO `bible_verses_tr` VALUES ('6540', '53', '3', '7', 'αυτοι γαρ οιδατε πως δει μιμεισθαι ημας οτι ουκ ητακτησαμεν εν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('6541', '53', '3', '8', 'ουδε δωρεαν αρτον εφαγομεν παρα τινος αλλ εν κοπω και μοχθω νυκτα και ημεραν εργαζομενοι προς το μη επιβαρησαι τινα υμων');
INSERT INTO `bible_verses_tr` VALUES ('6542', '53', '3', '9', 'ουχ οτι ουκ εχομεν εξουσιαν αλλ ινα εαυτους τυπον δωμεν υμιν εις το μιμεισθαι ημας');
INSERT INTO `bible_verses_tr` VALUES ('6543', '53', '3', '10', 'και γαρ οτε ημεν προς υμας τουτο παρηγγελλομεν υμιν οτι ει τις ου θελει εργαζεσθαι μηδε εσθιετω');
INSERT INTO `bible_verses_tr` VALUES ('6544', '53', '3', '11', 'ακουομεν γαρ τινας περιπατουντας εν υμιν ατακτως μηδεν εργαζομενους αλλα περιεργαζομενους');
INSERT INTO `bible_verses_tr` VALUES ('6545', '53', '3', '12', 'τοις δε τοιουτοις παραγγελλομεν και παρακαλουμεν δια του κυριου ημων ιησου χριστου ινα μετα ησυχιας εργαζομενοι τον εαυτων αρτον εσθιωσιν');
INSERT INTO `bible_verses_tr` VALUES ('6546', '53', '3', '13', 'υμεις δε αδελφοι μη εκκακησητε καλοποιουντες');
INSERT INTO `bible_verses_tr` VALUES ('6547', '53', '3', '14', 'ει δε τις ουχ υπακουει τω λογω ημων δια της επιστολης τουτον σημειουσθε και μη συναναμιγνυσθε αυτω ινα εντραπη');
INSERT INTO `bible_verses_tr` VALUES ('6548', '53', '3', '15', 'και μη ως εχθρον ηγεισθε αλλα νουθετειτε ως αδελφον');
INSERT INTO `bible_verses_tr` VALUES ('6549', '53', '3', '16', 'αυτος δε ο κυριος της ειρηνης δωη υμιν την ειρηνην δια παντος εν παντι τροπω ο κυριος μετα παντων υμων');
INSERT INTO `bible_verses_tr` VALUES ('6550', '53', '3', '17', 'ο ασπασμος τη εμη χειρι παυλου ο εστιν σημειον εν παση επιστολη ουτως γραφω');
INSERT INTO `bible_verses_tr` VALUES ('6551', '53', '3', '18', 'η χαρις του κυριου ημων ιησου χριστου μετα παντων υμων αμην [προς θεσσαλονικεις δευτερα εγραφη απο αθηνων]');
INSERT INTO `bible_verses_tr` VALUES ('6552', '54', '1', '1', 'παυλος αποστολος ιησου χριστου κατ επιταγην θεου σωτηρος ημων και κυριου ιησου χριστου της ελπιδος ημων');
INSERT INTO `bible_verses_tr` VALUES ('6553', '54', '1', '2', 'τιμοθεω γνησιω τεκνω εν πιστει χαρις ελεος ειρηνη απο θεου πατρος ημων και  {VAR1: χριστου ιησου } {VAR2: ιησου χριστου } του κυριου ημων');
INSERT INTO `bible_verses_tr` VALUES ('6554', '54', '1', '3', 'καθως παρεκαλεσα σε προσμειναι εν εφεσω πορευομενος εις μακεδονιαν ινα παραγγειλης τισιν μη ετεροδιδασκαλειν');
INSERT INTO `bible_verses_tr` VALUES ('6555', '54', '1', '4', 'μηδε προσεχειν μυθοις και γενεαλογιαις απεραντοις αιτινες ζητησεις παρεχουσιν μαλλον η  {VAR1: οικονομιαν } {VAR2: οικοδομιαν } θεου την εν πιστει');
INSERT INTO `bible_verses_tr` VALUES ('6556', '54', '1', '5', 'το δε τελος της παραγγελιας εστιν αγαπη εκ καθαρας καρδιας και συνειδησεως αγαθης και πιστεως ανυποκριτου');
INSERT INTO `bible_verses_tr` VALUES ('6557', '54', '1', '6', 'ων τινες αστοχησαντες εξετραπησαν εις ματαιολογιαν');
INSERT INTO `bible_verses_tr` VALUES ('6558', '54', '1', '7', 'θελοντες ειναι νομοδιδασκαλοι μη νοουντες μητε α λεγουσιν μητε περι τινων διαβεβαιουνται');
INSERT INTO `bible_verses_tr` VALUES ('6559', '54', '1', '8', 'οιδαμεν δε οτι καλος ο νομος εαν τις αυτω νομιμως χρηται');
INSERT INTO `bible_verses_tr` VALUES ('6560', '54', '1', '9', 'ειδως τουτο οτι δικαιω νομος ου κειται ανομοις δε και ανυποτακτοις ασεβεσιν και αμαρτωλοις ανοσιοις και βεβηλοις πατραλωαις και μητραλωαις ανδροφονοις');
INSERT INTO `bible_verses_tr` VALUES ('6561', '54', '1', '10', 'πορνοις αρσενοκοιταις ανδραποδισταις ψευσταις επιορκοις και ει τι ετερον τη υγιαινουση διδασκαλια αντικειται');
INSERT INTO `bible_verses_tr` VALUES ('6562', '54', '1', '11', 'κατα το ευαγγελιον της δοξης του μακαριου θεου ο επιστευθην εγω');
INSERT INTO `bible_verses_tr` VALUES ('6563', '54', '1', '12', 'και χαριν εχω τω ενδυναμωσαντι με χριστω ιησου τω κυριω ημων οτι πιστον με ηγησατο θεμενος εις διακονιαν');
INSERT INTO `bible_verses_tr` VALUES ('6564', '54', '1', '13', 'τον προτερον οντα βλασφημον και διωκτην και υβριστην αλλ ηλεηθην οτι αγνοων εποιησα εν απιστια');
INSERT INTO `bible_verses_tr` VALUES ('6565', '54', '1', '14', 'υπερεπλεονασεν δε η χαρις του κυριου ημων μετα πιστεως και αγαπης της εν χριστω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('6566', '54', '1', '15', 'πιστος ο λογος και πασης αποδοχης αξιος οτι χριστος ιησους ηλθεν εις τον κοσμον αμαρτωλους σωσαι ων πρωτος ειμι εγω');
INSERT INTO `bible_verses_tr` VALUES ('6567', '54', '1', '16', 'αλλα δια τουτο ηλεηθην ινα εν εμοι πρωτω ενδειξηται ιησους χριστος την πασαν μακροθυμιαν προς υποτυπωσιν των μελλοντων πιστευειν επ αυτω εις ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('6568', '54', '1', '17', 'τω δε βασιλει των αιωνων αφθαρτω αορατω μονω σοφω θεω τιμη και δοξα εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('6569', '54', '1', '18', 'ταυτην την παραγγελιαν παρατιθεμαι σοι τεκνον τιμοθεε κατα τας προαγουσας επι σε προφητειας ινα στρατευη εν αυταις την καλην στρατειαν');
INSERT INTO `bible_verses_tr` VALUES ('6570', '54', '1', '19', 'εχων πιστιν και αγαθην συνειδησιν ην τινες απωσαμενοι περι την πιστιν εναυαγησαν');
INSERT INTO `bible_verses_tr` VALUES ('6571', '54', '1', '20', 'ων εστιν υμεναιος και αλεξανδρος ους παρεδωκα τω σατανα ινα παιδευθωσιν μη βλασφημειν');
INSERT INTO `bible_verses_tr` VALUES ('6572', '54', '2', '1', 'παρακαλω ουν πρωτον παντων ποιεισθαι δεησεις προσευχας εντευξεις ευχαριστιας υπερ παντων ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('6573', '54', '2', '2', 'υπερ βασιλεων και παντων των εν υπεροχη οντων ινα ηρεμον και ησυχιον βιον διαγωμεν εν παση ευσεβεια και σεμνοτητι');
INSERT INTO `bible_verses_tr` VALUES ('6574', '54', '2', '3', 'τουτο γαρ καλον και αποδεκτον ενωπιον του σωτηρος ημων θεου');
INSERT INTO `bible_verses_tr` VALUES ('6575', '54', '2', '4', 'ος παντας ανθρωπους θελει σωθηναι και εις επιγνωσιν αληθειας ελθειν');
INSERT INTO `bible_verses_tr` VALUES ('6576', '54', '2', '5', 'εις γαρ θεος εις και μεσιτης θεου και ανθρωπων ανθρωπος χριστος ιησους');
INSERT INTO `bible_verses_tr` VALUES ('6577', '54', '2', '6', 'ο δους εαυτον αντιλυτρον υπερ παντων το μαρτυριον καιροις ιδιοις');
INSERT INTO `bible_verses_tr` VALUES ('6578', '54', '2', '7', 'εις ο ετεθην εγω κηρυξ και αποστολος αληθειαν λεγω εν χριστω ου ψευδομαι διδασκαλος εθνων εν πιστει και αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('6579', '54', '2', '8', 'βουλομαι ουν προσευχεσθαι τους ανδρας εν παντι τοπω επαιροντας οσιους χειρας χωρις οργης και διαλογισμου');
INSERT INTO `bible_verses_tr` VALUES ('6580', '54', '2', '9', 'ωσαυτως και τας γυναικας εν καταστολη κοσμιω μετα αιδους και σωφροσυνης κοσμειν εαυτας μη εν πλεγμασιν η χρυσω η μαργαριταις η ιματισμω πολυτελει');
INSERT INTO `bible_verses_tr` VALUES ('6581', '54', '2', '10', 'αλλ ο πρεπει γυναιξιν επαγγελλομεναις θεοσεβειαν δι εργων αγαθων');
INSERT INTO `bible_verses_tr` VALUES ('6582', '54', '2', '11', 'γυνη εν ησυχια μανθανετω εν παση υποταγη');
INSERT INTO `bible_verses_tr` VALUES ('6583', '54', '2', '12', 'γυναικι δε διδασκειν ουκ επιτρεπω ουδε αυθεντειν ανδρος αλλ ειναι εν ησυχια');
INSERT INTO `bible_verses_tr` VALUES ('6584', '54', '2', '13', 'αδαμ γαρ πρωτος επλασθη ειτα ευα');
INSERT INTO `bible_verses_tr` VALUES ('6585', '54', '2', '14', 'και αδαμ ουκ ηπατηθη η δε γυνη απατηθεισα εν παραβασει γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('6586', '54', '2', '15', 'σωθησεται δε δια της τεκνογονιας εαν μεινωσιν εν πιστει και αγαπη και αγιασμω μετα σωφροσυνης');
INSERT INTO `bible_verses_tr` VALUES ('6587', '54', '3', '1', 'πιστος ο λογος ει τις επισκοπης ορεγεται καλου εργου επιθυμει');
INSERT INTO `bible_verses_tr` VALUES ('6588', '54', '3', '2', 'δει ουν τον επισκοπον ανεπιληπτον ειναι μιας γυναικος ανδρα  {VAR1: νηφαλεον } {VAR2: νηφαλιον } σωφρονα κοσμιον φιλοξενον διδακτικον');
INSERT INTO `bible_verses_tr` VALUES ('6589', '54', '3', '3', 'μη παροινον μη πληκτην μη αισχροκερδη αλλ επιεικη αμαχον αφιλαργυρον');
INSERT INTO `bible_verses_tr` VALUES ('6590', '54', '3', '4', 'του ιδιου οικου καλως προισταμενον τεκνα εχοντα εν υποταγη μετα πασης σεμνοτητος');
INSERT INTO `bible_verses_tr` VALUES ('6591', '54', '3', '5', 'ει δε τις του ιδιου οικου προστηναι ουκ οιδεν πως εκκλησιας θεου επιμελησεται');
INSERT INTO `bible_verses_tr` VALUES ('6592', '54', '3', '6', 'μη νεοφυτον ινα μη τυφωθεις εις κριμα εμπεση του διαβολου');
INSERT INTO `bible_verses_tr` VALUES ('6593', '54', '3', '7', 'δει δε αυτον και μαρτυριαν καλην εχειν απο των εξωθεν ινα μη εις ονειδισμον εμπεση και παγιδα του διαβολου');
INSERT INTO `bible_verses_tr` VALUES ('6594', '54', '3', '8', 'διακονους ωσαυτως σεμνους μη διλογους μη οινω πολλω προσεχοντας μη αισχροκερδεις');
INSERT INTO `bible_verses_tr` VALUES ('6595', '54', '3', '9', 'εχοντας το μυστηριον της πιστεως εν καθαρα συνειδησει');
INSERT INTO `bible_verses_tr` VALUES ('6596', '54', '3', '10', 'και ουτοι δε δοκιμαζεσθωσαν πρωτον ειτα διακονειτωσαν ανεγκλητοι οντες');
