INSERT INTO `bible_verses_tr` VALUES ('6608', '54', '4', '6', 'ταυτα υποτιθεμενος τοις αδελφοις καλος εση διακονος ιησου χριστου εντρεφομενος τοις λογοις της πιστεως και της καλης διδασκαλιας η παρηκολουθηκας');
INSERT INTO `bible_verses_tr` VALUES ('6609', '54', '4', '7', 'τους δε βεβηλους και γραωδεις μυθους παραιτου γυμναζε δε σεαυτον προς ευσεβειαν');
INSERT INTO `bible_verses_tr` VALUES ('6610', '54', '4', '8', 'η γαρ σωματικη γυμνασια προς ολιγον εστιν ωφελιμος η δε ευσεβεια προς παντα ωφελιμος εστιν επαγγελιαν εχουσα ζωης της νυν και της μελλουσης');
