INSERT INTO `bible_verses_tr` VALUES ('6619', '54', '5', '1', 'πρεσβυτερω μη επιπληξης αλλα παρακαλει ως πατερα νεωτερους ως αδελφους');
INSERT INTO `bible_verses_tr` VALUES ('6620', '54', '5', '2', 'πρεσβυτερας ως μητερας νεωτερας ως αδελφας εν παση αγνεια');
INSERT INTO `bible_verses_tr` VALUES ('6621', '54', '5', '3', 'χηρας τιμα τας οντως χηρας');
INSERT INTO `bible_verses_tr` VALUES ('6622', '54', '5', '4', 'ει δε τις χηρα τεκνα η εκγονα εχει μανθανετωσαν πρωτον τον ιδιον οικον ευσεβειν και αμοιβας αποδιδοναι τοις προγονοις τουτο γαρ εστιν καλον και αποδεκτον ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6623', '54', '5', '5', 'η δε οντως χηρα και μεμονωμενη ηλπικεν επι τον θεον και προσμενει ταις δεησεσιν και ταις προσευχαις νυκτος και ημερας');
