INSERT INTO `bible_verses_tr` VALUES ('6631', '54', '5', '13', 'αμα δε και αργαι μανθανουσιν περιερχομεναι τας οικιας ου μονον δε αργαι αλλα και φλυαροι και περιεργοι λαλουσαι τα μη δεοντα');
INSERT INTO `bible_verses_tr` VALUES ('6632', '54', '5', '14', 'βουλομαι ουν νεωτερας γαμειν τεκνογονειν οικοδεσποτειν μηδεμιαν αφορμην διδοναι τω αντικειμενω λοιδοριας χαριν');
