INSERT INTO `bible_verses_tr` VALUES ('6634', '54', '5', '16', 'ει τις πιστος η πιστη εχει χηρας επαρκειτω αυταις και μη βαρεισθω η εκκλησια ινα ταις οντως χηραις επαρκεση');
