INSERT INTO `bible_verses_tr` VALUES ('6651', '54', '6', '8', 'εχοντες δε διατροφας και σκεπασματα τουτοις αρκεσθησομεθα');
INSERT INTO `bible_verses_tr` VALUES ('6652', '54', '6', '9', 'οι δε βουλομενοι πλουτειν εμπιπτουσιν εις πειρασμον και παγιδα και επιθυμιας πολλας ανοητους και βλαβερας αιτινες βυθιζουσιν τους ανθρωπους εις ολεθρον και απωλειαν');
