INSERT INTO `bible_verses_tr` VALUES ('6654', '54', '6', '11', 'συ δε ω ανθρωπε του θεου ταυτα φευγε διωκε δε δικαιοσυνην ευσεβειαν πιστιν αγαπην υπομονην πραοτητα');
INSERT INTO `bible_verses_tr` VALUES ('6655', '54', '6', '12', 'αγωνιζου τον καλον αγωνα της πιστεως επιλαβου της αιωνιου ζωης εις ην και εκληθης και ωμολογησας την καλην ομολογιαν ενωπιον πολλων μαρτυρων');
