INSERT INTO `bible_verses_tr` VALUES ('6662', '54', '6', '19', 'αποθησαυριζοντας εαυτοις θεμελιον καλον εις το μελλον ινα επιλαβωνται της αιωνιου ζωης');
INSERT INTO `bible_verses_tr` VALUES ('6663', '54', '6', '20', 'ω τιμοθεε την παρακαταθηκην φυλαξον εκτρεπομενος τας βεβηλους κενοφωνιας και αντιθεσεις της ψευδωνυμου γνωσεως');
