INSERT INTO `bible_verses_tr` VALUES ('6670', '55', '1', '6', 'δι ην αιτιαν αναμιμνησκω σε αναζωπυρειν το χαρισμα του θεου ο εστιν εν σοι δια της επιθεσεως των χειρων μου');
INSERT INTO `bible_verses_tr` VALUES ('6671', '55', '1', '7', 'ου γαρ εδωκεν ημιν ο θεος πνευμα δειλιας αλλα δυναμεως και αγαπης και σωφρονισμου');
