INSERT INTO `bible_verses_tr` VALUES ('6674', '55', '1', '10', 'φανερωθεισαν δε νυν δια της επιφανειας του σωτηρος ημων ιησου χριστου καταργησαντος μεν τον θανατον φωτισαντος δε ζωην και αφθαρσιαν δια του ευαγγελιου');
INSERT INTO `bible_verses_tr` VALUES ('6675', '55', '1', '11', 'εις ο ετεθην εγω κηρυξ και αποστολος και διδασκαλος εθνων');
INSERT INTO `bible_verses_tr` VALUES ('6676', '55', '1', '12', 'δι ην αιτιαν και ταυτα πασχω αλλ ουκ επαισχυνομαι οιδα γαρ ω πεπιστευκα και πεπεισμαι οτι δυνατος εστιν την παραθηκην μου φυλαξαι εις εκεινην την ημεραν');
