INSERT INTO `bible_verses_tr` VALUES ('6678', '55', '1', '14', 'την καλην παρακαταθηκην φυλαξον δια πνευματος αγιου του ενοικουντος εν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('6679', '55', '1', '15', 'οιδας τουτο οτι απεστραφησαν με παντες οι εν τη ασια ων εστιν φυγελλος και ερμογενης');
