INSERT INTO `bible_verses_tr` VALUES ('6688', '55', '2', '6', 'τον κοπιωντα γεωργον δει πρωτον των καρπων μεταλαμβανειν');
INSERT INTO `bible_verses_tr` VALUES ('6689', '55', '2', '7', 'νοει α λεγω δωη γαρ σοι ο κυριος συνεσιν εν πασιν');
INSERT INTO `bible_verses_tr` VALUES ('6690', '55', '2', '8', 'μνημονευε ιησουν χριστον εγηγερμενον εκ νεκρων εκ σπερματος δαβιδ κατα το ευαγγελιον μου');
INSERT INTO `bible_verses_tr` VALUES ('6691', '55', '2', '9', 'εν ω κακοπαθω μεχρι δεσμων ως κακουργος αλλ ο λογος του θεου ου δεδεται');
INSERT INTO `bible_verses_tr` VALUES ('6692', '55', '2', '10', 'δια τουτο παντα υπομενω δια τους εκλεκτους ινα και αυτοι σωτηριας τυχωσιν της εν χριστω ιησου μετα δοξης αιωνιου');
INSERT INTO `bible_verses_tr` VALUES ('6693', '55', '2', '11', 'πιστος ο λογος ει γαρ συναπεθανομεν και συζησομεν');
INSERT INTO `bible_verses_tr` VALUES ('6694', '55', '2', '12', 'ει υπομενομεν και συμβασιλευσομεν ει αρνουμεθα κακεινος αρνησεται ημας');
INSERT INTO `bible_verses_tr` VALUES ('6695', '55', '2', '13', 'ει απιστουμεν εκεινος πιστος μενει αρνησασθαι εαυτον ου δυναται');
INSERT INTO `bible_verses_tr` VALUES ('6696', '55', '2', '14', 'ταυτα υπομιμνησκε διαμαρτυρομενος ενωπιον του κυριου μη λογομαχειν εις ουδεν χρησιμον επι καταστροφη των ακουοντων');
INSERT INTO `bible_verses_tr` VALUES ('6697', '55', '2', '15', 'σπουδασον σεαυτον δοκιμον παραστησαι τω θεω εργατην ανεπαισχυντον ορθοτομουντα τον λογον της αληθειας');
INSERT INTO `bible_verses_tr` VALUES ('6698', '55', '2', '16', 'τας δε βεβηλους κενοφωνιας περιιστασο επι πλειον γαρ προκοψουσιν ασεβειας');
INSERT INTO `bible_verses_tr` VALUES ('6699', '55', '2', '17', 'και ο λογος αυτων ως γαγγραινα νομην εξει ων εστιν υμεναιος και φιλητος');
INSERT INTO `bible_verses_tr` VALUES ('6700', '55', '2', '18', 'οιτινες περι την αληθειαν ηστοχησαν λεγοντες την αναστασιν ηδη γεγονεναι και ανατρεπουσιν την τινων πιστιν');
INSERT INTO `bible_verses_tr` VALUES ('6701', '55', '2', '19', 'ο μεντοι στερεος θεμελιος του θεου εστηκεν εχων την σφραγιδα ταυτην εγνω κυριος τους οντας αυτου και αποστητω απο αδικιας πας ο ονομαζων το ονομα χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6702', '55', '2', '20', 'εν μεγαλη δε οικια ουκ εστιν μονον σκευη χρυσα και αργυρα αλλα και ξυλινα και οστρακινα και α μεν εις τιμην α δε εις ατιμιαν');
INSERT INTO `bible_verses_tr` VALUES ('6703', '55', '2', '21', 'εαν ουν τις εκκαθαρη εαυτον απο τουτων εσται σκευος εις τιμην ηγιασμενον και ευχρηστον τω δεσποτη εις παν εργον αγαθον ητοιμασμενον');
INSERT INTO `bible_verses_tr` VALUES ('6704', '55', '2', '22', 'τας δε νεωτερικας επιθυμιας φευγε διωκε δε δικαιοσυνην πιστιν αγαπην ειρηνην μετα των επικαλουμενων τον κυριον εκ καθαρας καρδιας');
INSERT INTO `bible_verses_tr` VALUES ('6705', '55', '2', '23', 'τας δε μωρας και απαιδευτους ζητησεις παραιτου ειδως οτι γεννωσιν μαχας');
INSERT INTO `bible_verses_tr` VALUES ('6706', '55', '2', '24', 'δουλον δε κυριου ου δει μαχεσθαι αλλ ηπιον ειναι προς παντας διδακτικον ανεξικακον');
INSERT INTO `bible_verses_tr` VALUES ('6707', '55', '2', '25', 'εν πραοτητι παιδευοντα τους αντιδιατιθεμενους μηποτε δω αυτοις ο θεος μετανοιαν εις επιγνωσιν αληθειας');
INSERT INTO `bible_verses_tr` VALUES ('6708', '55', '2', '26', 'και ανανηψωσιν εκ της του διαβολου παγιδος εζωγρημενοι υπ αυτου εις το εκεινου θελημα');
INSERT INTO `bible_verses_tr` VALUES ('6709', '55', '3', '1', 'τουτο δε γινωσκε οτι εν εσχαταις ημεραις ενστησονται καιροι χαλεποι');
INSERT INTO `bible_verses_tr` VALUES ('6710', '55', '3', '2', 'εσονται γαρ οι ανθρωποι φιλαυτοι φιλαργυροι αλαζονες υπερηφανοι βλασφημοι γονευσιν απειθεις αχαριστοι ανοσιοι');
INSERT INTO `bible_verses_tr` VALUES ('6711', '55', '3', '3', 'αστοργοι ασπονδοι διαβολοι ακρατεις ανημεροι αφιλαγαθοι');
INSERT INTO `bible_verses_tr` VALUES ('6712', '55', '3', '4', 'προδοται προπετεις τετυφωμενοι φιληδονοι μαλλον η φιλοθεοι');
INSERT INTO `bible_verses_tr` VALUES ('6713', '55', '3', '5', 'εχοντες μορφωσιν ευσεβειας την δε δυναμιν αυτης ηρνημενοι και τουτους αποτρεπου');
INSERT INTO `bible_verses_tr` VALUES ('6714', '55', '3', '6', 'εκ τουτων γαρ εισιν οι ενδυνοντες εις τας οικιας και αιχμαλωτευοντες τα γυναικαρια σεσωρευμενα αμαρτιαις αγομενα επιθυμιαις ποικιλαις');
INSERT INTO `bible_verses_tr` VALUES ('6715', '55', '3', '7', 'παντοτε μανθανοντα και μηδεποτε εις επιγνωσιν αληθειας ελθειν δυναμενα');
INSERT INTO `bible_verses_tr` VALUES ('6716', '55', '3', '8', 'ον τροπον δε ιαννης και ιαμβρης αντεστησαν μωυσει ουτως και ουτοι ανθιστανται τη αληθεια ανθρωποι κατεφθαρμενοι τον νουν αδοκιμοι περι την πιστιν');
INSERT INTO `bible_verses_tr` VALUES ('6717', '55', '3', '9', 'αλλ ου προκοψουσιν επι πλειον η γαρ ανοια αυτων εκδηλος εσται πασιν ως και η εκεινων εγενετο');
INSERT INTO `bible_verses_tr` VALUES ('6718', '55', '3', '10', 'συ δε παρηκολουθηκας μου τη διδασκαλια τη αγωγη τη προθεσει τη πιστει τη μακροθυμια τη αγαπη τη υπομονη');
INSERT INTO `bible_verses_tr` VALUES ('6719', '55', '3', '11', 'τοις διωγμοις τοις παθημασιν οια μοι εγενετο εν αντιοχεια εν ικονιω εν λυστροις οιους διωγμους υπηνεγκα και εκ παντων με ερρυσατο ο κυριος');
INSERT INTO `bible_verses_tr` VALUES ('6720', '55', '3', '12', 'και παντες δε οι θελοντες ευσεβως ζην εν χριστω ιησου διωχθησονται');
INSERT INTO `bible_verses_tr` VALUES ('6721', '55', '3', '13', 'πονηροι δε ανθρωποι και γοητες προκοψουσιν επι το χειρον πλανωντες και πλανωμενοι');
INSERT INTO `bible_verses_tr` VALUES ('6722', '55', '3', '14', 'συ δε μενε εν οις εμαθες και επιστωθης ειδως παρα τινος εμαθες');
INSERT INTO `bible_verses_tr` VALUES ('6723', '55', '3', '15', 'και οτι απο βρεφους τα ιερα γραμματα οιδας τα δυναμενα σε σοφισαι εις σωτηριαν δια πιστεως της εν χριστω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('6724', '55', '3', '16', 'πασα γραφη θεοπνευστος και ωφελιμος προς διδασκαλιαν προς ελεγχον προς επανορθωσιν προς παιδειαν την εν δικαιοσυνη');
INSERT INTO `bible_verses_tr` VALUES ('6725', '55', '3', '17', 'ινα αρτιος η ο του θεου ανθρωπος προς παν εργον αγαθον εξηρτισμενος');
INSERT INTO `bible_verses_tr` VALUES ('6726', '55', '4', '1', 'διαμαρτυρομαι ουν εγω ενωπιον του θεου και του κυριου ιησου χριστου του μελλοντος κρινειν ζωντας και νεκρους κατα την επιφανειαν αυτου και την βασιλειαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6727', '55', '4', '2', 'κηρυξον τον λογον επιστηθι ευκαιρως ακαιρως ελεγξον επιτιμησον παρακαλεσον εν παση μακροθυμια και διδαχη');
INSERT INTO `bible_verses_tr` VALUES ('6728', '55', '4', '3', 'εσται γαρ καιρος οτε της υγιαινουσης διδασκαλιας ουκ ανεξονται αλλα κατα τας επιθυμιας τας ιδιας εαυτοις επισωρευσουσιν διδασκαλους κνηθομενοι την ακοην');
INSERT INTO `bible_verses_tr` VALUES ('6729', '55', '4', '4', 'και απο μεν της αληθειας την ακοην αποστρεψουσιν επι δε τους μυθους εκτραπησονται');
INSERT INTO `bible_verses_tr` VALUES ('6730', '55', '4', '5', 'συ δε νηφε εν πασιν κακοπαθησον εργον ποιησον ευαγγελιστου την διακονιαν σου πληροφορησον');
INSERT INTO `bible_verses_tr` VALUES ('6731', '55', '4', '6', 'εγω γαρ ηδη σπενδομαι και ο καιρος της εμης αναλυσεως εφεστηκεν');
INSERT INTO `bible_verses_tr` VALUES ('6732', '55', '4', '7', 'τον αγωνα τον καλον ηγωνισμαι τον δρομον τετελεκα την πιστιν τετηρηκα');
INSERT INTO `bible_verses_tr` VALUES ('6733', '55', '4', '8', 'λοιπον αποκειται μοι ο της δικαιοσυνης στεφανος ον αποδωσει μοι ο κυριος εν εκεινη τη ημερα ο δικαιος κριτης ου μονον δε εμοι αλλα και πασιν τοις ηγαπηκοσιν την επιφανειαν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6734', '55', '4', '9', 'σπουδασον ελθειν προς με ταχεως');
INSERT INTO `bible_verses_tr` VALUES ('6735', '55', '4', '10', 'δημας γαρ με εγκατελιπεν αγαπησας τον νυν αιωνα και επορευθη εις θεσσαλονικην κρησκης εις γαλατιαν τιτος εις δαλματιαν');
INSERT INTO `bible_verses_tr` VALUES ('6736', '55', '4', '11', 'λουκας εστιν μονος μετ εμου μαρκον αναλαβων αγε μετα σεαυτου εστιν γαρ μοι ευχρηστος εις διακονιαν');
INSERT INTO `bible_verses_tr` VALUES ('6737', '55', '4', '12', 'τυχικον δε απεστειλα εις εφεσον');
INSERT INTO `bible_verses_tr` VALUES ('6738', '55', '4', '13', 'τον  {VAR1: φαιλονην } {VAR2: φελονην } ον απελιπον εν τρωαδι παρα καρπω ερχομενος φερε και τα βιβλια μαλιστα τας μεμβρανας');
INSERT INTO `bible_verses_tr` VALUES ('6739', '55', '4', '14', 'αλεξανδρος ο χαλκευς πολλα μοι κακα ενεδειξατο αποδωη αυτω ο κυριος κατα τα εργα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6740', '55', '4', '15', 'ον και συ φυλασσου λιαν γαρ ανθεστηκεν τοις ημετεροις λογοις');
INSERT INTO `bible_verses_tr` VALUES ('6741', '55', '4', '16', 'εν τη πρωτη μου απολογια ουδεις μοι συμπαρεγενετο αλλα παντες με εγκατελιπον μη αυτοις λογισθειη');
INSERT INTO `bible_verses_tr` VALUES ('6742', '55', '4', '17', 'ο δε κυριος μοι παρεστη και ενεδυναμωσεν με ινα δι εμου το κηρυγμα πληροφορηθη και ακουση παντα τα εθνη και ερρυσθην εκ στοματος λεοντος');
INSERT INTO `bible_verses_tr` VALUES ('6743', '55', '4', '18', 'και ρυσεται με ο κυριος απο παντος εργου πονηρου και σωσει εις την βασιλειαν αυτου την επουρανιον ω η δοξα εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('6744', '55', '4', '19', 'ασπασαι πρισκαν και ακυλαν και τον ονησιφορου οικον');
INSERT INTO `bible_verses_tr` VALUES ('6745', '55', '4', '20', 'εραστος εμεινεν εν κορινθω τροφιμον δε απελιπον εν μιλητω ασθενουντα');
INSERT INTO `bible_verses_tr` VALUES ('6746', '55', '4', '21', 'σπουδασον προ χειμωνος ελθειν ασπαζεται σε ευβουλος και πουδης και λινος και κλαυδια και οι αδελφοι παντες');
INSERT INTO `bible_verses_tr` VALUES ('6747', '55', '4', '22', 'ο κυριος ιησους χριστος μετα του πνευματος σου η χαρις μεθ υμων αμην [προς τιμοθεον δευτερα της εφεσιων εκκλησιας πρωτον επισκοπον χειροτονηθεντα εγραφη απο ρωμης οτε εκ δευτερου παρεστη παυλος τω καισαρι νερωνι]');
INSERT INTO `bible_verses_tr` VALUES ('6748', '56', '1', '1', 'παυλος δουλος θεου αποστολος δε ιησου χριστου κατα πιστιν εκλεκτων θεου και επιγνωσιν αληθειας της κατ ευσεβειαν');
INSERT INTO `bible_verses_tr` VALUES ('6749', '56', '1', '2', 'επ ελπιδι ζωης αιωνιου ην επηγγειλατο ο αψευδης θεος προ χρονων αιωνιων');
INSERT INTO `bible_verses_tr` VALUES ('6750', '56', '1', '3', 'εφανερωσεν δε καιροις ιδιοις τον λογον αυτου εν κηρυγματι ο επιστευθην εγω κατ επιταγην του σωτηρος ημων θεου');
INSERT INTO `bible_verses_tr` VALUES ('6751', '56', '1', '4', 'τιτω γνησιω τεκνω κατα κοινην πιστιν χαρις ελεος ειρηνη απο θεου πατρος και κυριου ιησου χριστου του σωτηρος ημων');
INSERT INTO `bible_verses_tr` VALUES ('6752', '56', '1', '5', 'τουτου χαριν κατελιπον σε εν κρητη ινα τα λειποντα επιδιορθωση και καταστησης κατα πολιν πρεσβυτερους ως εγω σοι διεταξαμην');
INSERT INTO `bible_verses_tr` VALUES ('6753', '56', '1', '6', 'ει τις εστιν ανεγκλητος μιας γυναικος ανηρ τεκνα εχων πιστα μη εν κατηγορια ασωτιας η ανυποτακτα');
INSERT INTO `bible_verses_tr` VALUES ('6754', '56', '1', '7', 'δει γαρ τον επισκοπον ανεγκλητον ειναι ως θεου οικονομον μη αυθαδη μη οργιλον μη παροινον μη πληκτην μη αισχροκερδη');
INSERT INTO `bible_verses_tr` VALUES ('6755', '56', '1', '8', 'αλλα φιλοξενον φιλαγαθον σωφρονα δικαιον οσιον εγκρατη');
INSERT INTO `bible_verses_tr` VALUES ('6756', '56', '1', '9', 'αντεχομενον του κατα την διδαχην πιστου λογου ινα δυνατος η και παρακαλειν εν τη διδασκαλια τη υγιαινουση και τους αντιλεγοντας ελεγχειν');
INSERT INTO `bible_verses_tr` VALUES ('6757', '56', '1', '10', 'εισιν γαρ πολλοι και ανυποτακτοι ματαιολογοι και φρεναπαται μαλιστα οι εκ περιτομης');
INSERT INTO `bible_verses_tr` VALUES ('6758', '56', '1', '11', 'ους δει επιστομιζειν οιτινες ολους οικους ανατρεπουσιν διδασκοντες α μη δει αισχρου κερδους χαριν');
INSERT INTO `bible_verses_tr` VALUES ('6759', '56', '1', '12', 'ειπεν τις εξ αυτων ιδιος αυτων προφητης κρητες αει ψευσται κακα θηρια γαστερες αργαι');
INSERT INTO `bible_verses_tr` VALUES ('6760', '56', '1', '13', 'η μαρτυρια αυτη εστιν αληθης δι ην αιτιαν ελεγχε αυτους αποτομως ινα υγιαινωσιν εν τη πιστει');
INSERT INTO `bible_verses_tr` VALUES ('6761', '56', '1', '14', 'μη προσεχοντες ιουδαικοις μυθοις και εντολαις ανθρωπων αποστρεφομενων την αληθειαν');
INSERT INTO `bible_verses_tr` VALUES ('6762', '56', '1', '15', 'παντα μεν καθαρα τοις καθαροις τοις δε μεμιασμενοις και απιστοις ουδεν καθαρον αλλα μεμιανται αυτων και ο νους και η συνειδησις');
INSERT INTO `bible_verses_tr` VALUES ('6763', '56', '1', '16', 'θεον ομολογουσιν ειδεναι τοις δε εργοις αρνουνται βδελυκτοι οντες και απειθεις και προς παν εργον αγαθον αδοκιμοι');
INSERT INTO `bible_verses_tr` VALUES ('6764', '56', '2', '1', 'συ δε λαλει α πρεπει τη υγιαινουση διδασκαλια');
INSERT INTO `bible_verses_tr` VALUES ('6765', '56', '2', '2', 'πρεσβυτας νηφαλιους ειναι σεμνους σωφρονας υγιαινοντας τη πιστει τη αγαπη τη υπομονη');
INSERT INTO `bible_verses_tr` VALUES ('6766', '56', '2', '3', 'πρεσβυτιδας ωσαυτως εν καταστηματι ιεροπρεπεις μη διαβολους μη οινω πολλω δεδουλωμενας καλοδιδασκαλους');
INSERT INTO `bible_verses_tr` VALUES ('6767', '56', '2', '4', 'ινα σωφρονιζωσιν τας νεας φιλανδρους ειναι φιλοτεκνους');
INSERT INTO `bible_verses_tr` VALUES ('6768', '56', '2', '5', 'σωφρονας αγνας οικουρους αγαθας υποτασσομενας τοις ιδιοις ανδρασιν ινα μη ο λογος του θεου βλασφημηται');
INSERT INTO `bible_verses_tr` VALUES ('6769', '56', '2', '6', 'τους νεωτερους ωσαυτως παρακαλει σωφρονειν');
INSERT INTO `bible_verses_tr` VALUES ('6770', '56', '2', '7', 'περι παντα σεαυτον παρεχομενος τυπον καλων εργων εν τη διδασκαλια αδιαφθοριαν σεμνοτητα αφθαρσιαν');
INSERT INTO `bible_verses_tr` VALUES ('6771', '56', '2', '8', 'λογον υγιη ακαταγνωστον ινα ο εξ εναντιας εντραπη μηδεν εχων περι υμων λεγειν φαυλον');
INSERT INTO `bible_verses_tr` VALUES ('6772', '56', '2', '9', 'δουλους ιδιοις δεσποταις υποτασσεσθαι εν πασιν ευαρεστους ειναι μη αντιλεγοντας');
INSERT INTO `bible_verses_tr` VALUES ('6773', '56', '2', '10', 'μη νοσφιζομενους αλλα πιστιν πασαν ενδεικνυμενους αγαθην ινα την διδασκαλιαν του σωτηρος  {VAR1: υμων } {VAR2: ημων } θεου κοσμωσιν εν πασιν');
INSERT INTO `bible_verses_tr` VALUES ('6774', '56', '2', '11', 'επεφανη γαρ η χαρις του θεου η σωτηριος πασιν ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('6775', '56', '2', '12', 'παιδευουσα ημας ινα αρνησαμενοι την ασεβειαν και τας κοσμικας επιθυμιας σωφρονως και δικαιως και ευσεβως ζησωμεν εν τω νυν αιωνι');
INSERT INTO `bible_verses_tr` VALUES ('6776', '56', '2', '13', 'προσδεχομενοι την μακαριαν ελπιδα και επιφανειαν της δοξης του μεγαλου θεου και σωτηρος ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6777', '56', '2', '14', 'ος εδωκεν εαυτον υπερ ημων ινα λυτρωσηται ημας απο πασης ανομιας και καθαριση εαυτω λαον περιουσιον ζηλωτην καλων εργων');
INSERT INTO `bible_verses_tr` VALUES ('6778', '56', '2', '15', 'ταυτα λαλει και παρακαλει και ελεγχε μετα πασης επιταγης μηδεις σου περιφρονειτω');
INSERT INTO `bible_verses_tr` VALUES ('6779', '56', '3', '1', 'υπομιμνησκε αυτους αρχαις και εξουσιαις υποτασσεσθαι πειθαρχειν προς παν εργον αγαθον ετοιμους ειναι');
INSERT INTO `bible_verses_tr` VALUES ('6780', '56', '3', '2', 'μηδενα βλασφημειν αμαχους ειναι επιεικεις πασαν ενδεικνυμενους πραοτητα προς παντας ανθρωπους');
INSERT INTO `bible_verses_tr` VALUES ('6781', '56', '3', '3', 'ημεν γαρ ποτε και ημεις ανοητοι απειθεις πλανωμενοι δουλευοντες επιθυμιαις και ηδοναις ποικιλαις εν κακια και φθονω διαγοντες στυγητοι μισουντες αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('6782', '56', '3', '4', 'οτε δε η χρηστοτης και η φιλανθρωπια επεφανη του σωτηρος ημων θεου');
INSERT INTO `bible_verses_tr` VALUES ('6783', '56', '3', '5', 'ουκ εξ εργων των εν δικαιοσυνη ων εποιησαμεν ημεις αλλα κατα τον αυτου ελεον εσωσεν ημας δια λουτρου παλιγγενεσιας και ανακαινωσεως πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('6784', '56', '3', '6', 'ου εξεχεεν εφ ημας πλουσιως δια ιησου χριστου του σωτηρος ημων');
INSERT INTO `bible_verses_tr` VALUES ('6785', '56', '3', '7', 'ινα δικαιωθεντες τη εκεινου χαριτι κληρονομοι γενωμεθα κατ ελπιδα ζωης αιωνιου');
INSERT INTO `bible_verses_tr` VALUES ('6786', '56', '3', '8', 'πιστος ο λογος και περι τουτων βουλομαι σε διαβεβαιουσθαι ινα φροντιζωσιν καλων εργων προιστασθαι οι πεπιστευκοτες τω θεω ταυτα εστιν τα καλα και ωφελιμα τοις ανθρωποις');
INSERT INTO `bible_verses_tr` VALUES ('6787', '56', '3', '9', 'μωρας δε ζητησεις και γενεαλογιας και ερεις και μαχας νομικας περιιστασο εισιν γαρ ανωφελεις και ματαιοι');
INSERT INTO `bible_verses_tr` VALUES ('6788', '56', '3', '10', 'αιρετικον ανθρωπον μετα μιαν και δευτεραν νουθεσιαν παραιτου');
INSERT INTO `bible_verses_tr` VALUES ('6789', '56', '3', '11', 'ειδως οτι εξεστραπται ο τοιουτος και αμαρτανει ων αυτοκατακριτος');
INSERT INTO `bible_verses_tr` VALUES ('6790', '56', '3', '12', 'οταν πεμψω αρτεμαν προς σε η τυχικον σπουδασον ελθειν προς με εις νικοπολιν εκει γαρ κεκρικα παραχειμασαι');
INSERT INTO `bible_verses_tr` VALUES ('6791', '56', '3', '13', 'ζηναν τον νομικον και απολλω σπουδαιως προπεμψον ινα μηδεν αυτοις λειπη');
INSERT INTO `bible_verses_tr` VALUES ('6792', '56', '3', '14', 'μανθανετωσαν δε και οι ημετεροι καλων εργων προιστασθαι εις τας αναγκαιας χρειας ινα μη ωσιν ακαρποι');
INSERT INTO `bible_verses_tr` VALUES ('6793', '56', '3', '15', 'ασπαζονται σε οι μετ εμου παντες ασπασαι τους φιλουντας ημας εν πιστει η χαρις μετα παντων υμων αμην [προς τιτον της κρητων εκκλησιας πρωτον επισκοπον χειροτονηθεντα εγραφη απο νικοπολεως της μακεδονιας]');
INSERT INTO `bible_verses_tr` VALUES ('6794', '57', '1', '1', 'παυλος δεσμιος χριστου ιησου και τιμοθεος ο αδελφος φιλημονι τω αγαπητω και συνεργω ημων');
INSERT INTO `bible_verses_tr` VALUES ('6795', '57', '1', '2', 'και απφια τη αγαπητη και αρχιππω τω συστρατιωτη ημων και τη κατ οικον σου εκκλησια');
INSERT INTO `bible_verses_tr` VALUES ('6796', '57', '1', '3', 'χαρις υμιν και ειρηνη απο θεου πατρος ημων και κυριου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6797', '57', '1', '4', 'ευχαριστω τω θεω μου παντοτε μνειαν σου ποιουμενος επι των προσευχων μου');
INSERT INTO `bible_verses_tr` VALUES ('6798', '57', '1', '5', 'ακουων σου την αγαπην και την πιστιν ην εχεις προς τον κυριον ιησουν και εις παντας τους αγιους');
INSERT INTO `bible_verses_tr` VALUES ('6799', '57', '1', '6', 'οπως η κοινωνια της πιστεως σου ενεργης γενηται εν επιγνωσει παντος αγαθου του εν υμιν εις χριστον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('6800', '57', '1', '7', '{VAR1: χαριν } {VAR2: χαραν } γαρ εχομεν πολλην και παρακλησιν επι τη αγαπη σου οτι τα σπλαγχνα των αγιων αναπεπαυται δια σου αδελφε');
INSERT INTO `bible_verses_tr` VALUES ('6801', '57', '1', '8', 'διο πολλην εν χριστω παρρησιαν εχων επιτασσειν σοι το ανηκον');
INSERT INTO `bible_verses_tr` VALUES ('6802', '57', '1', '9', 'δια την αγαπην μαλλον παρακαλω τοιουτος ων ως παυλος πρεσβυτης νυνι δε και δεσμιος ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('6803', '57', '1', '10', 'παρακαλω σε περι του εμου τεκνου ον εγεννησα εν τοις δεσμοις μου ονησιμον');
INSERT INTO `bible_verses_tr` VALUES ('6804', '57', '1', '11', 'τον ποτε σοι αχρηστον νυνι δε σοι και εμοι ευχρηστον');
INSERT INTO `bible_verses_tr` VALUES ('6805', '57', '1', '12', 'ον ανεπεμψα συ δε αυτον τουτ εστιν τα εμα σπλαγχνα προσλαβου');
INSERT INTO `bible_verses_tr` VALUES ('6806', '57', '1', '13', 'ον εγω εβουλομην προς εμαυτον κατεχειν ινα υπερ σου διακονη μοι εν τοις δεσμοις του ευαγγελιου');
INSERT INTO `bible_verses_tr` VALUES ('6807', '57', '1', '14', 'χωρις δε της σης γνωμης ουδεν ηθελησα ποιησαι ινα μη ως κατα αναγκην το αγαθον σου η αλλα κατα εκουσιον');
INSERT INTO `bible_verses_tr` VALUES ('6808', '57', '1', '15', 'ταχα γαρ δια τουτο εχωρισθη προς ωραν ινα αιωνιον αυτον απεχης');
INSERT INTO `bible_verses_tr` VALUES ('6809', '57', '1', '16', 'ουκετι ως δουλον αλλ υπερ δουλον αδελφον αγαπητον μαλιστα εμοι ποσω δε μαλλον σοι και εν σαρκι και εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6810', '57', '1', '17', 'ει ουν εμε εχεις κοινωνον προσλαβου αυτον ως εμε');
INSERT INTO `bible_verses_tr` VALUES ('6811', '57', '1', '18', 'ει δε τι ηδικησεν σε η οφειλει τουτο εμοι ελλογει');
INSERT INTO `bible_verses_tr` VALUES ('6812', '57', '1', '19', 'εγω παυλος εγραψα τη εμη χειρι εγω αποτισω ινα μη λεγω σοι οτι και σεαυτον μοι προσοφειλεις');
INSERT INTO `bible_verses_tr` VALUES ('6813', '57', '1', '20', 'ναι αδελφε εγω σου οναιμην εν κυριω αναπαυσον μου τα σπλαγχνα εν κυριω');
INSERT INTO `bible_verses_tr` VALUES ('6814', '57', '1', '21', 'πεποιθως τη υπακοη σου εγραψα σοι ειδως οτι και υπερ ο λεγω ποιησεις');
INSERT INTO `bible_verses_tr` VALUES ('6815', '57', '1', '22', 'αμα δε και ετοιμαζε μοι ξενιαν ελπιζω γαρ οτι δια των προσευχων υμων χαρισθησομαι υμιν');
INSERT INTO `bible_verses_tr` VALUES ('6816', '57', '1', '23', 'ασπαζονται σε επαφρας ο συναιχμαλωτος μου εν χριστω ιησου');
INSERT INTO `bible_verses_tr` VALUES ('6817', '57', '1', '24', 'μαρκος αρισταρχος δημας λουκας οι συνεργοι μου');
INSERT INTO `bible_verses_tr` VALUES ('6818', '57', '1', '25', 'η χαρις του κυριου ημων ιησου χριστου μετα του πνευματος υμων αμην [προς φιλημονα εγραφη απο ρωμης δια ονησιμου οικετου]');
INSERT INTO `bible_verses_tr` VALUES ('6819', '58', '1', '1', 'πολυμερως και πολυτροπως παλαι ο θεος λαλησας τοις πατρασιν εν τοις προφηταις');
INSERT INTO `bible_verses_tr` VALUES ('6820', '58', '1', '2', 'επ εσχατων των ημερων τουτων ελαλησεν ημιν εν υιω ον εθηκεν κληρονομον παντων δι ου και τους αιωνας εποιησεν');
INSERT INTO `bible_verses_tr` VALUES ('6821', '58', '1', '3', 'ος ων απαυγασμα της δοξης και χαρακτηρ της υποστασεως αυτου φερων τε τα παντα τω ρηματι της δυναμεως αυτου δι εαυτου καθαρισμον ποιησαμενος των αμαρτιων ημων εκαθισεν εν δεξια της μεγαλωσυνης εν υψηλοις');
INSERT INTO `bible_verses_tr` VALUES ('6822', '58', '1', '4', 'τοσουτω κρειττων γενομενος των αγγελων οσω διαφορωτερον παρ αυτους κεκληρονομηκεν ονομα');
INSERT INTO `bible_verses_tr` VALUES ('6823', '58', '1', '5', 'τινι γαρ ειπεν ποτε των αγγελων υιος μου ει συ εγω σημερον γεγεννηκα σε και παλιν εγω εσομαι αυτω εις πατερα και αυτος εσται μοι εις υιον');
INSERT INTO `bible_verses_tr` VALUES ('6824', '58', '1', '6', 'οταν δε παλιν εισαγαγη τον πρωτοτοκον εις την οικουμενην λεγει και προσκυνησατωσαν αυτω παντες αγγελοι θεου');
INSERT INTO `bible_verses_tr` VALUES ('6825', '58', '1', '7', 'και προς μεν τους αγγελους λεγει ο ποιων τους αγγελους αυτου πνευματα και τους λειτουργους αυτου πυρος φλογα');
INSERT INTO `bible_verses_tr` VALUES ('6826', '58', '1', '8', 'προς δε τον υιον ο θρονος σου ο θεος εις τον αιωνα του αιωνος ραβδος ευθυτητος η ραβδος της βασιλειας σου');
INSERT INTO `bible_verses_tr` VALUES ('6827', '58', '1', '9', 'ηγαπησας δικαιοσυνην και εμισησας ανομιαν δια τουτο εχρισεν σε ο θεος ο θεος σου ελαιον αγαλλιασεως παρα τους μετοχους σου');
INSERT INTO `bible_verses_tr` VALUES ('6828', '58', '1', '10', 'και συ κατ αρχας κυριε την γην εθεμελιωσας και εργα των χειρων σου εισιν οι ουρανοι');
INSERT INTO `bible_verses_tr` VALUES ('6829', '58', '1', '11', 'αυτοι απολουνται συ δε διαμενεις και παντες ως ιματιον παλαιωθησονται');
INSERT INTO `bible_verses_tr` VALUES ('6830', '58', '1', '12', 'και ωσει περιβολαιον ελιξεις αυτους και αλλαγησονται συ δε ο αυτος ει και τα ετη σου ουκ εκλειψουσιν');
INSERT INTO `bible_verses_tr` VALUES ('6831', '58', '1', '13', 'προς τινα δε των αγγελων ειρηκεν ποτε καθου εκ δεξιων μου εως αν θω τους εχθρους σου υποποδιον των ποδων σου');
INSERT INTO `bible_verses_tr` VALUES ('6832', '58', '1', '14', 'ουχι παντες εισιν λειτουργικα πνευματα εις διακονιαν αποστελλομενα δια τους μελλοντας κληρονομειν σωτηριαν');
INSERT INTO `bible_verses_tr` VALUES ('6833', '58', '2', '1', 'δια τουτο δει περισσοτερως ημας προσεχειν τοις ακουσθεισιν μηποτε παραρρυωμεν');
INSERT INTO `bible_verses_tr` VALUES ('6834', '58', '2', '2', 'ει γαρ ο δι αγγελων λαληθεις λογος εγενετο βεβαιος και πασα παραβασις και παρακοη ελαβεν ενδικον μισθαποδοσιαν');
INSERT INTO `bible_verses_tr` VALUES ('6835', '58', '2', '3', 'πως ημεις εκφευξομεθα τηλικαυτης αμελησαντες σωτηριας ητις αρχην λαβουσα λαλεισθαι δια του κυριου υπο των ακουσαντων εις ημας εβεβαιωθη');
INSERT INTO `bible_verses_tr` VALUES ('6836', '58', '2', '4', 'συνεπιμαρτυρουντος του θεου σημειοις τε και τερασιν και ποικιλαις δυναμεσιν και πνευματος αγιου μερισμοις κατα την αυτου θελησιν');
INSERT INTO `bible_verses_tr` VALUES ('6837', '58', '2', '5', 'ου γαρ αγγελοις υπεταξεν την οικουμενην την μελλουσαν περι ης λαλουμεν');
INSERT INTO `bible_verses_tr` VALUES ('6838', '58', '2', '6', 'διεμαρτυρατο δε που τις λεγων τι εστιν ανθρωπος οτι μιμνησκη αυτου η υιος ανθρωπου οτι επισκεπτη αυτον');
INSERT INTO `bible_verses_tr` VALUES ('6839', '58', '2', '7', 'ηλαττωσας αυτον βραχυ τι παρ αγγελους δοξη και τιμη εστεφανωσας αυτον και κατεστησας αυτον επι τα εργα των χειρων σου');
INSERT INTO `bible_verses_tr` VALUES ('6840', '58', '2', '8', 'παντα υπεταξας υποκατω των ποδων αυτου εν γαρ τω υποταξαι αυτω τα παντα ουδεν αφηκεν αυτω ανυποτακτον νυν δε ουπω ορωμεν αυτω τα παντα υποτεταγμενα');
INSERT INTO `bible_verses_tr` VALUES ('6841', '58', '2', '9', 'τον δε βραχυ τι παρ αγγελους ηλαττωμενον βλεπομεν ιησουν δια το παθημα του θανατου δοξη και τιμη εστεφανωμενον οπως χαριτι θεου υπερ παντος γευσηται θανατου');
INSERT INTO `bible_verses_tr` VALUES ('6842', '58', '2', '10', 'επρεπεν γαρ αυτω δι ον τα παντα και δι ου τα παντα πολλους υιους εις δοξαν αγαγοντα τον αρχηγον της σωτηριας αυτων δια παθηματων τελειωσαι');
INSERT INTO `bible_verses_tr` VALUES ('6843', '58', '2', '11', 'ο τε γαρ αγιαζων και οι αγιαζομενοι εξ ενος παντες δι ην αιτιαν ουκ επαισχυνεται αδελφους αυτους καλειν');
INSERT INTO `bible_verses_tr` VALUES ('6844', '58', '2', '12', 'λεγων απαγγελω το ονομα σου τοις αδελφοις μου εν μεσω εκκλησιας υμνησω σε');
INSERT INTO `bible_verses_tr` VALUES ('6845', '58', '2', '13', 'και παλιν εγω εσομαι πεποιθως επ αυτω και παλιν ιδου εγω και τα παιδια α μοι εδωκεν ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('6846', '58', '2', '14', 'επει ουν τα παιδια κεκοινωνηκεν σαρκος και αιματος και αυτος παραπλησιως μετεσχεν των αυτων ινα δια του θανατου καταργηση τον το κρατος εχοντα του θανατου τουτ εστιν τον διαβολον');
INSERT INTO `bible_verses_tr` VALUES ('6847', '58', '2', '15', 'και απαλλαξη τουτους οσοι φοβω θανατου δια παντος του ζην ενοχοι ησαν δουλειας');
INSERT INTO `bible_verses_tr` VALUES ('6848', '58', '2', '16', 'ου γαρ δηπου αγγελων επιλαμβανεται αλλα σπερματος αβρααμ επιλαμβανεται');
INSERT INTO `bible_verses_tr` VALUES ('6849', '58', '2', '17', 'οθεν ωφειλεν κατα παντα τοις αδελφοις ομοιωθηναι ινα ελεημων γενηται και πιστος αρχιερευς τα προς τον θεον εις το ιλασκεσθαι τας αμαρτιας του λαου');
INSERT INTO `bible_verses_tr` VALUES ('6850', '58', '2', '18', 'εν ω γαρ πεπονθεν αυτος πειρασθεις δυναται τοις πειραζομενοις βοηθησαι');
INSERT INTO `bible_verses_tr` VALUES ('6851', '58', '3', '1', 'οθεν αδελφοι αγιοι κλησεως επουρανιου μετοχοι κατανοησατε τον αποστολον και αρχιερεα της ομολογιας ημων χριστον ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('6852', '58', '3', '2', 'πιστον οντα τω ποιησαντι αυτον ως και μωσης εν ολω τω οικω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6853', '58', '3', '3', 'πλειονος γαρ δοξης ουτος παρα μωσην ηξιωται καθ οσον πλειονα τιμην εχει του οικου ο κατασκευασας αυτον');
INSERT INTO `bible_verses_tr` VALUES ('6854', '58', '3', '4', 'πας γαρ οικος κατασκευαζεται υπο τινος ο δε τα παντα κατασκευασας θεος');
INSERT INTO `bible_verses_tr` VALUES ('6855', '58', '3', '5', 'και μωσης μεν πιστος εν ολω τω οικω αυτου ως θεραπων εις μαρτυριον των λαληθησομενων');
INSERT INTO `bible_verses_tr` VALUES ('6856', '58', '3', '6', 'χριστος δε ως υιος επι τον οικον αυτου ου οικος εσμεν ημεις εανπερ την παρρησιαν και το καυχημα της ελπιδος μεχρι τελους βεβαιαν κατασχωμεν');
INSERT INTO `bible_verses_tr` VALUES ('6857', '58', '3', '7', 'διο καθως λεγει το πνευμα το αγιον σημερον εαν της φωνης αυτου ακουσητε');
INSERT INTO `bible_verses_tr` VALUES ('6858', '58', '3', '8', 'μη σκληρυνητε τας καρδιας υμων ως εν τω παραπικρασμω κατα την ημεραν του πειρασμου εν τη ερημω');
INSERT INTO `bible_verses_tr` VALUES ('6859', '58', '3', '9', 'ου επειρασαν με οι πατερες υμων εδοκιμασαν με και ειδον τα εργα μου τεσσαρακοντα ετη');
INSERT INTO `bible_verses_tr` VALUES ('6860', '58', '3', '10', 'διο προσωχθισα τη γενεα εκεινη και ειπον αει πλανωνται τη καρδια αυτοι δε ουκ εγνωσαν τας οδους μου');
INSERT INTO `bible_verses_tr` VALUES ('6861', '58', '3', '11', 'ως ωμοσα εν τη οργη μου ει εισελευσονται εις την καταπαυσιν μου');
INSERT INTO `bible_verses_tr` VALUES ('6862', '58', '3', '12', 'βλεπετε αδελφοι μηποτε εσται εν τινι υμων καρδια πονηρα απιστιας εν τω αποστηναι απο θεου ζωντος');
INSERT INTO `bible_verses_tr` VALUES ('6863', '58', '3', '13', 'αλλα παρακαλειτε εαυτους καθ εκαστην ημεραν αχρις ου το σημερον καλειται ινα μη σκληρυνθη τις εξ υμων απατη της αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('6864', '58', '3', '14', 'μετοχοι γαρ γεγοναμεν του χριστου εανπερ την αρχην της υποστασεως μεχρι τελους βεβαιαν κατασχωμεν');
INSERT INTO `bible_verses_tr` VALUES ('6865', '58', '3', '15', 'εν τω λεγεσθαι σημερον εαν της φωνης αυτου ακουσητε μη σκληρυνητε τας καρδιας υμων ως εν τω παραπικρασμω');
INSERT INTO `bible_verses_tr` VALUES ('6866', '58', '3', '16', 'τινες γαρ ακουσαντες παρεπικραναν αλλ ου παντες οι εξελθοντες εξ αιγυπτου δια μωσεως');
INSERT INTO `bible_verses_tr` VALUES ('6867', '58', '3', '17', 'τισιν δε προσωχθισεν τεσσαρακοντα ετη ουχι τοις αμαρτησασιν ων τα κωλα επεσεν εν τη ερημω');
INSERT INTO `bible_verses_tr` VALUES ('6868', '58', '3', '18', 'τισιν δε ωμοσεν μη εισελευσεσθαι εις την καταπαυσιν αυτου ει μη τοις απειθησασιν');
INSERT INTO `bible_verses_tr` VALUES ('6869', '58', '3', '19', 'και βλεπομεν οτι ουκ ηδυνηθησαν εισελθειν δι απιστιαν');
INSERT INTO `bible_verses_tr` VALUES ('6870', '58', '4', '1', 'φοβηθωμεν ουν μηποτε καταλειπομενης επαγγελιας εισελθειν εις την καταπαυσιν αυτου δοκη τις εξ υμων υστερηκεναι');
INSERT INTO `bible_verses_tr` VALUES ('6871', '58', '4', '2', 'και γαρ εσμεν ευηγγελισμενοι καθαπερ κακεινοι αλλ ουκ ωφελησεν ο λογος της ακοης εκεινους μη συγκεκραμενος τη πιστει τοις ακουσασιν');
INSERT INTO `bible_verses_tr` VALUES ('6872', '58', '4', '3', 'εισερχομεθα γαρ εις την καταπαυσιν οι πιστευσαντες καθως ειρηκεν ως ωμοσα εν τη οργη μου ει εισελευσονται εις την καταπαυσιν μου καιτοι των εργων απο καταβολης κοσμου γενηθεντων');
INSERT INTO `bible_verses_tr` VALUES ('6873', '58', '4', '4', 'ειρηκεν γαρ που περι της εβδομης ουτως και κατεπαυσεν ο θεος εν τη ημερα τη εβδομη απο παντων των εργων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('6874', '58', '4', '5', 'και εν τουτω παλιν ει εισελευσονται εις την καταπαυσιν μου');
INSERT INTO `bible_verses_tr` VALUES ('6875', '58', '4', '6', 'επει ουν απολειπεται τινας εισελθειν εις αυτην και οι προτερον ευαγγελισθεντες ουκ εισηλθον δι απειθειαν');
INSERT INTO `bible_verses_tr` VALUES ('6876', '58', '4', '7', 'παλιν τινα οριζει ημεραν σημερον εν δαβιδ λεγων μετα τοσουτον χρονον καθως ειρηται σημερον εαν της φωνης αυτου ακουσητε μη σκληρυνητε τας καρδιας υμων');
INSERT INTO `bible_verses_tr` VALUES ('6877', '58', '4', '8', 'ει γαρ αυτους ιησους κατεπαυσεν ουκ αν περι αλλης ελαλει μετα ταυτα ημερας');
INSERT INTO `bible_verses_tr` VALUES ('6878', '58', '4', '9', 'αρα απολειπεται σαββατισμος τω λαω του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6879', '58', '4', '10', 'ο γαρ εισελθων εις την καταπαυσιν αυτου και αυτος κατεπαυσεν απο των εργων αυτου ωσπερ απο των ιδιων ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('6880', '58', '4', '11', 'σπουδασωμεν ουν εισελθειν εις εκεινην την καταπαυσιν ινα μη εν τω αυτω τις υποδειγματι πεση της απειθειας');
INSERT INTO `bible_verses_tr` VALUES ('6881', '58', '4', '12', 'ζων γαρ ο λογος του θεου και ενεργης και τομωτερος υπερ πασαν μαχαιραν διστομον και διικνουμενος αχρι μερισμου ψυχης τε και πνευματος αρμων τε και μυελων και κριτικος ενθυμησεων και εννοιων καρδιας');
INSERT INTO `bible_verses_tr` VALUES ('6882', '58', '4', '13', 'και ουκ εστιν κτισις αφανης ενωπιον αυτου παντα δε γυμνα και τετραχηλισμενα τοις οφθαλμοις αυτου προς ον ημιν ο λογος');
INSERT INTO `bible_verses_tr` VALUES ('6883', '58', '4', '14', 'εχοντες ουν αρχιερεα μεγαν διεληλυθοτα τους ουρανους ιησουν τον υιον του θεου κρατωμεν της ομολογιας');
INSERT INTO `bible_verses_tr` VALUES ('6884', '58', '4', '15', 'ου γαρ εχομεν αρχιερεα μη δυναμενον συμπαθησαι ταις ασθενειαις ημων  {VAR1: πεπειραμενον } {VAR2: πεπειρασμενον } δε κατα παντα καθ ομοιοτητα χωρις αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('6885', '58', '4', '16', 'προσερχωμεθα ουν μετα παρρησιας τω θρονω της χαριτος ινα λαβωμεν ελεον και χαριν ευρωμεν εις ευκαιρον βοηθειαν');
INSERT INTO `bible_verses_tr` VALUES ('6886', '58', '5', '1', 'πας γαρ αρχιερευς εξ ανθρωπων λαμβανομενος υπερ ανθρωπων καθισταται τα προς τον θεον ινα προσφερη δωρα τε και θυσιας υπερ αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('6887', '58', '5', '2', 'μετριοπαθειν δυναμενος τοις αγνοουσιν και πλανωμενοις επει και αυτος περικειται ασθενειαν');
INSERT INTO `bible_verses_tr` VALUES ('6888', '58', '5', '3', 'και δια ταυτην οφειλει καθως περι του λαου ουτως και περι εαυτου προσφερειν υπερ αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('6889', '58', '5', '4', 'και ουχ εαυτω τις λαμβανει την τιμην αλλα ο καλουμενος υπο του θεου καθαπερ και ο ααρων');
INSERT INTO `bible_verses_tr` VALUES ('6890', '58', '5', '5', 'ουτως και ο χριστος ουχ εαυτον εδοξασεν γενηθηναι αρχιερεα αλλ ο λαλησας προς αυτον υιος μου ει συ εγω σημερον γεγεννηκα σε');
INSERT INTO `bible_verses_tr` VALUES ('6891', '58', '5', '6', 'καθως και εν ετερω λεγει συ ιερευς εις τον αιωνα κατα την ταξιν μελχισεδεκ');
INSERT INTO `bible_verses_tr` VALUES ('6892', '58', '5', '7', 'ος εν ταις ημεραις της σαρκος αυτου δεησεις τε και ικετηριας προς τον δυναμενον σωζειν αυτον εκ θανατου μετα κραυγης ισχυρας και δακρυων προσενεγκας και εισακουσθεις απο της ευλαβειας');
INSERT INTO `bible_verses_tr` VALUES ('6893', '58', '5', '8', 'καιπερ ων υιος εμαθεν αφ ων επαθεν την υπακοην');
INSERT INTO `bible_verses_tr` VALUES ('6894', '58', '5', '9', 'και τελειωθεις εγενετο τοις υπακουουσιν αυτω πασιν αιτιος σωτηριας αιωνιου');
INSERT INTO `bible_verses_tr` VALUES ('6895', '58', '5', '10', 'προσαγορευθεις υπο του θεου αρχιερευς κατα την ταξιν μελχισεδεκ');
INSERT INTO `bible_verses_tr` VALUES ('6896', '58', '5', '11', 'περι ου πολυς ημιν ο λογος και δυσερμηνευτος λεγειν επει νωθροι γεγονατε ταις ακοαις');
INSERT INTO `bible_verses_tr` VALUES ('6897', '58', '5', '12', 'και γαρ οφειλοντες ειναι διδασκαλοι δια τον χρονον παλιν χρειαν εχετε του διδασκειν υμας τινα τα στοιχεια της αρχης των λογιων του θεου και γεγονατε χρειαν εχοντες γαλακτος και ου στερεας τροφης');
INSERT INTO `bible_verses_tr` VALUES ('6898', '58', '5', '13', 'πας γαρ ο μετεχων γαλακτος απειρος λογου δικαιοσυνης νηπιος γαρ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('6899', '58', '5', '14', 'τελειων δε εστιν η στερεα τροφη των δια την εξιν τα αισθητηρια γεγυμνασμενα εχοντων προς διακρισιν καλου τε και κακου');
INSERT INTO `bible_verses_tr` VALUES ('6900', '58', '6', '1', 'διο αφεντες τον της αρχης του χριστου λογον επι την τελειοτητα φερωμεθα μη παλιν θεμελιον καταβαλλομενοι μετανοιας απο νεκρων εργων και πιστεως επι θεον');
INSERT INTO `bible_verses_tr` VALUES ('6901', '58', '6', '2', 'βαπτισμων διδαχης επιθεσεως τε χειρων αναστασεως τε νεκρων και κριματος αιωνιου');
INSERT INTO `bible_verses_tr` VALUES ('6902', '58', '6', '3', 'και τουτο ποιησομεν εανπερ επιτρεπη ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('6903', '58', '6', '4', 'αδυνατον γαρ τους απαξ φωτισθεντας γευσαμενους τε της δωρεας της επουρανιου και μετοχους γενηθεντας πνευματος αγιου');
INSERT INTO `bible_verses_tr` VALUES ('6904', '58', '6', '5', 'και καλον γευσαμενους θεου ρημα δυναμεις τε μελλοντος αιωνος');
INSERT INTO `bible_verses_tr` VALUES ('6905', '58', '6', '6', 'και παραπεσοντας παλιν ανακαινιζειν εις μετανοιαν ανασταυρουντας εαυτοις τον υιον του θεου και παραδειγματιζοντας');
INSERT INTO `bible_verses_tr` VALUES ('6906', '58', '6', '7', 'γη γαρ η πιουσα τον επ αυτης πολλακις ερχομενον υετον και τικτουσα βοτανην ευθετον εκεινοις δι ους και γεωργειται μεταλαμβανει ευλογιας απο του θεου');
INSERT INTO `bible_verses_tr` VALUES ('6907', '58', '6', '8', 'εκφερουσα δε ακανθας και τριβολους αδοκιμος και καταρας εγγυς ης το τελος εις καυσιν');
INSERT INTO `bible_verses_tr` VALUES ('6908', '58', '6', '9', 'πεπεισμεθα δε περι υμων αγαπητοι τα κρειττονα και εχομενα σωτηριας ει και ουτως λαλουμεν');
INSERT INTO `bible_verses_tr` VALUES ('6909', '58', '6', '10', 'ου γαρ αδικος ο θεος επιλαθεσθαι του εργου υμων και του κοπου της αγαπης ης  {VAR1: ενεδειξασθε } {VAR2: ενδειξασθε } εις το ονομα αυτου διακονησαντες τοις αγιοις και διακονουντες');
INSERT INTO `bible_verses_tr` VALUES ('6910', '58', '6', '11', 'επιθυμουμεν δε εκαστον υμων την αυτην ενδεικνυσθαι σπουδην προς την πληροφοριαν της ελπιδος αχρι τελους');
INSERT INTO `bible_verses_tr` VALUES ('6911', '58', '6', '12', 'ινα μη νωθροι γενησθε μιμηται δε των δια πιστεως και μακροθυμιας κληρονομουντων τας επαγγελιας');
INSERT INTO `bible_verses_tr` VALUES ('6912', '58', '6', '13', 'τω γαρ αβρααμ επαγγειλαμενος ο θεος επει κατ ουδενος ειχεν μειζονος ομοσαι ωμοσεν καθ εαυτου');
INSERT INTO `bible_verses_tr` VALUES ('6913', '58', '6', '14', 'λεγων η μην ευλογων ευλογησω σε και πληθυνων πληθυνω σε');
INSERT INTO `bible_verses_tr` VALUES ('6914', '58', '6', '15', 'και ουτως μακροθυμησας επετυχεν της επαγγελιας');
INSERT INTO `bible_verses_tr` VALUES ('6915', '58', '6', '16', 'ανθρωποι μεν γαρ κατα του μειζονος ομνυουσιν και πασης αυτοις αντιλογιας περας εις βεβαιωσιν ο ορκος');
INSERT INTO `bible_verses_tr` VALUES ('6916', '58', '6', '17', 'εν ω περισσοτερον βουλομενος ο θεος επιδειξαι τοις κληρονομοις της επαγγελιας το αμεταθετον της βουλης αυτου εμεσιτευσεν ορκω');
INSERT INTO `bible_verses_tr` VALUES ('6917', '58', '6', '18', 'ινα δια δυο πραγματων αμεταθετων εν οις αδυνατον ψευσασθαι θεον ισχυραν παρακλησιν εχωμεν οι καταφυγοντες κρατησαι της προκειμενης ελπιδος');
INSERT INTO `bible_verses_tr` VALUES ('6918', '58', '6', '19', 'ην ως αγκυραν εχομεν της ψυχης ασφαλη τε και βεβαιαν και εισερχομενην εις το εσωτερον του καταπετασματος');
INSERT INTO `bible_verses_tr` VALUES ('6919', '58', '6', '20', 'οπου προδρομος υπερ ημων εισηλθεν ιησους κατα την ταξιν μελχισεδεκ αρχιερευς γενομενος εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('6920', '58', '7', '1', 'ουτος γαρ ο μελχισεδεκ βασιλευς σαλημ ιερευς του θεου του υψιστου ο συναντησας αβρααμ υποστρεφοντι απο της κοπης των βασιλεων και ευλογησας αυτον');
INSERT INTO `bible_verses_tr` VALUES ('6921', '58', '7', '2', 'ω και δεκατην απο παντων εμερισεν αβρααμ πρωτον μεν ερμηνευομενος βασιλευς δικαιοσυνης επειτα δε και βασιλευς σαλημ ο εστιν βασιλευς ειρηνης');
INSERT INTO `bible_verses_tr` VALUES ('6922', '58', '7', '3', 'απατωρ αμητωρ αγενεαλογητος μητε αρχην ημερων μητε ζωης τελος εχων αφωμοιωμενος δε τω υιω του θεου μενει ιερευς εις το διηνεκες');
INSERT INTO `bible_verses_tr` VALUES ('6923', '58', '7', '4', 'θεωρειτε δε πηλικος ουτος ω και δεκατην αβρααμ εδωκεν εκ των ακροθινιων ο πατριαρχης');
INSERT INTO `bible_verses_tr` VALUES ('6924', '58', '7', '5', 'και οι μεν εκ των υιων λευι την ιερατειαν λαμβανοντες εντολην εχουσιν αποδεκατουν τον λαον κατα τον νομον τουτ εστιν τους αδελφους αυτων καιπερ εξεληλυθοτας εκ της οσφυος αβρααμ');
INSERT INTO `bible_verses_tr` VALUES ('6925', '58', '7', '6', 'ο δε μη γενεαλογουμενος εξ αυτων δεδεκατωκεν τον αβρααμ και τον εχοντα τας επαγγελιας ευλογηκεν');
INSERT INTO `bible_verses_tr` VALUES ('6926', '58', '7', '7', 'χωρις δε πασης αντιλογιας το ελαττον υπο του κρειττονος ευλογειται');
INSERT INTO `bible_verses_tr` VALUES ('6927', '58', '7', '8', 'και ωδε μεν δεκατας αποθνησκοντες ανθρωποι λαμβανουσιν εκει δε μαρτυρουμενος οτι ζη');
INSERT INTO `bible_verses_tr` VALUES ('6928', '58', '7', '9', 'και ως επος ειπειν δια αβρααμ και λευι ο δεκατας λαμβανων δεδεκατωται');
INSERT INTO `bible_verses_tr` VALUES ('6929', '58', '7', '10', 'ετι γαρ εν τη οσφυι του πατρος ην οτε συνηντησεν αυτω ο μελχισεδεκ');
INSERT INTO `bible_verses_tr` VALUES ('6930', '58', '7', '11', 'ει μεν ουν τελειωσις δια της λευιτικης ιερωσυνης ην ο λαος γαρ επ αυτη νενομοθετητο τις ετι χρεια κατα την ταξιν μελχισεδεκ ετερον ανιστασθαι ιερεα και ου κατα την ταξιν ααρων λεγεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('6931', '58', '7', '12', 'μετατιθεμενης γαρ της ιερωσυνης εξ αναγκης και νομου μεταθεσις γινεται');
INSERT INTO `bible_verses_tr` VALUES ('6932', '58', '7', '13', 'εφ ον γαρ λεγεται ταυτα φυλης ετερας μετεσχηκεν αφ ης ουδεις προσεσχηκεν τω θυσιαστηριω');
INSERT INTO `bible_verses_tr` VALUES ('6933', '58', '7', '14', 'προδηλον γαρ οτι εξ ιουδα ανατεταλκεν ο κυριος ημων εις ην φυλην ουδεν περι ιερωσυνης μωσης ελαλησεν');
INSERT INTO `bible_verses_tr` VALUES ('6934', '58', '7', '15', 'και περισσοτερον ετι καταδηλον εστιν ει κατα την ομοιοτητα μελχισεδεκ ανισταται ιερευς ετερος');
INSERT INTO `bible_verses_tr` VALUES ('6935', '58', '7', '16', 'ος ου κατα νομον εντολης σαρκικης γεγονεν αλλα κατα δυναμιν ζωης ακαταλυτου');
INSERT INTO `bible_verses_tr` VALUES ('6936', '58', '7', '17', 'μαρτυρει γαρ οτι συ ιερευς εις τον αιωνα κατα την ταξιν μελχισεδεκ');
INSERT INTO `bible_verses_tr` VALUES ('6937', '58', '7', '18', 'αθετησις μεν γαρ γινεται προαγουσης εντολης δια το αυτης ασθενες και ανωφελες');
INSERT INTO `bible_verses_tr` VALUES ('6938', '58', '7', '19', 'ουδεν γαρ ετελειωσεν ο νομος επεισαγωγη δε κρειττονος ελπιδος δι ης εγγιζομεν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('6939', '58', '7', '20', 'και καθ οσον ου χωρις ορκωμοσιας');
INSERT INTO `bible_verses_tr` VALUES ('6940', '58', '7', '21', 'οι μεν γαρ χωρις ορκωμοσιας εισιν ιερεις γεγονοτες ο δε μετα ορκωμοσιας δια του λεγοντος προς αυτον ωμοσεν κυριος και ου μεταμεληθησεται συ ιερευς εις τον αιωνα κατα την ταξιν μελχισεδεκ');
INSERT INTO `bible_verses_tr` VALUES ('6941', '58', '7', '22', 'κατα τοσουτον κρειττονος διαθηκης γεγονεν εγγυος ιησους');
INSERT INTO `bible_verses_tr` VALUES ('6942', '58', '7', '23', 'και οι μεν πλειονες εισιν γεγονοτες ιερεις δια το θανατω κωλυεσθαι παραμενειν');
INSERT INTO `bible_verses_tr` VALUES ('6943', '58', '7', '24', 'ο δε δια το μενειν αυτον εις τον αιωνα απαραβατον εχει την ιερωσυνην');
INSERT INTO `bible_verses_tr` VALUES ('6944', '58', '7', '25', 'οθεν και σωζειν εις το παντελες δυναται τους προσερχομενους δι αυτου τω θεω παντοτε ζων εις το εντυγχανειν υπερ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('6945', '58', '7', '26', 'τοιουτος γαρ ημιν επρεπεν αρχιερευς οσιος ακακος αμιαντος κεχωρισμενος απο των αμαρτωλων και υψηλοτερος των ουρανων γενομενος');
INSERT INTO `bible_verses_tr` VALUES ('6946', '58', '7', '27', 'ος ουκ εχει καθ ημεραν αναγκην ωσπερ οι αρχιερεις προτερον υπερ των ιδιων αμαρτιων θυσιας αναφερειν επειτα των του λαου τουτο γαρ εποιησεν εφαπαξ εαυτον ανενεγκας');
INSERT INTO `bible_verses_tr` VALUES ('6947', '58', '7', '28', 'ο νομος γαρ ανθρωπους καθιστησιν αρχιερεις εχοντας ασθενειαν ο λογος δε της ορκωμοσιας της μετα τον νομον υιον εις τον αιωνα τετελειωμενον');
INSERT INTO `bible_verses_tr` VALUES ('6948', '58', '8', '1', 'κεφαλαιον δε επι τοις λεγομενοις τοιουτον εχομεν αρχιερεα ος εκαθισεν εν δεξια του θρονου της μεγαλωσυνης εν τοις ουρανοις');
INSERT INTO `bible_verses_tr` VALUES ('6949', '58', '8', '2', 'των αγιων λειτουργος και της σκηνης της αληθινης ην επηξεν ο κυριος και ουκ ανθρωπος');
INSERT INTO `bible_verses_tr` VALUES ('6950', '58', '8', '3', 'πας γαρ αρχιερευς εις το προσφερειν δωρα τε και θυσιας καθισταται οθεν αναγκαιον εχειν τι και τουτον ο προσενεγκη');
INSERT INTO `bible_verses_tr` VALUES ('6951', '58', '8', '4', 'ει μεν γαρ ην επι γης ουδ αν ην ιερευς οντων των ιερεων των προσφεροντων κατα τον νομον τα δωρα');
INSERT INTO `bible_verses_tr` VALUES ('6952', '58', '8', '5', 'οιτινες υποδειγματι και σκια λατρευουσιν των επουρανιων καθως κεχρηματισται μωσης μελλων επιτελειν την σκηνην ορα γαρ φησιν ποιησης παντα κατα τον τυπον τον δειχθεντα σοι εν τω ορει');
INSERT INTO `bible_verses_tr` VALUES ('6953', '58', '8', '6', 'νυνι δε διαφορωτερας τετευχεν λειτουργιας οσω και κρειττονος εστιν διαθηκης μεσιτης ητις επι κρειττοσιν επαγγελιαις νενομοθετηται');
INSERT INTO `bible_verses_tr` VALUES ('6954', '58', '8', '7', 'ει γαρ η πρωτη εκεινη ην αμεμπτος ουκ αν δευτερας εζητειτο τοπος');
INSERT INTO `bible_verses_tr` VALUES ('6955', '58', '8', '8', 'μεμφομενος γαρ αυτοις λεγει ιδου ημεραι ερχονται λεγει κυριος και συντελεσω επι τον οικον ισραηλ και επι τον οικον ιουδα διαθηκην καινην');
INSERT INTO `bible_verses_tr` VALUES ('6956', '58', '8', '9', 'ου κατα την διαθηκην ην εποιησα τοις πατρασιν αυτων εν ημερα επιλαβομενου μου της χειρος αυτων εξαγαγειν αυτους εκ γης αιγυπτου οτι αυτοι ουκ ενεμειναν εν τη διαθηκη μου καγω ημελησα αυτων λεγει κυριος');
INSERT INTO `bible_verses_tr` VALUES ('6957', '58', '8', '10', 'οτι αυτη η διαθηκη ην διαθησομαι τω οικω ισραηλ μετα τας ημερας εκεινας λεγει κυριος διδους νομους μου εις την διανοιαν αυτων και επι καρδιας αυτων επιγραψω αυτους και εσομαι αυτοις εις θεον και αυτοι εσονται μοι εις λαον');
INSERT INTO `bible_verses_tr` VALUES ('6958', '58', '8', '11', 'και ου μη διδαξωσιν εκαστος τον πλησιον αυτου και εκαστος τον αδελφον αυτου λεγων γνωθι τον κυριον οτι παντες ειδησουσιν με απο μικρου αυτων εως μεγαλου αυτων');
INSERT INTO `bible_verses_tr` VALUES ('6959', '58', '8', '12', 'οτι ιλεως εσομαι ταις αδικιαις αυτων και των αμαρτιων αυτων και των ανομιων αυτων ου μη μνησθω ετι');
INSERT INTO `bible_verses_tr` VALUES ('6960', '58', '8', '13', 'εν τω λεγειν καινην πεπαλαιωκεν την πρωτην το δε παλαιουμενον και γηρασκον εγγυς αφανισμου');
INSERT INTO `bible_verses_tr` VALUES ('6961', '58', '9', '1', 'ειχεν μεν ουν και η πρωτη  {VAR1: σκηνη } δικαιωματα λατρειας το τε αγιον κοσμικον');
INSERT INTO `bible_verses_tr` VALUES ('6962', '58', '9', '2', 'σκηνη γαρ κατεσκευασθη η πρωτη εν η η τε λυχνια και η τραπεζα και η προθεσις των αρτων ητις λεγεται αγια');
INSERT INTO `bible_verses_tr` VALUES ('6963', '58', '9', '3', 'μετα δε το δευτερον καταπετασμα σκηνη η λεγομενη αγια αγιων');
INSERT INTO `bible_verses_tr` VALUES ('6964', '58', '9', '4', 'χρυσουν εχουσα θυμιατηριον και την κιβωτον της διαθηκης περικεκαλυμμενην παντοθεν χρυσιω εν η σταμνος χρυση εχουσα το μαννα και η ραβδος ααρων η βλαστησασα και αι πλακες της διαθηκης');
INSERT INTO `bible_verses_tr` VALUES ('6965', '58', '9', '5', 'υπερανω δε αυτης χερουβιμ δοξης κατασκιαζοντα το ιλαστηριον περι ων ουκ εστιν νυν λεγειν κατα μερος');
INSERT INTO `bible_verses_tr` VALUES ('6966', '58', '9', '6', 'τουτων δε ουτως κατεσκευασμενων εις μεν την πρωτην σκηνην διαπαντος εισιασιν οι ιερεις τας λατρειας επιτελουντες');
INSERT INTO `bible_verses_tr` VALUES ('6967', '58', '9', '7', 'εις δε την δευτεραν απαξ του ενιαυτου μονος ο αρχιερευς ου χωρις αιματος ο προσφερει υπερ εαυτου και των του λαου αγνοηματων');
INSERT INTO `bible_verses_tr` VALUES ('6968', '58', '9', '8', 'τουτο δηλουντος του πνευματος του αγιου μηπω πεφανερωσθαι την των αγιων οδον ετι της πρωτης σκηνης εχουσης στασιν');
INSERT INTO `bible_verses_tr` VALUES ('6969', '58', '9', '9', 'ητις παραβολη εις τον καιρον τον ενεστηκοτα καθ ον δωρα τε και θυσιαι προσφερονται μη δυναμεναι κατα συνειδησιν τελειωσαι τον λατρευοντα');
INSERT INTO `bible_verses_tr` VALUES ('6970', '58', '9', '10', 'μονον επι βρωμασιν και πομασιν και διαφοροις βαπτισμοις και δικαιωμασιν σαρκος μεχρι καιρου διορθωσεως επικειμενα');
INSERT INTO `bible_verses_tr` VALUES ('6971', '58', '9', '11', 'χριστος δε παραγενομενος αρχιερευς των μελλοντων αγαθων δια της μειζονος και τελειοτερας σκηνης ου χειροποιητου τουτ εστιν ου ταυτης της κτισεως');
INSERT INTO `bible_verses_tr` VALUES ('6972', '58', '9', '12', 'ουδε δι αιματος τραγων και μοσχων δια δε του ιδιου αιματος εισηλθεν εφαπαξ εις τα αγια αιωνιαν λυτρωσιν ευραμενος');
INSERT INTO `bible_verses_tr` VALUES ('6973', '58', '9', '13', 'ει γαρ το αιμα ταυρων και τραγων και σποδος δαμαλεως ραντιζουσα τους κεκοινωμενους αγιαζει προς την της σαρκος καθαροτητα');
INSERT INTO `bible_verses_tr` VALUES ('6974', '58', '9', '14', 'ποσω μαλλον το αιμα του χριστου ος δια πνευματος αιωνιου εαυτον προσηνεγκεν αμωμον τω θεω καθαριει την συνειδησιν υμων απο νεκρων εργων εις το λατρευειν θεω ζωντι');
INSERT INTO `bible_verses_tr` VALUES ('6975', '58', '9', '15', 'και δια τουτο διαθηκης καινης μεσιτης εστιν οπως θανατου γενομενου εις απολυτρωσιν των επι τη πρωτη διαθηκη παραβασεων την επαγγελιαν λαβωσιν οι κεκλημενοι της αιωνιου κληρονομιας');
INSERT INTO `bible_verses_tr` VALUES ('6976', '58', '9', '16', 'οπου γαρ διαθηκη θανατον αναγκη φερεσθαι του διαθεμενου');
INSERT INTO `bible_verses_tr` VALUES ('6977', '58', '9', '17', 'διαθηκη γαρ επι νεκροις βεβαια επει μηποτε ισχυει οτε ζη ο διαθεμενος');
INSERT INTO `bible_verses_tr` VALUES ('6978', '58', '9', '18', 'οθεν ουδ η πρωτη χωρις αιματος εγκεκαινισται');
INSERT INTO `bible_verses_tr` VALUES ('6979', '58', '9', '19', 'λαληθεισης γαρ πασης εντολης κατα νομον υπο μωυσεως παντι τω λαω λαβων το αιμα των μοσχων και τραγων μετα υδατος και εριου κοκκινου και υσσωπου αυτο τε το βιβλιον και παντα τον λαον ερραντισεν');
INSERT INTO `bible_verses_tr` VALUES ('6980', '58', '9', '20', 'λεγων τουτο το αιμα της διαθηκης ης ενετειλατο προς υμας ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('6981', '58', '9', '21', 'και την σκηνην δε και παντα τα σκευη της λειτουργιας τω αιματι ομοιως ερραντισεν');
INSERT INTO `bible_verses_tr` VALUES ('6982', '58', '9', '22', 'και σχεδον εν αιματι παντα καθαριζεται κατα τον νομον και χωρις αιματεκχυσιας ου γινεται αφεσις');
INSERT INTO `bible_verses_tr` VALUES ('6983', '58', '9', '23', 'αναγκη ουν τα μεν υποδειγματα των εν τοις ουρανοις τουτοις καθαριζεσθαι αυτα δε τα επουρανια κρειττοσιν θυσιαις παρα ταυτας');
INSERT INTO `bible_verses_tr` VALUES ('6984', '58', '9', '24', 'ου γαρ εις χειροποιητα αγια εισηλθεν ο χριστος αντιτυπα των αληθινων αλλ εις αυτον τον ουρανον νυν εμφανισθηναι τω προσωπω του θεου υπερ ημων');
INSERT INTO `bible_verses_tr` VALUES ('6985', '58', '9', '25', 'ουδ ινα πολλακις προσφερη εαυτον ωσπερ ο αρχιερευς εισερχεται εις τα αγια κατ ενιαυτον εν αιματι αλλοτριω');
INSERT INTO `bible_verses_tr` VALUES ('6986', '58', '9', '26', 'επει εδει αυτον πολλακις παθειν απο καταβολης κοσμου νυν δε απαξ επι συντελεια των αιωνων εις αθετησιν αμαρτιας δια της θυσιας αυτου πεφανερωται');
INSERT INTO `bible_verses_tr` VALUES ('6987', '58', '9', '27', 'και καθ οσον αποκειται τοις ανθρωποις απαξ αποθανειν μετα δε τουτο κρισις');
INSERT INTO `bible_verses_tr` VALUES ('6988', '58', '9', '28', 'ουτως ο χριστος απαξ προσενεχθεις εις το πολλων ανενεγκειν αμαρτιας εκ δευτερου χωρις αμαρτιας οφθησεται τοις αυτον απεκδεχομενοις εις σωτηριαν');
INSERT INTO `bible_verses_tr` VALUES ('6989', '58', '10', '1', 'σκιαν γαρ εχων ο νομος των μελλοντων αγαθων ουκ αυτην την εικονα των πραγματων κατ ενιαυτον ταις αυταις θυσιαις ας προσφερουσιν εις το διηνεκες ουδεποτε δυναται τους προσερχομενους τελειωσαι');
INSERT INTO `bible_verses_tr` VALUES ('6990', '58', '10', '2', 'επει ουκ αν επαυσαντο προσφερομεναι δια το μηδεμιαν εχειν ετι συνειδησιν αμαρτιων τους λατρευοντας απαξ κεκαθαρμενους');
INSERT INTO `bible_verses_tr` VALUES ('6991', '58', '10', '3', 'αλλ εν αυταις αναμνησις αμαρτιων κατ ενιαυτον');
INSERT INTO `bible_verses_tr` VALUES ('6992', '58', '10', '4', 'αδυνατον γαρ αιμα ταυρων και τραγων αφαιρειν αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('6993', '58', '10', '5', 'διο εισερχομενος εις τον κοσμον λεγει θυσιαν και προσφοραν ουκ ηθελησας σωμα δε κατηρτισω μοι');
INSERT INTO `bible_verses_tr` VALUES ('6994', '58', '10', '6', 'ολοκαυτωματα και περι αμαρτιας ουκ ευδοκησας');
INSERT INTO `bible_verses_tr` VALUES ('6995', '58', '10', '7', 'τοτε ειπον ιδου ηκω εν κεφαλιδι βιβλιου γεγραπται περι εμου του ποιησαι ο θεος το θελημα σου');
INSERT INTO `bible_verses_tr` VALUES ('6996', '58', '10', '8', 'ανωτερον λεγων οτι θυσιαν και προσφοραν και ολοκαυτωματα και περι αμαρτιας ουκ ηθελησας ουδε ευδοκησας αιτινες κατα τον νομον προσφερονται');
INSERT INTO `bible_verses_tr` VALUES ('6997', '58', '10', '9', 'τοτε ειρηκεν ιδου ηκω του ποιησαι ο θεος το θελημα σου αναιρει το πρωτον ινα το δευτερον στηση');
INSERT INTO `bible_verses_tr` VALUES ('6998', '58', '10', '10', 'εν ω θεληματι ηγιασμενοι εσμεν  {VAR1: οι } δια της προσφορας του σωματος του ιησου χριστου εφαπαξ');
INSERT INTO `bible_verses_tr` VALUES ('6999', '58', '10', '11', 'και πας μεν ιερευς εστηκεν καθ ημεραν λειτουργων και τας αυτας πολλακις προσφερων θυσιας αιτινες ουδεποτε δυνανται περιελειν αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('7000', '58', '10', '12', 'αυτος δε μιαν υπερ αμαρτιων προσενεγκας θυσιαν εις το διηνεκες εκαθισεν εν δεξια του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7001', '58', '10', '13', 'το λοιπον εκδεχομενος εως τεθωσιν οι εχθροι αυτου υποποδιον των ποδων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7002', '58', '10', '14', 'μια γαρ προσφορα τετελειωκεν εις το διηνεκες τους αγιαζομενους');
INSERT INTO `bible_verses_tr` VALUES ('7003', '58', '10', '15', 'μαρτυρει δε ημιν και το πνευμα το αγιον μετα γαρ το προειρηκεναι');
INSERT INTO `bible_verses_tr` VALUES ('7004', '58', '10', '16', 'αυτη η διαθηκη ην διαθησομαι προς αυτους μετα τας ημερας εκεινας λεγει κυριος διδους νομους μου επι καρδιας αυτων και επι των διανοιων αυτων επιγραψω αυτους');
INSERT INTO `bible_verses_tr` VALUES ('7005', '58', '10', '17', 'και των αμαρτιων αυτων και των ανομιων αυτων ου μη μνησθω ετι');
INSERT INTO `bible_verses_tr` VALUES ('7006', '58', '10', '18', 'οπου δε αφεσις τουτων ουκετι προσφορα περι αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('7007', '58', '10', '19', 'εχοντες ουν αδελφοι παρρησιαν εις την εισοδον των αγιων εν τω αιματι ιησου');
INSERT INTO `bible_verses_tr` VALUES ('7008', '58', '10', '20', 'ην ενεκαινισεν ημιν οδον προσφατον και ζωσαν δια του καταπετασματος τουτ εστιν της σαρκος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7009', '58', '10', '21', 'και ιερεα μεγαν επι τον οικον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7010', '58', '10', '22', 'προσερχωμεθα μετα αληθινης καρδιας εν πληροφορια πιστεως ερραντισμενοι τας καρδιας απο συνειδησεως πονηρας και λελουμενοι το σωμα υδατι καθαρω');
INSERT INTO `bible_verses_tr` VALUES ('7011', '58', '10', '23', 'κατεχωμεν την ομολογιαν της ελπιδος ακλινη πιστος γαρ ο επαγγειλαμενος');
INSERT INTO `bible_verses_tr` VALUES ('7012', '58', '10', '24', 'και κατανοωμεν αλληλους εις παροξυσμον αγαπης και καλων εργων');
INSERT INTO `bible_verses_tr` VALUES ('7013', '58', '10', '25', 'μη εγκαταλειποντες την επισυναγωγην εαυτων καθως εθος τισιν αλλα παρακαλουντες και τοσουτω μαλλον οσω βλεπετε εγγιζουσαν την ημεραν');
INSERT INTO `bible_verses_tr` VALUES ('7014', '58', '10', '26', 'εκουσιως γαρ αμαρτανοντων ημων μετα το λαβειν την επιγνωσιν της αληθειας ουκετι περι αμαρτιων απολειπεται θυσια');
INSERT INTO `bible_verses_tr` VALUES ('7015', '58', '10', '27', 'φοβερα δε τις εκδοχη κρισεως και πυρος ζηλος εσθιειν μελλοντος τους υπεναντιους');
INSERT INTO `bible_verses_tr` VALUES ('7016', '58', '10', '28', 'αθετησας τις νομον μωσεως χωρις οικτιρμων επι δυσιν η τρισιν μαρτυσιν αποθνησκει');
INSERT INTO `bible_verses_tr` VALUES ('7017', '58', '10', '29', 'ποσω δοκειτε χειρονος αξιωθησεται τιμωριας ο τον υιον του θεου καταπατησας και το αιμα της διαθηκης κοινον ηγησαμενος εν ω ηγιασθη και το πνευμα της χαριτος ενυβρισας');
INSERT INTO `bible_verses_tr` VALUES ('7018', '58', '10', '30', 'οιδαμεν γαρ τον ειποντα εμοι εκδικησις εγω ανταποδωσω λεγει κυριος και παλιν κυριος κρινει τον λαον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7019', '58', '10', '31', 'φοβερον το εμπεσειν εις χειρας θεου ζωντος');
INSERT INTO `bible_verses_tr` VALUES ('7020', '58', '10', '32', 'αναμιμνησκεσθε δε τας προτερον ημερας εν αις φωτισθεντες πολλην αθλησιν υπεμεινατε παθηματων');
INSERT INTO `bible_verses_tr` VALUES ('7021', '58', '10', '33', 'τουτο μεν ονειδισμοις τε και θλιψεσιν θεατριζομενοι τουτο δε κοινωνοι των ουτως αναστρεφομενων γενηθεντες');
INSERT INTO `bible_verses_tr` VALUES ('7022', '58', '10', '34', 'και γαρ τοις δεσμοις μου συνεπαθησατε και την αρπαγην των υπαρχοντων υμων μετα χαρας προσεδεξασθε γινωσκοντες εχειν εν εαυτοις κρειττονα υπαρξιν εν ουρανοις και μενουσαν');
INSERT INTO `bible_verses_tr` VALUES ('7023', '58', '10', '35', 'μη αποβαλητε ουν την παρρησιαν υμων ητις εχει μισθαποδοσιαν μεγαλην');
INSERT INTO `bible_verses_tr` VALUES ('7024', '58', '10', '36', 'υπομονης γαρ εχετε χρειαν ινα το θελημα του θεου ποιησαντες κομισησθε την επαγγελιαν');
INSERT INTO `bible_verses_tr` VALUES ('7025', '58', '10', '37', 'ετι γαρ μικρον οσον οσον ο ερχομενος ηξει και ου χρονιει');
INSERT INTO `bible_verses_tr` VALUES ('7026', '58', '10', '38', 'ο δε δικαιος εκ πιστεως ζησεται και εαν υποστειληται ουκ ευδοκει η ψυχη μου εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7027', '58', '10', '39', 'ημεις δε ουκ εσμεν υποστολης εις απωλειαν αλλα πιστεως εις περιποιησιν ψυχης');
INSERT INTO `bible_verses_tr` VALUES ('7028', '58', '11', '1', 'εστιν δε πιστις ελπιζομενων υποστασις πραγματων ελεγχος ου βλεπομενων');
INSERT INTO `bible_verses_tr` VALUES ('7029', '58', '11', '2', 'εν ταυτη γαρ εμαρτυρηθησαν οι πρεσβυτεροι');
INSERT INTO `bible_verses_tr` VALUES ('7030', '58', '11', '3', 'πιστει νοουμεν κατηρτισθαι τους αιωνας ρηματι θεου εις το μη εκ φαινομενων τα βλεπομενα γεγονεναι');
INSERT INTO `bible_verses_tr` VALUES ('7031', '58', '11', '4', 'πιστει πλειονα θυσιαν αβελ παρα καιν προσηνεγκεν τω θεω δι ης εμαρτυρηθη ειναι δικαιος μαρτυρουντος επι τοις δωροις αυτου του θεου και δι αυτης αποθανων ετι  {VAR1: λαλειται } {VAR2: λαλει }');
INSERT INTO `bible_verses_tr` VALUES ('7032', '58', '11', '5', 'πιστει ενωχ μετετεθη του μη ιδειν θανατον και ουχ ευρισκετο διοτι μετεθηκεν αυτον ο θεος προ γαρ της μεταθεσεως αυτου μεμαρτυρηται ευηρεστηκεναι τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('7033', '58', '11', '6', 'χωρις δε πιστεως αδυνατον ευαρεστησαι πιστευσαι γαρ δει τον προσερχομενον τω θεω οτι εστιν και τοις εκζητουσιν αυτον μισθαποδοτης γινεται');
INSERT INTO `bible_verses_tr` VALUES ('7034', '58', '11', '7', 'πιστει χρηματισθεις νωε περι των μηδεπω βλεπομενων ευλαβηθεις κατεσκευασεν κιβωτον εις σωτηριαν του οικου αυτου δι ης κατεκρινεν τον κοσμον και της κατα πιστιν δικαιοσυνης εγενετο κληρονομος');
INSERT INTO `bible_verses_tr` VALUES ('7035', '58', '11', '8', 'πιστει καλουμενος αβρααμ υπηκουσεν εξελθειν εις τον τοπον ον ημελλεν λαμβανειν εις κληρονομιαν και εξηλθεν μη επισταμενος που ερχεται');
INSERT INTO `bible_verses_tr` VALUES ('7036', '58', '11', '9', 'πιστει παρωκησεν εις την γην της επαγγελιας ως αλλοτριαν εν σκηναις κατοικησας μετα ισαακ και ιακωβ των συγκληρονομων της επαγγελιας της αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7037', '58', '11', '10', 'εξεδεχετο γαρ την τους θεμελιους εχουσαν πολιν ης τεχνιτης και δημιουργος ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('7038', '58', '11', '11', 'πιστει και αυτη σαρρα δυναμιν εις καταβολην σπερματος ελαβεν και παρα καιρον ηλικιας ετεκεν επει πιστον ηγησατο τον επαγγειλαμενον');
INSERT INTO `bible_verses_tr` VALUES ('7039', '58', '11', '12', 'διο και αφ ενος εγεννηθησαν και ταυτα νενεκρωμενου καθως τα αστρα του ουρανου τω πληθει και ωσει αμμος η παρα το χειλος της θαλασσης η αναριθμητος');
INSERT INTO `bible_verses_tr` VALUES ('7040', '58', '11', '13', 'κατα πιστιν απεθανον ουτοι παντες μη λαβοντες τας επαγγελιας αλλα πορρωθεν αυτας ιδοντες και πεισθεντες και ασπασαμενοι και ομολογησαντες οτι ξενοι και παρεπιδημοι εισιν επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7041', '58', '11', '14', 'οι γαρ τοιαυτα λεγοντες εμφανιζουσιν οτι πατριδα επιζητουσιν');
INSERT INTO `bible_verses_tr` VALUES ('7042', '58', '11', '15', 'και ει μεν εκεινης εμνημονευον αφ ης εξηλθον ειχον αν καιρον ανακαμψαι');
INSERT INTO `bible_verses_tr` VALUES ('7043', '58', '11', '16', 'νυνι δε κρειττονος ορεγονται τουτ εστιν επουρανιου διο ουκ επαισχυνεται αυτους ο θεος θεος επικαλεισθαι αυτων ητοιμασεν γαρ αυτοις πολιν');
INSERT INTO `bible_verses_tr` VALUES ('7044', '58', '11', '17', 'πιστει προσενηνοχεν αβρααμ τον ισαακ πειραζομενος και τον μονογενη προσεφερεν ο τας επαγγελιας αναδεξαμενος');
INSERT INTO `bible_verses_tr` VALUES ('7045', '58', '11', '18', 'προς ον ελαληθη οτι εν ισαακ κληθησεται σοι σπερμα');
INSERT INTO `bible_verses_tr` VALUES ('7046', '58', '11', '19', 'λογισαμενος οτι και εκ νεκρων εγειρειν δυνατος ο θεος οθεν αυτον και εν παραβολη εκομισατο');
INSERT INTO `bible_verses_tr` VALUES ('7047', '58', '11', '20', 'πιστει περι μελλοντων ευλογησεν ισαακ τον ιακωβ και τον ησαυ');
INSERT INTO `bible_verses_tr` VALUES ('7048', '58', '11', '21', 'πιστει ιακωβ αποθνησκων εκαστον των υιων ιωσηφ ευλογησεν και προσεκυνησεν επι το ακρον της ραβδου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7049', '58', '11', '22', 'πιστει ιωσηφ τελευτων περι της εξοδου των υιων ισραηλ εμνημονευσεν και περι των οστεων αυτου ενετειλατο');
INSERT INTO `bible_verses_tr` VALUES ('7050', '58', '11', '23', 'πιστει μωσης γεννηθεις εκρυβη τριμηνον υπο των πατερων αυτου διοτι ειδον αστειον το παιδιον και ουκ εφοβηθησαν το διαταγμα του βασιλεως');
INSERT INTO `bible_verses_tr` VALUES ('7051', '58', '11', '24', 'πιστει μωσης μεγας γενομενος ηρνησατο λεγεσθαι υιος θυγατρος φαραω');
INSERT INTO `bible_verses_tr` VALUES ('7052', '58', '11', '25', 'μαλλον ελομενος συγκακουχεισθαι τω λαω του θεου η προσκαιρον εχειν αμαρτιας απολαυσιν');
INSERT INTO `bible_verses_tr` VALUES ('7053', '58', '11', '26', 'μειζονα πλουτον ηγησαμενος των εν αιγυπτω θησαυρων τον ονειδισμον του χριστου απεβλεπεν γαρ εις την μισθαποδοσιαν');
INSERT INTO `bible_verses_tr` VALUES ('7054', '58', '11', '27', 'πιστει κατελιπεν αιγυπτον μη φοβηθεις τον θυμον του βασιλεως τον γαρ αορατον ως ορων εκαρτερησεν');
INSERT INTO `bible_verses_tr` VALUES ('7055', '58', '11', '28', 'πιστει πεποιηκεν το πασχα και την προσχυσιν του αιματος ινα μη ο ολοθρευων τα πρωτοτοκα θιγη αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7056', '58', '11', '29', 'πιστει διεβησαν την ερυθραν θαλασσαν ως δια ξηρας ης πειραν λαβοντες οι αιγυπτιοι κατεποθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7057', '58', '11', '30', 'πιστει τα τειχη ιεριχω επεσεν κυκλωθεντα επι επτα ημερας');
INSERT INTO `bible_verses_tr` VALUES ('7058', '58', '11', '31', 'πιστει ρααβ η πορνη ου συναπωλετο τοις απειθησασιν δεξαμενη τους κατασκοπους μετ ειρηνης');
INSERT INTO `bible_verses_tr` VALUES ('7059', '58', '11', '32', 'και τι ετι λεγω επιλειψει γαρ με διηγουμενον ο χρονος περι γεδεων βαρακ τε και σαμψων και ιεφθαε δαβιδ τε και σαμουηλ και των προφητων');
INSERT INTO `bible_verses_tr` VALUES ('7060', '58', '11', '33', 'οι δια πιστεως κατηγωνισαντο βασιλειας ειργασαντο δικαιοσυνην επετυχον επαγγελιων εφραξαν στοματα λεοντων');
INSERT INTO `bible_verses_tr` VALUES ('7061', '58', '11', '34', 'εσβεσαν δυναμιν πυρος εφυγον στοματα μαχαιρας ενεδυναμωθησαν απο ασθενειας εγενηθησαν ισχυροι εν πολεμω παρεμβολας εκλιναν αλλοτριων');
INSERT INTO `bible_verses_tr` VALUES ('7062', '58', '11', '35', 'ελαβον γυναικες εξ αναστασεως τους νεκρους αυτων αλλοι δε ετυμπανισθησαν ου προσδεξαμενοι την απολυτρωσιν ινα κρειττονος αναστασεως τυχωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7063', '58', '11', '36', 'ετεροι δε εμπαιγμων και μαστιγων πειραν ελαβον ετι δε δεσμων και φυλακης');
INSERT INTO `bible_verses_tr` VALUES ('7064', '58', '11', '37', 'ελιθασθησαν επρισθησαν επειρασθησαν εν φονω μαχαιρας απεθανον περιηλθον εν μηλωταις εν αιγειοις δερμασιν υστερουμενοι θλιβομενοι κακουχουμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7065', '58', '11', '38', 'ων ουκ ην αξιος ο κοσμος εν ερημιαις πλανωμενοι και ορεσιν και σπηλαιοις και ταις οπαις της γης');
INSERT INTO `bible_verses_tr` VALUES ('7066', '58', '11', '39', 'και ουτοι παντες μαρτυρηθεντες δια της πιστεως ουκ εκομισαντο την επαγγελιαν');
INSERT INTO `bible_verses_tr` VALUES ('7067', '58', '11', '40', 'του θεου περι ημων κρειττον τι προβλεψαμενου ινα μη χωρις ημων τελειωθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7068', '58', '12', '1', 'τοιγαρουν και ημεις τοσουτον εχοντες περικειμενον ημιν νεφος μαρτυρων ογκον αποθεμενοι παντα και την ευπεριστατον αμαρτιαν δι υπομονης τρεχωμεν τον προκειμενον ημιν αγωνα');
INSERT INTO `bible_verses_tr` VALUES ('7069', '58', '12', '2', 'αφορωντες εις τον της πιστεως αρχηγον και τελειωτην ιησουν ος αντι της προκειμενης αυτω χαρας υπεμεινεν σταυρον αισχυνης καταφρονησας εν δεξια τε του θρονου του θεου εκαθισεν');
INSERT INTO `bible_verses_tr` VALUES ('7070', '58', '12', '3', 'αναλογισασθε γαρ τον τοιαυτην υπομεμενηκοτα υπο των αμαρτωλων εις αυτον αντιλογιαν ινα μη καμητε ταις ψυχαις υμων εκλυομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7071', '58', '12', '4', 'ουπω μεχρις αιματος αντικατεστητε προς την αμαρτιαν ανταγωνιζομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7072', '58', '12', '5', 'και εκλελησθε της παρακλησεως ητις υμιν ως υιοις διαλεγεται υιε μου μη ολιγωρει παιδειας κυριου μηδε εκλυου υπ αυτου ελεγχομενος');
INSERT INTO `bible_verses_tr` VALUES ('7073', '58', '12', '6', 'ον γαρ αγαπα κυριος παιδευει μαστιγοι δε παντα υιον ον παραδεχεται');
INSERT INTO `bible_verses_tr` VALUES ('7074', '58', '12', '7', 'ει παιδειαν υπομενετε ως υιοις υμιν προσφερεται ο θεος τις γαρ εστιν υιος ον ου παιδευει πατηρ');
INSERT INTO `bible_verses_tr` VALUES ('7075', '58', '12', '8', 'ει δε χωρις εστε παιδειας ης μετοχοι γεγονασιν παντες αρα νοθοι εστε και ουχ υιοι');
INSERT INTO `bible_verses_tr` VALUES ('7076', '58', '12', '9', 'ειτα τους μεν της σαρκος ημων πατερας ειχομεν παιδευτας και ενετρεπομεθα ου πολλω μαλλον υποταγησομεθα τω πατρι των πνευματων και ζησομεν');
INSERT INTO `bible_verses_tr` VALUES ('7077', '58', '12', '10', 'οι μεν γαρ προς ολιγας ημερας κατα το δοκουν αυτοις επαιδευον ο δε επι το συμφερον εις το μεταλαβειν της αγιοτητος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7078', '58', '12', '11', 'πασα δε παιδεια προς μεν το παρον ου δοκει χαρας ειναι αλλα λυπης υστερον δε καρπον ειρηνικον τοις δι αυτης γεγυμνασμενοις αποδιδωσιν δικαιοσυνης');
INSERT INTO `bible_verses_tr` VALUES ('7079', '58', '12', '12', 'διο τας παρειμενας χειρας και τα παραλελυμενα γονατα ανορθωσατε');
INSERT INTO `bible_verses_tr` VALUES ('7080', '58', '12', '13', 'και τροχιας ορθας ποιησατε τοις ποσιν υμων ινα μη το χωλον εκτραπη ιαθη δε μαλλον');
INSERT INTO `bible_verses_tr` VALUES ('7081', '58', '12', '14', 'ειρηνην διωκετε μετα παντων και τον αγιασμον ου χωρις ουδεις οψεται τον κυριον');
INSERT INTO `bible_verses_tr` VALUES ('7082', '58', '12', '15', 'επισκοπουντες μη τις υστερων απο της χαριτος του θεου μη τις ριζα πικριας ανω φυουσα ενοχλη και δια ταυτης μιανθωσιν πολλοι');
INSERT INTO `bible_verses_tr` VALUES ('7083', '58', '12', '16', 'μη τις πορνος η βεβηλος ως ησαυ ος αντι βρωσεως μιας απεδοτο τα πρωτοτοκια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7084', '58', '12', '17', 'ιστε γαρ οτι και μετεπειτα θελων κληρονομησαι την ευλογιαν απεδοκιμασθη μετανοιας γαρ τοπον ουχ ευρεν καιπερ μετα δακρυων εκζητησας αυτην');
INSERT INTO `bible_verses_tr` VALUES ('7085', '58', '12', '18', 'ου γαρ προσεληλυθατε ψηλαφωμενω ορει και κεκαυμενω πυρι και γνοφω και σκοτω και θυελλη');
INSERT INTO `bible_verses_tr` VALUES ('7086', '58', '12', '19', 'και σαλπιγγος ηχω και φωνη ρηματων ης οι ακουσαντες παρητησαντο μη προστεθηναι αυτοις λογον');
INSERT INTO `bible_verses_tr` VALUES ('7087', '58', '12', '20', 'ουκ εφερον γαρ το διαστελλομενον καν θηριον θιγη του ορους λιθοβοληθησεται η βολιδι κατατοξευθησεται');
INSERT INTO `bible_verses_tr` VALUES ('7088', '58', '12', '21', 'και ουτως φοβερον ην το φανταζομενον μωσης ειπεν εκφοβος ειμι και εντρομος');
INSERT INTO `bible_verses_tr` VALUES ('7089', '58', '12', '22', 'αλλα προσεληλυθατε σιων ορει και πολει θεου ζωντος ιερουσαλημ επουρανιω και μυριασιν αγγελων');
INSERT INTO `bible_verses_tr` VALUES ('7090', '58', '12', '23', 'πανηγυρει και εκκλησια πρωτοτοκων εν ουρανοις απογεγραμμενων και κριτη θεω παντων και πνευμασιν δικαιων τετελειωμενων');
INSERT INTO `bible_verses_tr` VALUES ('7091', '58', '12', '24', 'και διαθηκης νεας μεσιτη ιησου και αιματι ραντισμου κρειττονα λαλουντι παρα  {VAR1: τον } {VAR2: το } αβελ');
INSERT INTO `bible_verses_tr` VALUES ('7092', '58', '12', '25', 'βλεπετε μη παραιτησησθε τον λαλουντα ει γαρ εκεινοι ουκ εφυγον τον επι της γης παραιτησαμενοι χρηματιζοντα πολλω μαλλον ημεις οι τον απ ουρανων αποστρεφομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7093', '58', '12', '26', 'ου η φωνη την γην εσαλευσεν τοτε νυν δε επηγγελται λεγων ετι απαξ εγω σειω ου μονον την γην αλλα και τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('7094', '58', '12', '27', 'το δε ετι απαξ δηλοι των σαλευομενων την μεταθεσιν ως πεποιημενων ινα μεινη τα μη σαλευομενα');
INSERT INTO `bible_verses_tr` VALUES ('7095', '58', '12', '28', 'διο βασιλειαν ασαλευτον παραλαμβανοντες εχωμεν χαριν δι ης λατρευωμεν ευαρεστως τω θεω μετα αιδους και ευλαβειας');
INSERT INTO `bible_verses_tr` VALUES ('7096', '58', '12', '29', 'και γαρ ο θεος ημων πυρ καταναλισκον');
INSERT INTO `bible_verses_tr` VALUES ('7097', '58', '13', '1', 'η φιλαδελφια μενετω');
INSERT INTO `bible_verses_tr` VALUES ('7098', '58', '13', '2', 'της φιλοξενιας μη επιλανθανεσθε δια ταυτης γαρ ελαθον τινες ξενισαντες αγγελους');
INSERT INTO `bible_verses_tr` VALUES ('7099', '58', '13', '3', 'μιμνησκεσθε των δεσμιων ως συνδεδεμενοι των κακουχουμενων ως και αυτοι οντες εν σωματι');
INSERT INTO `bible_verses_tr` VALUES ('7100', '58', '13', '4', 'τιμιος ο γαμος εν πασιν και η κοιτη αμιαντος πορνους δε και μοιχους κρινει ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('7101', '58', '13', '5', 'αφιλαργυρος ο τροπος αρκουμενοι τοις παρουσιν αυτος γαρ ειρηκεν ου μη σε ανω ουδ ου μη σε εγκαταλιπω');
INSERT INTO `bible_verses_tr` VALUES ('7102', '58', '13', '6', 'ωστε θαρρουντας ημας λεγειν κυριος εμοι βοηθος και ου φοβηθησομαι τι ποιησει μοι ανθρωπος');
INSERT INTO `bible_verses_tr` VALUES ('7103', '58', '13', '7', 'μνημονευετε των ηγουμενων υμων οιτινες ελαλησαν υμιν τον λογον του θεου ων αναθεωρουντες την εκβασιν της αναστροφης μιμεισθε την πιστιν');
INSERT INTO `bible_verses_tr` VALUES ('7104', '58', '13', '8', 'ιησους χριστος χθες και σημερον ο αυτος και εις τους αιωνας');
INSERT INTO `bible_verses_tr` VALUES ('7105', '58', '13', '9', 'διδαχαις ποικιλαις και ξεναις μη περιφερεσθε καλον γαρ χαριτι βεβαιουσθαι την καρδιαν ου βρωμασιν εν οις ουκ ωφεληθησαν οι περιπατησαντες');
INSERT INTO `bible_verses_tr` VALUES ('7106', '58', '13', '10', 'εχομεν θυσιαστηριον εξ ου φαγειν ουκ εχουσιν εξουσιαν οι τη σκηνη λατρευοντες');
INSERT INTO `bible_verses_tr` VALUES ('7107', '58', '13', '11', 'ων γαρ εισφερεται ζωων το αιμα περι αμαρτιας εις τα αγια δια του αρχιερεως τουτων τα σωματα κατακαιεται εξω της παρεμβολης');
INSERT INTO `bible_verses_tr` VALUES ('7108', '58', '13', '12', 'διο και ιησους ινα αγιαση δια του ιδιου αιματος τον λαον εξω της πυλης επαθεν');
INSERT INTO `bible_verses_tr` VALUES ('7109', '58', '13', '13', 'τοινυν εξερχωμεθα προς αυτον εξω της παρεμβολης τον ονειδισμον αυτου φεροντες');
INSERT INTO `bible_verses_tr` VALUES ('7110', '58', '13', '14', 'ου γαρ εχομεν ωδε μενουσαν πολιν αλλα την μελλουσαν επιζητουμεν');
INSERT INTO `bible_verses_tr` VALUES ('7111', '58', '13', '15', 'δι αυτου ουν αναφερωμεν θυσιαν αινεσεως διαπαντος τω θεω τουτ εστιν καρπον χειλεων ομολογουντων τω ονοματι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7112', '58', '13', '16', 'της δε ευποιιας και κοινωνιας μη επιλανθανεσθε τοιαυταις γαρ θυσιαις ευαρεστειται ο θεος');
INSERT INTO `bible_verses_tr` VALUES ('7113', '58', '13', '17', 'πειθεσθε τοις ηγουμενοις υμων και υπεικετε αυτοι γαρ αγρυπνουσιν υπερ των ψυχων υμων ως λογον αποδωσοντες ινα μετα χαρας τουτο ποιωσιν και μη στεναζοντες αλυσιτελες γαρ υμιν τουτο');
INSERT INTO `bible_verses_tr` VALUES ('7114', '58', '13', '18', 'προσευχεσθε περι ημων πεποιθαμεν γαρ οτι καλην συνειδησιν εχομεν εν πασιν καλως θελοντες αναστρεφεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('7115', '58', '13', '19', 'περισσοτερως δε παρακαλω τουτο ποιησαι ινα ταχιον αποκατασταθω υμιν');
INSERT INTO `bible_verses_tr` VALUES ('7116', '58', '13', '20', 'ο δε θεος της ειρηνης ο αναγαγων εκ νεκρων τον ποιμενα των προβατων τον μεγαν εν αιματι διαθηκης αιωνιου τον κυριον ημων ιησουν');
INSERT INTO `bible_verses_tr` VALUES ('7117', '58', '13', '21', 'καταρτισαι υμας εν παντι εργω αγαθω εις το ποιησαι το θελημα αυτου ποιων εν υμιν το ευαρεστον ενωπιον αυτου δια ιησου χριστου ω η δοξα εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('7118', '58', '13', '22', 'παρακαλω δε υμας αδελφοι ανεχεσθε του λογου της παρακλησεως και γαρ δια βραχεων επεστειλα υμιν');
INSERT INTO `bible_verses_tr` VALUES ('7119', '58', '13', '23', 'γινωσκετε τον αδελφον τιμοθεον απολελυμενον μεθ ου εαν ταχιον ερχηται οψομαι υμας');
INSERT INTO `bible_verses_tr` VALUES ('7120', '58', '13', '24', 'ασπασασθε παντας τους ηγουμενους υμων και παντας τους αγιους ασπαζονται υμας οι απο της ιταλιας');
INSERT INTO `bible_verses_tr` VALUES ('7121', '58', '13', '25', 'η χαρις μετα παντων υμων αμην [προς εβραιους εγραφη απο της ιταλιας δια τιμοθεου]');
INSERT INTO `bible_verses_tr` VALUES ('7122', '59', '1', '1', 'ιακωβος θεου και κυριου ιησου χριστου δουλος ταις δωδεκα φυλαις ταις εν τη διασπορα χαιρειν');
INSERT INTO `bible_verses_tr` VALUES ('7123', '59', '1', '2', 'πασαν χαραν ηγησασθε αδελφοι μου οταν πειρασμοις περιπεσητε ποικιλοις');
INSERT INTO `bible_verses_tr` VALUES ('7124', '59', '1', '3', 'γινωσκοντες οτι το δοκιμιον υμων της πιστεως κατεργαζεται υπομονην');
INSERT INTO `bible_verses_tr` VALUES ('7125', '59', '1', '4', 'η δε υπομονη εργον τελειον εχετω ινα ητε τελειοι και ολοκληροι εν μηδενι λειπομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7126', '59', '1', '5', 'ει δε τις υμων λειπεται σοφιας αιτειτω παρα του διδοντος θεου πασιν απλως και μη ονειδιζοντος και δοθησεται αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7127', '59', '1', '6', 'αιτειτω δε εν πιστει μηδεν διακρινομενος ο γαρ διακρινομενος εοικεν κλυδωνι θαλασσης ανεμιζομενω και ριπιζομενω');
INSERT INTO `bible_verses_tr` VALUES ('7128', '59', '1', '7', 'μη γαρ οιεσθω ο ανθρωπος εκεινος οτι ληψεται τι παρα του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('7129', '59', '1', '8', 'ανηρ διψυχος ακαταστατος εν πασαις ταις οδοις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7130', '59', '1', '9', 'καυχασθω δε ο αδελφος ο ταπεινος εν τω υψει αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7131', '59', '1', '10', 'ο δε πλουσιος εν τη ταπεινωσει αυτου οτι ως ανθος χορτου παρελευσεται');
INSERT INTO `bible_verses_tr` VALUES ('7132', '59', '1', '11', 'ανετειλεν γαρ ο ηλιος συν τω καυσωνι και εξηρανεν τον χορτον και το ανθος αυτου εξεπεσεν και η ευπρεπεια του προσωπου αυτου απωλετο ουτως και ο πλουσιος εν ταις πορειαις αυτου μαρανθησεται');
INSERT INTO `bible_verses_tr` VALUES ('7133', '59', '1', '12', 'μακαριος ανηρ ος υπομενει πειρασμον οτι δοκιμος γενομενος ληψεται τον στεφανον της ζωης ον επηγγειλατο ο κυριος τοις αγαπωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('7134', '59', '1', '13', 'μηδεις πειραζομενος λεγετω οτι απο του θεου πειραζομαι ο γαρ θεος απειραστος εστιν κακων πειραζει δε αυτος ουδενα');
INSERT INTO `bible_verses_tr` VALUES ('7135', '59', '1', '14', 'εκαστος δε πειραζεται υπο της ιδιας επιθυμιας εξελκομενος και δελεαζομενος');
INSERT INTO `bible_verses_tr` VALUES ('7136', '59', '1', '15', 'ειτα η επιθυμια συλλαβουσα τικτει αμαρτιαν η δε αμαρτια αποτελεσθεισα αποκυει θανατον');
INSERT INTO `bible_verses_tr` VALUES ('7137', '59', '1', '16', 'μη πλανασθε αδελφοι μου αγαπητοι');
INSERT INTO `bible_verses_tr` VALUES ('7138', '59', '1', '17', 'πασα δοσις αγαθη και παν δωρημα τελειον ανωθεν εστιν καταβαινον απο του πατρος των φωτων παρ ω ουκ ενι παραλλαγη η τροπης αποσκιασμα');
INSERT INTO `bible_verses_tr` VALUES ('7139', '59', '1', '18', 'βουληθεις απεκυησεν ημας λογω αληθειας εις το ειναι ημας απαρχην τινα των αυτου κτισματων');
INSERT INTO `bible_verses_tr` VALUES ('7140', '59', '1', '19', 'ωστε αδελφοι μου αγαπητοι εστω πας ανθρωπος ταχυς εις το ακουσαι βραδυς εις το λαλησαι βραδυς εις οργην');
INSERT INTO `bible_verses_tr` VALUES ('7141', '59', '1', '20', 'οργη γαρ ανδρος δικαιοσυνην θεου ου κατεργαζεται');
INSERT INTO `bible_verses_tr` VALUES ('7142', '59', '1', '21', 'διο αποθεμενοι πασαν ρυπαριαν και περισσειαν κακιας εν πραυτητι δεξασθε τον εμφυτον λογον τον δυναμενον σωσαι τας ψυχας υμων');
INSERT INTO `bible_verses_tr` VALUES ('7143', '59', '1', '22', 'γινεσθε δε ποιηται λογου και μη μονον ακροαται παραλογιζομενοι εαυτους');
INSERT INTO `bible_verses_tr` VALUES ('7144', '59', '1', '23', 'οτι ει τις ακροατης λογου εστιν και ου ποιητης ουτος εοικεν ανδρι κατανοουντι το προσωπον της γενεσεως αυτου εν εσοπτρω');
INSERT INTO `bible_verses_tr` VALUES ('7145', '59', '1', '24', 'κατενοησεν γαρ εαυτον και απεληλυθεν και ευθεως επελαθετο οποιος ην');
INSERT INTO `bible_verses_tr` VALUES ('7146', '59', '1', '25', 'ο δε παρακυψας εις νομον τελειον τον της ελευθεριας και παραμεινας ουτος ουκ ακροατης επιλησμονης γενομενος αλλα ποιητης εργου ουτος μακαριος εν τη ποιησει αυτου εσται');
INSERT INTO `bible_verses_tr` VALUES ('7147', '59', '1', '26', 'ει τις δοκει θρησκος ειναι εν υμιν μη χαλιναγωγων γλωσσαν αυτου αλλ απατων καρδιαν αυτου τουτου ματαιος η θρησκεια');
INSERT INTO `bible_verses_tr` VALUES ('7148', '59', '1', '27', 'θρησκεια καθαρα και αμιαντος παρα τω θεω και πατρι αυτη εστιν επισκεπτεσθαι ορφανους και χηρας εν τη θλιψει αυτων ασπιλον εαυτον τηρειν απο του κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('7149', '59', '2', '1', 'αδελφοι μου μη εν προσωποληψιαις εχετε την πιστιν του κυριου ημων ιησου χριστου της δοξης');
INSERT INTO `bible_verses_tr` VALUES ('7150', '59', '2', '2', 'εαν γαρ εισελθη εις την συναγωγην υμων ανηρ χρυσοδακτυλιος εν εσθητι λαμπρα εισελθη δε και πτωχος εν ρυπαρα εσθητι');
INSERT INTO `bible_verses_tr` VALUES ('7151', '59', '2', '3', 'και επιβλεψητε επι τον φορουντα την εσθητα την λαμπραν και ειπητε αυτω συ καθου ωδε καλως και τω πτωχω ειπητε συ στηθι εκει η καθου ωδε υπο το υποποδιον μου');
INSERT INTO `bible_verses_tr` VALUES ('7152', '59', '2', '4', 'και ου διεκριθητε εν εαυτοις και εγενεσθε κριται διαλογισμων πονηρων');
INSERT INTO `bible_verses_tr` VALUES ('7153', '59', '2', '5', 'ακουσατε αδελφοι μου αγαπητοι ουχ ο θεος εξελεξατο τους πτωχους του κοσμου τουτου πλουσιους εν πιστει και κληρονομους της βασιλειας ης επηγγειλατο τοις αγαπωσιν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('7154', '59', '2', '6', 'υμεις δε ητιμασατε τον πτωχον ουχ οι πλουσιοι καταδυναστευουσιν υμων και αυτοι ελκουσιν υμας εις κριτηρια');
INSERT INTO `bible_verses_tr` VALUES ('7155', '59', '2', '7', 'ουκ αυτοι βλασφημουσιν το καλον ονομα το επικληθεν εφ υμας');
INSERT INTO `bible_verses_tr` VALUES ('7156', '59', '2', '8', 'ει μεντοι νομον τελειτε βασιλικον κατα την γραφην αγαπησεις τον πλησιον σου ως σεαυτον καλως ποιειτε');
INSERT INTO `bible_verses_tr` VALUES ('7157', '59', '2', '9', 'ει δε προσωποληπτειτε αμαρτιαν εργαζεσθε ελεγχομενοι υπο του νομου ως παραβαται');
INSERT INTO `bible_verses_tr` VALUES ('7158', '59', '2', '10', 'οστις γαρ ολον τον νομον τηρησει πταισει δε εν ενι γεγονεν παντων ενοχος');
INSERT INTO `bible_verses_tr` VALUES ('7159', '59', '2', '11', 'ο γαρ ειπων μη μοιχευσης ειπεν και μη φονευσης ει δε ου μοιχευσεις φονευσεις δε γεγονας παραβατης νομου');
INSERT INTO `bible_verses_tr` VALUES ('7160', '59', '2', '12', 'ουτως λαλειτε και ουτως ποιειτε ως δια νομου ελευθεριας μελλοντες κρινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('7161', '59', '2', '13', 'η γαρ κρισις ανιλεως τω μη ποιησαντι ελεος και κατακαυχαται ελεος κρισεως');
INSERT INTO `bible_verses_tr` VALUES ('7162', '59', '2', '14', 'τι το οφελος αδελφοι μου εαν πιστιν λεγη τις εχειν εργα δε μη εχη μη δυναται η πιστις σωσαι αυτον');
INSERT INTO `bible_verses_tr` VALUES ('7163', '59', '2', '15', 'εαν δε αδελφος η αδελφη γυμνοι υπαρχωσιν και λειπομενοι ωσιν της εφημερου τροφης');
INSERT INTO `bible_verses_tr` VALUES ('7164', '59', '2', '16', 'ειπη δε τις αυτοις εξ υμων υπαγετε εν ειρηνη θερμαινεσθε και χορταζεσθε μη δωτε δε αυτοις τα επιτηδεια του σωματος τι το οφελος');
INSERT INTO `bible_verses_tr` VALUES ('7165', '59', '2', '17', 'ουτως και η πιστις εαν μη εργα εχη νεκρα εστιν καθ εαυτην');
INSERT INTO `bible_verses_tr` VALUES ('7166', '59', '2', '18', 'αλλ ερει τις συ πιστιν εχεις καγω εργα εχω δειξον μοι την πιστιν σου  {VAR1: εκ } {VAR2: χωρις } των εργων σου καγω δειξω σοι εκ των εργων μου την πιστιν μου');
INSERT INTO `bible_verses_tr` VALUES ('7167', '59', '2', '19', 'συ πιστευεις οτι ο θεος εις εστιν καλως ποιεις και τα δαιμονια πιστευουσιν και φρισσουσιν');
INSERT INTO `bible_verses_tr` VALUES ('7168', '59', '2', '20', 'θελεις δε γνωναι ω ανθρωπε κενε οτι η πιστις χωρις των εργων νεκρα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7169', '59', '2', '21', 'αβρααμ ο πατηρ ημων ουκ εξ εργων εδικαιωθη ανενεγκας ισαακ τον υιον αυτου επι το θυσιαστηριον');
INSERT INTO `bible_verses_tr` VALUES ('7170', '59', '2', '22', 'βλεπεις οτι η πιστις συνηργει τοις εργοις αυτου και εκ των εργων η πιστις ετελειωθη');
INSERT INTO `bible_verses_tr` VALUES ('7171', '59', '2', '23', 'και επληρωθη η γραφη η λεγουσα επιστευσεν δε αβρααμ τω θεω και ελογισθη αυτω εις δικαιοσυνην και φιλος θεου εκληθη');
INSERT INTO `bible_verses_tr` VALUES ('7172', '59', '2', '24', 'ορατε τοινυν οτι εξ εργων δικαιουται ανθρωπος και ουκ εκ πιστεως μονον');
INSERT INTO `bible_verses_tr` VALUES ('7173', '59', '2', '25', 'ομοιως δε και ρααβ η πορνη ουκ εξ εργων εδικαιωθη υποδεξαμενη τους αγγελους και ετερα οδω εκβαλουσα');
INSERT INTO `bible_verses_tr` VALUES ('7174', '59', '2', '26', 'ωσπερ γαρ το σωμα χωρις πνευματος νεκρον εστιν ουτως και η πιστις χωρις των εργων νεκρα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7175', '59', '3', '1', 'μη πολλοι διδασκαλοι γινεσθε αδελφοι μου ειδοτες οτι μειζον κριμα ληψομεθα');
INSERT INTO `bible_verses_tr` VALUES ('7176', '59', '3', '2', 'πολλα γαρ πταιομεν απαντες ει τις εν λογω ου πταιει ουτος τελειος ανηρ δυνατος χαλιναγωγησαι και ολον το σωμα');
INSERT INTO `bible_verses_tr` VALUES ('7177', '59', '3', '3', 'ιδου των ιππων τους χαλινους εις τα στοματα βαλλομεν προς το πειθεσθαι αυτους ημιν και ολον το σωμα αυτων μεταγομεν');
INSERT INTO `bible_verses_tr` VALUES ('7178', '59', '3', '4', 'ιδου και τα πλοια τηλικαυτα οντα και υπο σκληρων ανεμων ελαυνομενα μεταγεται υπο ελαχιστου πηδαλιου οπου αν η ορμη του ευθυνοντος βουληται');
INSERT INTO `bible_verses_tr` VALUES ('7179', '59', '3', '5', 'ουτως και η γλωσσα μικρον μελος εστιν και μεγαλαυχει ιδου ολιγον πυρ ηλικην υλην αναπτει');
INSERT INTO `bible_verses_tr` VALUES ('7180', '59', '3', '6', 'και η γλωσσα πυρ ο κοσμος της αδικιας ουτως η γλωσσα καθισταται εν τοις μελεσιν ημων η σπιλουσα ολον το σωμα και φλογιζουσα τον τροχον της γενεσεως και φλογιζομενη υπο της γεεννης');
INSERT INTO `bible_verses_tr` VALUES ('7181', '59', '3', '7', 'πασα γαρ φυσις θηριων τε και πετεινων ερπετων τε και εναλιων δαμαζεται και δεδαμασται τη φυσει τη ανθρωπινη');
INSERT INTO `bible_verses_tr` VALUES ('7182', '59', '3', '8', 'την δε γλωσσαν ουδεις δυναται ανθρωπων δαμασαι ακατασχετον κακον μεστη ιου θανατηφορου');
INSERT INTO `bible_verses_tr` VALUES ('7183', '59', '3', '9', 'εν αυτη ευλογουμεν τον θεον και πατερα και εν αυτη καταρωμεθα τους ανθρωπους τους καθ ομοιωσιν θεου γεγονοτας');
INSERT INTO `bible_verses_tr` VALUES ('7184', '59', '3', '10', 'εκ του αυτου στοματος εξερχεται ευλογια και καταρα ου χρη αδελφοι μου ταυτα ουτως γινεσθαι');
INSERT INTO `bible_verses_tr` VALUES ('7185', '59', '3', '11', 'μητι η πηγη εκ της αυτης οπης βρυει το γλυκυ και το πικρον');
INSERT INTO `bible_verses_tr` VALUES ('7186', '59', '3', '12', 'μη δυναται αδελφοι μου συκη ελαιας ποιησαι η αμπελος συκα ουτως ουδεμια πηγη αλυκον και γλυκυ ποιησαι υδωρ');
INSERT INTO `bible_verses_tr` VALUES ('7187', '59', '3', '13', 'τις σοφος και επιστημων εν υμιν δειξατω εκ της καλης αναστροφης τα εργα αυτου εν πραυτητι σοφιας');
INSERT INTO `bible_verses_tr` VALUES ('7188', '59', '3', '14', 'ει δε ζηλον πικρον εχετε και εριθειαν εν τη καρδια υμων μη κατακαυχασθε και ψευδεσθε κατα της αληθειας');
INSERT INTO `bible_verses_tr` VALUES ('7189', '59', '3', '15', 'ουκ εστιν αυτη η σοφια ανωθεν κατερχομενη αλλ επιγειος ψυχικη δαιμονιωδης');
INSERT INTO `bible_verses_tr` VALUES ('7190', '59', '3', '16', 'οπου γαρ ζηλος και εριθεια εκει ακαταστασια και παν φαυλον πραγμα');
INSERT INTO `bible_verses_tr` VALUES ('7191', '59', '3', '17', 'η δε ανωθεν σοφια πρωτον μεν αγνη εστιν επειτα ειρηνικη επιεικης ευπειθης μεστη ελεους και καρπων αγαθων αδιακριτος και ανυποκριτος');
INSERT INTO `bible_verses_tr` VALUES ('7192', '59', '3', '18', 'καρπος δε της δικαιοσυνης εν ειρηνη σπειρεται τοις ποιουσιν ειρηνην');
INSERT INTO `bible_verses_tr` VALUES ('7193', '59', '4', '1', 'ποθεν πολεμοι και μαχαι εν υμιν ουκ εντευθεν εκ των ηδονων υμων των στρατευομενων εν τοις μελεσιν υμων');
INSERT INTO `bible_verses_tr` VALUES ('7194', '59', '4', '2', 'επιθυμειτε και ουκ εχετε φονευετε και ζηλουτε και ου δυνασθε επιτυχειν μαχεσθε και πολεμειτε ουκ εχετε δε δια το μη αιτεισθαι υμας');
INSERT INTO `bible_verses_tr` VALUES ('7195', '59', '4', '3', 'αιτειτε και ου λαμβανετε διοτι κακως αιτεισθε ινα εν ταις ηδοναις υμων δαπανησητε');
INSERT INTO `bible_verses_tr` VALUES ('7196', '59', '4', '4', 'μοιχοι και μοιχαλιδες ουκ οιδατε οτι η φιλια του κοσμου εχθρα του θεου εστιν ος αν ουν βουληθη φιλος ειναι του κοσμου εχθρος του θεου καθισταται');
INSERT INTO `bible_verses_tr` VALUES ('7197', '59', '4', '5', 'η δοκειτε οτι κενως η γραφη λεγει προς φθονον επιποθει το πνευμα ο κατωκησεν εν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7198', '59', '4', '6', 'μειζονα δε διδωσιν χαριν διο λεγει ο θεος υπερηφανοις αντιτασσεται ταπεινοις δε διδωσιν χαριν');
INSERT INTO `bible_verses_tr` VALUES ('7199', '59', '4', '7', 'υποταγητε ουν τω θεω αντιστητε τω διαβολω και φευξεται αφ υμων');
INSERT INTO `bible_verses_tr` VALUES ('7200', '59', '4', '8', 'εγγισατε τω θεω και εγγιει υμιν καθαρισατε χειρας αμαρτωλοι και αγνισατε καρδιας διψυχοι');
INSERT INTO `bible_verses_tr` VALUES ('7201', '59', '4', '9', 'ταλαιπωρησατε και πενθησατε και κλαυσατε ο γελως υμων εις πενθος μεταστραφητω και η χαρα εις κατηφειαν');
INSERT INTO `bible_verses_tr` VALUES ('7202', '59', '4', '10', 'ταπεινωθητε ενωπιον του κυριου και υψωσει υμας');
INSERT INTO `bible_verses_tr` VALUES ('7203', '59', '4', '11', 'μη καταλαλειτε αλληλων αδελφοι ο καταλαλων αδελφου και κρινων τον αδελφον αυτου καταλαλει νομου και κρινει νομον ει δε νομον κρινεις ουκ ει ποιητης νομου αλλα κριτης');
INSERT INTO `bible_verses_tr` VALUES ('7204', '59', '4', '12', 'εις εστιν ο νομοθετης ο δυναμενος σωσαι και απολεσαι συ τις ει ος κρινεις τον ετερον');
INSERT INTO `bible_verses_tr` VALUES ('7205', '59', '4', '13', 'αγε νυν οι λεγοντες σημερον  {VAR1: και } {VAR2: η } αυριον πορευσωμεθα εις τηνδε την πολιν και  {VAR1: ποιησωμεν } {VAR2: ποιησομεν } εκει ενιαυτον ενα και  {VAR1: εμπορευσωμεθα και κερδησωμεν } {VAR2: εμπορευσομεθα και κερδησομεν }');
INSERT INTO `bible_verses_tr` VALUES ('7206', '59', '4', '14', 'οιτινες ουκ επιστασθε το της αυριον ποια γαρ η ζωη υμων ατμις γαρ εστιν η προς ολιγον φαινομενη επειτα δε αφανιζομενη');
INSERT INTO `bible_verses_tr` VALUES ('7207', '59', '4', '15', 'αντι του λεγειν υμας εαν ο κυριος θεληση και  {VAR1: ζησωμεν και ποιησωμεν } {VAR2: ζησομεν και ποιησομεν } τουτο η εκεινο');
INSERT INTO `bible_verses_tr` VALUES ('7208', '59', '4', '16', 'νυν δε καυχασθε εν ταις αλαζονειαις υμων πασα καυχησις τοιαυτη πονηρα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7209', '59', '4', '17', 'ειδοτι ουν καλον ποιειν και μη ποιουντι αμαρτια αυτω εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7210', '59', '5', '1', 'αγε νυν οι πλουσιοι κλαυσατε ολολυζοντες επι ταις ταλαιπωριαις υμων ταις επερχομεναις');
INSERT INTO `bible_verses_tr` VALUES ('7211', '59', '5', '2', 'ο πλουτος υμων σεσηπεν και τα ιματια υμων σητοβρωτα γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('7212', '59', '5', '3', 'ο χρυσος υμων και ο αργυρος κατιωται και ο ιος αυτων εις μαρτυριον υμιν εσται και φαγεται τας σαρκας υμων ως πυρ εθησαυρισατε εν εσχαταις ημεραις');
INSERT INTO `bible_verses_tr` VALUES ('7213', '59', '5', '4', 'ιδου ο μισθος των εργατων των αμησαντων τας χωρας υμων ο απεστερημενος αφ υμων κραζει και αι βοαι των θερισαντων εις τα ωτα κυριου σαβαωθ εισεληλυθασιν');
INSERT INTO `bible_verses_tr` VALUES ('7214', '59', '5', '5', 'ετρυφησατε επι της γης και εσπαταλησατε εθρεψατε τας καρδιας υμων ως εν ημερα σφαγης');
INSERT INTO `bible_verses_tr` VALUES ('7215', '59', '5', '6', 'κατεδικασατε εφονευσατε τον δικαιον ουκ αντιτασσεται υμιν');
INSERT INTO `bible_verses_tr` VALUES ('7216', '59', '5', '7', 'μακροθυμησατε ουν αδελφοι εως της παρουσιας του κυριου ιδου ο γεωργος εκδεχεται τον τιμιον καρπον της γης μακροθυμων επ αυτω εως αν λαβη υετον πρωιμον και οψιμον');
INSERT INTO `bible_verses_tr` VALUES ('7217', '59', '5', '8', 'μακροθυμησατε και υμεις στηριξατε τας καρδιας υμων οτι η παρουσια του κυριου ηγγικεν');
INSERT INTO `bible_verses_tr` VALUES ('7218', '59', '5', '9', 'μη στεναζετε κατ αλληλων αδελφοι ινα μη κατακριθητε ιδου  {VAR2: ο } κριτης προ των θυρων εστηκεν');
INSERT INTO `bible_verses_tr` VALUES ('7219', '59', '5', '10', 'υποδειγμα λαβετε της κακοπαθειας αδελφοι μου και της μακροθυμιας τους προφητας οι ελαλησαν τω ονοματι κυριου');
INSERT INTO `bible_verses_tr` VALUES ('7220', '59', '5', '11', 'ιδου μακαριζομεν τους υπομενοντας την υπομονην ιωβ ηκουσατε και το τελος κυριου ειδετε οτι πολυσπλαγχνος εστιν ο κυριος και οικτιρμων');
INSERT INTO `bible_verses_tr` VALUES ('7221', '59', '5', '12', 'προ παντων δε αδελφοι μου μη ομνυετε μητε τον ουρανον μητε την γην μητε αλλον τινα ορκον ητω δε υμων το ναι ναι και το ου ου ινα μη  {VAR1: εις υποκρισιν } {VAR2: υπο κρισιν } πεσητε');
INSERT INTO `bible_verses_tr` VALUES ('7222', '59', '5', '13', 'κακοπαθει τις εν υμιν προσευχεσθω ευθυμει τις ψαλλετω');
INSERT INTO `bible_verses_tr` VALUES ('7223', '59', '5', '14', 'ασθενει τις εν υμιν προσκαλεσασθω τους πρεσβυτερους της εκκλησιας και προσευξασθωσαν επ αυτον αλειψαντες αυτον ελαιω εν τω ονοματι του κυριου');
INSERT INTO `bible_verses_tr` VALUES ('7224', '59', '5', '15', 'και η ευχη της πιστεως σωσει τον καμνοντα και εγερει αυτον ο κυριος καν αμαρτιας η πεποιηκως αφεθησεται αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7225', '59', '5', '16', 'εξομολογεισθε αλληλοις τα παραπτωματα και ευχεσθε υπερ αλληλων οπως ιαθητε πολυ ισχυει δεησις δικαιου ενεργουμενη');
INSERT INTO `bible_verses_tr` VALUES ('7226', '59', '5', '17', 'ηλιας ανθρωπος ην ομοιοπαθης ημιν και προσευχη προσηυξατο του μη βρεξαι και ουκ εβρεξεν επι της γης ενιαυτους τρεις και μηνας εξ');
INSERT INTO `bible_verses_tr` VALUES ('7227', '59', '5', '18', 'και παλιν προσηυξατο και ο ουρανος υετον εδωκεν και η γη εβλαστησεν τον καρπον αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7228', '59', '5', '19', 'αδελφοι εαν τις εν υμιν πλανηθη απο της αληθειας και επιστρεψη τις αυτον');
INSERT INTO `bible_verses_tr` VALUES ('7229', '59', '5', '20', 'γινωσκετω οτι ο επιστρεψας αμαρτωλον εκ πλανης οδου αυτου σωσει ψυχην εκ θανατου και καλυψει πληθος αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('7230', '60', '1', '1', 'πετρος αποστολος ιησου χριστου εκλεκτοις παρεπιδημοις διασπορας ποντου γαλατιας καππαδοκιας ασιας και βιθυνιας');
INSERT INTO `bible_verses_tr` VALUES ('7231', '60', '1', '2', 'κατα προγνωσιν θεου πατρος εν αγιασμω πνευματος εις υπακοην και ραντισμον αιματος ιησου χριστου χαρις υμιν και ειρηνη πληθυνθειη');
INSERT INTO `bible_verses_tr` VALUES ('7232', '60', '1', '3', 'ευλογητος ο θεος και πατηρ του κυριου ημων ιησου χριστου ο κατα το πολυ αυτου ελεος αναγεννησας ημας εις ελπιδα ζωσαν δι αναστασεως ιησου χριστου εκ νεκρων');
INSERT INTO `bible_verses_tr` VALUES ('7233', '60', '1', '4', 'εις κληρονομιαν αφθαρτον και αμιαντον και αμαραντον τετηρημενην εν ουρανοις εις  {VAR1: ημας } {VAR2: υμας }');
INSERT INTO `bible_verses_tr` VALUES ('7234', '60', '1', '5', 'τους εν δυναμει θεου φρουρουμενους δια πιστεως εις σωτηριαν ετοιμην αποκαλυφθηναι εν καιρω εσχατω');
INSERT INTO `bible_verses_tr` VALUES ('7235', '60', '1', '6', 'εν ω αγαλλιασθε ολιγον αρτι ει δεον εστιν λυπηθεντες εν ποικιλοις πειρασμοις');
INSERT INTO `bible_verses_tr` VALUES ('7236', '60', '1', '7', 'ινα το δοκιμιον υμων της πιστεως πολυ τιμιωτερον χρυσιου του απολλυμενου δια πυρος δε δοκιμαζομενου ευρεθη εις επαινον και τιμην και δοξαν εν αποκαλυψει ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7237', '60', '1', '8', 'ον ουκ  {VAR1: ειδοτες } {VAR2: ιδοντες } αγαπατε εις ον αρτι μη ορωντες πιστευοντες δε αγαλλιασθε χαρα ανεκλαλητω και δεδοξασμενη');
INSERT INTO `bible_verses_tr` VALUES ('7238', '60', '1', '9', 'κομιζομενοι το τελος της πιστεως υμων σωτηριαν ψυχων');
INSERT INTO `bible_verses_tr` VALUES ('7239', '60', '1', '10', 'περι ης σωτηριας εξεζητησαν και εξηρευνησαν προφηται οι περι της εις υμας χαριτος προφητευσαντες');
INSERT INTO `bible_verses_tr` VALUES ('7240', '60', '1', '11', 'ερευνωντες εις τινα η ποιον καιρον εδηλου το εν αυτοις πνευμα χριστου προμαρτυρομενον τα εις χριστον παθηματα και τας μετα ταυτα δοξας');
INSERT INTO `bible_verses_tr` VALUES ('7241', '60', '1', '12', 'οις απεκαλυφθη οτι ουχ εαυτοις ημιν δε διηκονουν αυτα α νυν ανηγγελη υμιν δια των ευαγγελισαμενων υμας εν πνευματι αγιω αποσταλεντι απ ουρανου εις α επιθυμουσιν αγγελοι παρακυψαι');
INSERT INTO `bible_verses_tr` VALUES ('7242', '60', '1', '13', 'διο αναζωσαμενοι τας οσφυας της διανοιας υμων νηφοντες τελειως ελπισατε επι την φερομενην υμιν χαριν εν αποκαλυψει ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7243', '60', '1', '14', 'ως τεκνα υπακοης μη συσχηματιζομενοι ταις προτερον εν τη αγνοια υμων επιθυμιαις');
INSERT INTO `bible_verses_tr` VALUES ('7244', '60', '1', '15', 'αλλα κατα τον καλεσαντα υμας αγιον και αυτοι αγιοι εν παση αναστροφη γενηθητε');
INSERT INTO `bible_verses_tr` VALUES ('7245', '60', '1', '16', 'διοτι γεγραπται αγιοι γενεσθε οτι εγω αγιος ειμι');
INSERT INTO `bible_verses_tr` VALUES ('7246', '60', '1', '17', 'και ει πατερα επικαλεισθε τον απροσωποληπτως κρινοντα κατα το εκαστου εργον εν φοβω τον της παροικιας υμων χρονον αναστραφητε');
INSERT INTO `bible_verses_tr` VALUES ('7247', '60', '1', '18', 'ειδοτες οτι ου φθαρτοις αργυριω η χρυσιω ελυτρωθητε εκ της ματαιας υμων αναστροφης πατροπαραδοτου');
INSERT INTO `bible_verses_tr` VALUES ('7248', '60', '1', '19', 'αλλα τιμιω αιματι ως αμνου αμωμου και ασπιλου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7249', '60', '1', '20', 'προεγνωσμενου μεν προ καταβολης κοσμου φανερωθεντος δε επ εσχατων των χρονων δι υμας');
INSERT INTO `bible_verses_tr` VALUES ('7250', '60', '1', '21', 'τους δι αυτου πιστευοντας εις θεον τον εγειραντα αυτον εκ νεκρων και δοξαν αυτω δοντα ωστε την πιστιν υμων και ελπιδα ειναι εις θεον');
INSERT INTO `bible_verses_tr` VALUES ('7251', '60', '1', '22', 'τας ψυχας υμων ηγνικοτες εν τη υπακοη της αληθειας δια πνευματος εις φιλαδελφιαν ανυποκριτον εκ καθαρας καρδιας αλληλους αγαπησατε εκτενως');
INSERT INTO `bible_verses_tr` VALUES ('7252', '60', '1', '23', 'αναγεγεννημενοι ουκ εκ σπορας φθαρτης αλλα αφθαρτου δια λογου ζωντος θεου και μενοντος εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('7253', '60', '1', '24', 'διοτι πασα σαρξ ως χορτος και πασα δοξα ανθρωπου ως ανθος χορτου εξηρανθη ο χορτος και το ανθος αυτου εξεπεσεν');
INSERT INTO `bible_verses_tr` VALUES ('7254', '60', '1', '25', 'το δε ρημα κυριου μενει εις τον αιωνα τουτο δε εστιν το ρημα το ευαγγελισθεν εις υμας');
INSERT INTO `bible_verses_tr` VALUES ('7255', '60', '2', '1', 'αποθεμενοι ουν πασαν κακιαν και παντα δολον και υποκρισεις και φθονους και πασας καταλαλιας');
INSERT INTO `bible_verses_tr` VALUES ('7256', '60', '2', '2', 'ως αρτιγεννητα βρεφη το λογικον αδολον γαλα επιποθησατε ινα εν αυτω αυξηθητε');
INSERT INTO `bible_verses_tr` VALUES ('7257', '60', '2', '3', 'ειπερ εγευσασθε οτι χρηστος ο κυριος');
INSERT INTO `bible_verses_tr` VALUES ('7258', '60', '2', '4', 'προς ον προσερχομενοι λιθον ζωντα υπο ανθρωπων μεν αποδεδοκιμασμενον παρα δε θεω εκλεκτον εντιμον');
INSERT INTO `bible_verses_tr` VALUES ('7259', '60', '2', '5', 'και αυτοι ως λιθοι ζωντες οικοδομεισθε οικος πνευματικος ιερατευμα αγιον ανενεγκαι πνευματικας θυσιας ευπροσδεκτους τω θεω δια ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7260', '60', '2', '6', 'διο και περιεχει εν τη γραφη ιδου τιθημι εν σιων λιθον ακρογωνιαιον εκλεκτον εντιμον και ο πιστευων επ αυτω ου μη καταισχυνθη');
INSERT INTO `bible_verses_tr` VALUES ('7261', '60', '2', '7', 'υμιν ουν η τιμη τοις πιστευουσιν απειθουσιν δε λιθον ον απεδοκιμασαν οι οικοδομουντες ουτος εγενηθη εις κεφαλην γωνιας');
INSERT INTO `bible_verses_tr` VALUES ('7262', '60', '2', '8', 'και λιθος προσκομματος και πετρα σκανδαλου οι προσκοπτουσιν τω λογω απειθουντες εις ο και ετεθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7263', '60', '2', '9', 'υμεις δε γενος εκλεκτον βασιλειον ιερατευμα εθνος αγιον λαος εις περιποιησιν οπως τας αρετας εξαγγειλητε του εκ σκοτους υμας καλεσαντος εις το θαυμαστον αυτου φως');
INSERT INTO `bible_verses_tr` VALUES ('7264', '60', '2', '10', 'οι ποτε ου λαος νυν δε λαος θεου οι ουκ ηλεημενοι νυν δε ελεηθεντες');
INSERT INTO `bible_verses_tr` VALUES ('7265', '60', '2', '11', 'αγαπητοι παρακαλω ως παροικους και παρεπιδημους απεχεσθαι των σαρκικων επιθυμιων αιτινες στρατευονται κατα της ψυχης');
INSERT INTO `bible_verses_tr` VALUES ('7266', '60', '2', '12', 'την αναστροφην υμων εν τοις εθνεσιν εχοντες καλην ινα εν ω καταλαλουσιν υμων ως κακοποιων εκ των καλων εργων εποπτευσαντες δοξασωσιν τον θεον εν ημερα επισκοπης');
INSERT INTO `bible_verses_tr` VALUES ('7267', '60', '2', '13', 'υποταγητε ουν παση ανθρωπινη κτισει δια τον κυριον ειτε βασιλει ως υπερεχοντι');
INSERT INTO `bible_verses_tr` VALUES ('7268', '60', '2', '14', 'ειτε ηγεμοσιν ως δι αυτου πεμπομενοις εις εκδικησιν μεν κακοποιων επαινον δε αγαθοποιων');
INSERT INTO `bible_verses_tr` VALUES ('7269', '60', '2', '15', 'οτι ουτως εστιν το θελημα του θεου αγαθοποιουντας φιμουν την των αφρονων ανθρωπων αγνωσιαν');
INSERT INTO `bible_verses_tr` VALUES ('7270', '60', '2', '16', 'ως ελευθεροι και μη ως επικαλυμμα εχοντες της κακιας την ελευθεριαν αλλ ως δουλοι θεου');
INSERT INTO `bible_verses_tr` VALUES ('7271', '60', '2', '17', 'παντας τιμησατε την αδελφοτητα αγαπατε τον θεον φοβεισθε τον βασιλεα τιματε');
INSERT INTO `bible_verses_tr` VALUES ('7272', '60', '2', '18', 'οι οικεται υποτασσομενοι εν παντι φοβω τοις δεσποταις ου μονον τοις αγαθοις και επιεικεσιν αλλα και τοις σκολιοις');
INSERT INTO `bible_verses_tr` VALUES ('7273', '60', '2', '19', 'τουτο γαρ χαρις ει δια συνειδησιν θεου υποφερει τις λυπας πασχων αδικως');
INSERT INTO `bible_verses_tr` VALUES ('7274', '60', '2', '20', 'ποιον γαρ κλεος ει αμαρτανοντες και κολαφιζομενοι υπομενειτε αλλ ει αγαθοποιουντες και πασχοντες υπομενειτε τουτο χαρις παρα θεω');
INSERT INTO `bible_verses_tr` VALUES ('7275', '60', '2', '21', 'εις τουτο γαρ εκληθητε οτι και χριστος επαθεν υπερ ημων ημιν υπολιμπανων υπογραμμον ινα επακολουθησητε τοις ιχνεσιν αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7276', '60', '2', '22', 'ος αμαρτιαν ουκ εποιησεν ουδε ευρεθη δολος εν τω στοματι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7277', '60', '2', '23', 'ος λοιδορουμενος ουκ αντελοιδορει πασχων ουκ ηπειλει παρεδιδου δε τω κρινοντι δικαιως');
INSERT INTO `bible_verses_tr` VALUES ('7278', '60', '2', '24', 'ος τας αμαρτιας ημων αυτος ανηνεγκεν εν τω σωματι αυτου επι το ξυλον ινα ταις αμαρτιαις απογενομενοι τη δικαιοσυνη ζησωμεν ου τω μωλωπι αυτου ιαθητε');
INSERT INTO `bible_verses_tr` VALUES ('7279', '60', '2', '25', 'ητε γαρ ως προβατα πλανωμενα αλλ επεστραφητε νυν επι τον ποιμενα και επισκοπον των ψυχων υμων');
INSERT INTO `bible_verses_tr` VALUES ('7280', '60', '3', '1', 'ομοιως αι γυναικες υποτασσομεναι τοις ιδιοις ανδρασιν ινα και ει τινες απειθουσιν τω λογω δια της των γυναικων αναστροφης ανευ λογου κερδηθησωνται');
INSERT INTO `bible_verses_tr` VALUES ('7281', '60', '3', '2', 'εποπτευσαντες την εν φοβω αγνην αναστροφην υμων');
INSERT INTO `bible_verses_tr` VALUES ('7282', '60', '3', '3', 'ων εστω ουχ ο εξωθεν εμπλοκης τριχων και περιθεσεως χρυσιων η ενδυσεως ιματιων κοσμος');
INSERT INTO `bible_verses_tr` VALUES ('7283', '60', '3', '4', 'αλλ ο κρυπτος της καρδιας ανθρωπος εν τω αφθαρτω του πραεος και ησυχιου πνευματος ο εστιν ενωπιον του θεου πολυτελες');
INSERT INTO `bible_verses_tr` VALUES ('7284', '60', '3', '5', 'ουτως γαρ ποτε και αι αγιαι γυναικες αι ελπιζουσαι επι τον θεον εκοσμουν εαυτας υποτασσομεναι τοις ιδιοις ανδρασιν');
INSERT INTO `bible_verses_tr` VALUES ('7285', '60', '3', '6', 'ως σαρρα υπηκουσεν τω αβρααμ κυριον αυτον καλουσα ης εγενηθητε τεκνα αγαθοποιουσαι και μη φοβουμεναι μηδεμιαν πτοησιν');
INSERT INTO `bible_verses_tr` VALUES ('7286', '60', '3', '7', 'οι ανδρες ομοιως συνοικουντες κατα γνωσιν ως ασθενεστερω σκευει τω γυναικειω απονεμοντες τιμην ως και συγκληρονομοι χαριτος ζωης εις το μη εκκοπτεσθαι τας προσευχας υμων');
INSERT INTO `bible_verses_tr` VALUES ('7287', '60', '3', '8', 'το δε τελος παντες ομοφρονες συμπαθεις φιλαδελφοι ευσπλαγχνοι φιλοφρονες');
INSERT INTO `bible_verses_tr` VALUES ('7288', '60', '3', '9', 'μη αποδιδοντες κακον αντι κακου η λοιδοριαν αντι λοιδοριας τουναντιον δε ευλογουντες ειδοτες οτι εις τουτο εκληθητε ινα ευλογιαν κληρονομησητε');
INSERT INTO `bible_verses_tr` VALUES ('7289', '60', '3', '10', 'ο γαρ θελων ζωην αγαπαν και ιδειν ημερας αγαθας παυσατω την γλωσσαν αυτου απο κακου και χειλη αυτου του μη λαλησαι δολον');
INSERT INTO `bible_verses_tr` VALUES ('7290', '60', '3', '11', 'εκκλινατω απο κακου και ποιησατω αγαθον ζητησατω ειρηνην και διωξατω αυτην');
INSERT INTO `bible_verses_tr` VALUES ('7291', '60', '3', '12', 'οτι οι οφθαλμοι κυριου επι δικαιους και ωτα αυτου εις δεησιν αυτων προσωπον δε κυριου επι ποιουντας κακα');
INSERT INTO `bible_verses_tr` VALUES ('7292', '60', '3', '13', 'και τις ο κακωσων υμας εαν του αγαθου μιμηται γενησθε');
INSERT INTO `bible_verses_tr` VALUES ('7293', '60', '3', '14', 'αλλ ει και πασχοιτε δια δικαιοσυνην μακαριοι τον δε φοβον αυτων μη φοβηθητε μηδε ταραχθητε');
INSERT INTO `bible_verses_tr` VALUES ('7294', '60', '3', '15', 'κυριον δε τον θεον αγιασατε εν ταις καρδιαις υμων ετοιμοι δε αει προς απολογιαν παντι τω αιτουντι υμας λογον περι της εν υμιν ελπιδος μετα πραυτητος και φοβου');
INSERT INTO `bible_verses_tr` VALUES ('7295', '60', '3', '16', 'συνειδησιν εχοντες αγαθην ινα εν ω καταλαλωσιν υμων ως κακοποιων καταισχυνθωσιν οι επηρεαζοντες υμων την αγαθην εν χριστω αναστροφην');
INSERT INTO `bible_verses_tr` VALUES ('7296', '60', '3', '17', 'κρειττον γαρ αγαθοποιουντας ει θελει το θελημα του θεου πασχειν η κακοποιουντας');
INSERT INTO `bible_verses_tr` VALUES ('7297', '60', '3', '18', 'οτι και χριστος απαξ περι αμαρτιων επαθεν δικαιος υπερ αδικων ινα ημας προσαγαγη τω θεω θανατωθεις μεν σαρκι ζωοποιηθεις δε τω πνευματι');
INSERT INTO `bible_verses_tr` VALUES ('7298', '60', '3', '19', 'εν ω και τοις εν φυλακη πνευμασιν πορευθεις εκηρυξεν');
INSERT INTO `bible_verses_tr` VALUES ('7299', '60', '3', '20', 'απειθησασιν ποτε οτε απαξ εξεδεχετο η του θεου μακροθυμια εν ημεραις νωε κατασκευαζομενης κιβωτου εις ην ολιγαι τουτ εστιν οκτω ψυχαι διεσωθησαν δι υδατος');
INSERT INTO `bible_verses_tr` VALUES ('7300', '60', '3', '21', '{VAR1: ο } {VAR2: ω } και ημας αντιτυπον νυν σωζει βαπτισμα ου σαρκος αποθεσις ρυπου αλλα συνειδησεως αγαθης επερωτημα εις θεον δι αναστασεως ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7301', '60', '3', '22', 'ος εστιν εν δεξια του θεου πορευθεις εις ουρανον υποταγεντων αυτω αγγελων και εξουσιων και δυναμεων');
INSERT INTO `bible_verses_tr` VALUES ('7302', '60', '4', '1', 'χριστου ουν παθοντος υπερ ημων σαρκι και υμεις την αυτην εννοιαν οπλισασθε οτι ο παθων εν σαρκι πεπαυται αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('7303', '60', '4', '2', 'εις το μηκετι ανθρωπων επιθυμιαις αλλα θεληματι θεου τον επιλοιπον εν σαρκι βιωσαι χρονον');
INSERT INTO `bible_verses_tr` VALUES ('7304', '60', '4', '3', 'αρκετος γαρ ημιν ο παρεληλυθως χρονος του βιου το θελημα των εθνων κατεργασασθαι πεπορευμενους εν ασελγειαις επιθυμιαις οινοφλυγιαις κωμοις ποτοις και αθεμιτοις ειδωλολατρειαις');
INSERT INTO `bible_verses_tr` VALUES ('7305', '60', '4', '4', 'εν ω ξενιζονται μη συντρεχοντων υμων εις την αυτην της ασωτιας αναχυσιν βλασφημουντες');
INSERT INTO `bible_verses_tr` VALUES ('7306', '60', '4', '5', 'οι αποδωσουσιν λογον τω ετοιμως εχοντι κριναι ζωντας και νεκρους');
INSERT INTO `bible_verses_tr` VALUES ('7307', '60', '4', '6', 'εις τουτο γαρ και νεκροις ευηγγελισθη ινα κριθωσιν μεν κατα ανθρωπους σαρκι ζωσιν δε κατα θεον πνευματι');
INSERT INTO `bible_verses_tr` VALUES ('7308', '60', '4', '7', 'παντων δε το τελος ηγγικεν σωφρονησατε ουν και νηψατε εις τας προσευχας');
INSERT INTO `bible_verses_tr` VALUES ('7309', '60', '4', '8', 'προ παντων δε την εις εαυτους αγαπην εκτενη εχοντες οτι  {VAR2: η } αγαπη καλυψει πληθος αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('7310', '60', '4', '9', 'φιλοξενοι εις αλληλους ανευ γογγυσμων');
INSERT INTO `bible_verses_tr` VALUES ('7311', '60', '4', '10', 'εκαστος καθως ελαβεν χαρισμα εις εαυτους αυτο διακονουντες ως καλοι οικονομοι ποικιλης χαριτος θεου');
INSERT INTO `bible_verses_tr` VALUES ('7312', '60', '4', '11', 'ει τις λαλει ως λογια θεου ει τις διακονει ως εξ ισχυος ης χορηγει ο θεος ινα εν πασιν δοξαζηται ο θεος δια ιησου χριστου ω εστιν η δοξα και το κρατος εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('7313', '60', '4', '12', 'αγαπητοι μη ξενιζεσθε τη εν υμιν πυρωσει προς πειρασμον υμιν γινομενη ως ξενου υμιν συμβαινοντος');
INSERT INTO `bible_verses_tr` VALUES ('7314', '60', '4', '13', 'αλλα καθο κοινωνειτε τοις του χριστου παθημασιν χαιρετε ινα και εν τη αποκαλυψει της δοξης αυτου χαρητε αγαλλιωμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7315', '60', '4', '14', 'ει ονειδιζεσθε εν ονοματι χριστου μακαριοι οτι το της δοξης και το του θεου πνευμα εφ υμας αναπαυεται κατα μεν αυτους βλασφημειται κατα δε υμας δοξαζεται');
INSERT INTO `bible_verses_tr` VALUES ('7316', '60', '4', '15', 'μη γαρ τις υμων πασχετω ως φονευς η κλεπτης η κακοποιος η ως αλλοτριοεπισκοπος');
INSERT INTO `bible_verses_tr` VALUES ('7317', '60', '4', '16', 'ει δε ως χριστιανος μη αισχυνεσθω δοξαζετω δε τον θεον εν τω μερει τουτω');
INSERT INTO `bible_verses_tr` VALUES ('7318', '60', '4', '17', 'οτι ο καιρος του αρξασθαι το κριμα απο του οικου του θεου ει δε πρωτον αφ ημων τι το τελος των απειθουντων τω του θεου ευαγγελιω');
INSERT INTO `bible_verses_tr` VALUES ('7319', '60', '4', '18', 'και ει ο δικαιος μολις σωζεται ο ασεβης και αμαρτωλος που φανειται');
INSERT INTO `bible_verses_tr` VALUES ('7320', '60', '4', '19', 'ωστε και οι πασχοντες κατα το θελημα του θεου ως πιστω κτιστη παρατιθεσθωσαν τας ψυχας εαυτων εν αγαθοποιια');
INSERT INTO `bible_verses_tr` VALUES ('7321', '60', '5', '1', 'πρεσβυτερους τους εν υμιν παρακαλω ο συμπρεσβυτερος και μαρτυς των του χριστου παθηματων ο και της μελλουσης αποκαλυπτεσθαι δοξης κοινωνος');
INSERT INTO `bible_verses_tr` VALUES ('7322', '60', '5', '2', 'ποιμανατε το εν υμιν ποιμνιον του θεου επισκοπουντες μη αναγκαστως αλλ εκουσιως μηδε αισχροκερδως αλλα προθυμως');
INSERT INTO `bible_verses_tr` VALUES ('7323', '60', '5', '3', 'μηδ ως κατακυριευοντες των κληρων αλλα τυποι γινομενοι του ποιμνιου');
INSERT INTO `bible_verses_tr` VALUES ('7324', '60', '5', '4', 'και φανερωθεντος του αρχιποιμενος κομιεισθε τον αμαραντινον της δοξης στεφανον');
INSERT INTO `bible_verses_tr` VALUES ('7325', '60', '5', '5', 'ομοιως νεωτεροι υποταγητε πρεσβυτεροις παντες δε αλληλοις υποτασσομενοι την ταπεινοφροσυνην εγκομβωσασθε οτι ο θεος υπερηφανοις αντιτασσεται ταπεινοις δε διδωσιν χαριν');
INSERT INTO `bible_verses_tr` VALUES ('7326', '60', '5', '6', 'ταπεινωθητε ουν υπο την κραταιαν χειρα του θεου ινα υμας υψωση εν καιρω');
INSERT INTO `bible_verses_tr` VALUES ('7327', '60', '5', '7', 'πασαν την μεριμναν υμων επιρριψαντες επ αυτον οτι αυτω μελει περι υμων');
INSERT INTO `bible_verses_tr` VALUES ('7328', '60', '5', '8', 'νηψατε γρηγορησατε οτι ο αντιδικος υμων διαβολος ως λεων ωρυομενος περιπατει ζητων τινα καταπιη');
INSERT INTO `bible_verses_tr` VALUES ('7329', '60', '5', '9', 'ω αντιστητε στερεοι τη πιστει ειδοτες τα αυτα των παθηματων τη εν κοσμω υμων αδελφοτητι επιτελεισθαι');
INSERT INTO `bible_verses_tr` VALUES ('7330', '60', '5', '10', 'ο δε θεος πασης χαριτος ο καλεσας ημας εις την αιωνιον αυτου δοξαν εν χριστω ιησου ολιγον παθοντας αυτος καταρτισαι υμας στηριξαι σθενωσαι θεμελιωσαι');
INSERT INTO `bible_verses_tr` VALUES ('7331', '60', '5', '11', 'αυτω η δοξα και το κρατος εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('7332', '60', '5', '12', 'δια σιλουανου υμιν του πιστου αδελφου ως λογιζομαι δι ολιγων εγραψα παρακαλων και επιμαρτυρων ταυτην ειναι αληθη χαριν του θεου εις ην εστηκατε');
INSERT INTO `bible_verses_tr` VALUES ('7333', '60', '5', '13', 'ασπαζεται υμας η εν βαβυλωνι συνεκλεκτη και μαρκος ο υιος μου');
INSERT INTO `bible_verses_tr` VALUES ('7334', '60', '5', '14', 'ασπασασθε αλληλους εν φιληματι αγαπης ειρηνη υμιν πασιν τοις εν χριστω ιησου αμην');
INSERT INTO `bible_verses_tr` VALUES ('7335', '61', '1', '1', '{VAR1: συμεων } {VAR2: σιμων } πετρος δουλος και αποστολος ιησου χριστου τοις ισοτιμον ημιν λαχουσιν πιστιν εν δικαιοσυνη του θεου ημων και σωτηρος  {VAR2: ημων } ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7336', '61', '1', '2', 'χαρις υμιν και ειρηνη πληθυνθειη εν επιγνωσει του θεου και ιησου του κυριου ημων');
INSERT INTO `bible_verses_tr` VALUES ('7337', '61', '1', '3', 'ως παντα ημιν της θειας δυναμεως αυτου τα προς ζωην και ευσεβειαν δεδωρημενης δια της επιγνωσεως του καλεσαντος ημας δια δοξης και αρετης');
INSERT INTO `bible_verses_tr` VALUES ('7338', '61', '1', '4', 'δι ων τα μεγιστα ημιν και τιμια επαγγελματα δεδωρηται ινα δια τουτων γενησθε θειας κοινωνοι φυσεως αποφυγοντες της εν κοσμω εν επιθυμια φθορας');
INSERT INTO `bible_verses_tr` VALUES ('7339', '61', '1', '5', 'και αυτο τουτο δε σπουδην πασαν παρεισενεγκαντες επιχορηγησατε εν τη πιστει υμων την αρετην εν δε τη αρετη την γνωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7340', '61', '1', '6', 'εν δε τη γνωσει την εγκρατειαν εν δε τη εγκρατεια την υπομονην εν δε τη υπομονη την ευσεβειαν');
INSERT INTO `bible_verses_tr` VALUES ('7341', '61', '1', '7', 'εν δε τη ευσεβεια την φιλαδελφιαν εν δε τη φιλαδελφια την αγαπην');
INSERT INTO `bible_verses_tr` VALUES ('7342', '61', '1', '8', 'ταυτα γαρ υμιν υπαρχοντα και πλεοναζοντα ουκ αργους ουδε ακαρπους καθιστησιν εις την του κυριου ημων ιησου χριστου επιγνωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7343', '61', '1', '9', 'ω γαρ μη παρεστιν ταυτα τυφλος εστιν μυωπαζων ληθην λαβων του καθαρισμου των παλαι αυτου αμαρτιων');
INSERT INTO `bible_verses_tr` VALUES ('7344', '61', '1', '10', 'διο μαλλον αδελφοι σπουδασατε βεβαιαν υμων την κλησιν και εκλογην ποιεισθαι ταυτα γαρ ποιουντες ου μη πταισητε ποτε');
INSERT INTO `bible_verses_tr` VALUES ('7345', '61', '1', '11', 'ουτως γαρ πλουσιως επιχορηγηθησεται υμιν η εισοδος εις την αιωνιον βασιλειαν του κυριου ημων και σωτηρος ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7346', '61', '1', '12', 'διο ουκ αμελησω υμας αει υπομιμνησκειν περι τουτων καιπερ ειδοτας και εστηριγμενους εν τη παρουση αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('7347', '61', '1', '13', 'δικαιον δε ηγουμαι εφ οσον ειμι εν τουτω τω σκηνωματι διεγειρειν υμας εν υπομνησει');
INSERT INTO `bible_verses_tr` VALUES ('7348', '61', '1', '14', 'ειδως οτι ταχινη εστιν η αποθεσις του σκηνωματος μου καθως και ο κυριος ημων ιησους χριστος εδηλωσεν μοι');
INSERT INTO `bible_verses_tr` VALUES ('7349', '61', '1', '15', 'σπουδασω δε και εκαστοτε εχειν υμας μετα την εμην εξοδον την τουτων μνημην ποιεισθαι');
INSERT INTO `bible_verses_tr` VALUES ('7350', '61', '1', '16', 'ου γαρ σεσοφισμενοις μυθοις εξακολουθησαντες εγνωρισαμεν υμιν την του κυριου ημων ιησου χριστου δυναμιν και παρουσιαν αλλ εποπται γενηθεντες της εκεινου μεγαλειοτητος');
INSERT INTO `bible_verses_tr` VALUES ('7351', '61', '1', '17', 'λαβων γαρ παρα θεου πατρος τιμην και δοξαν φωνης ενεχθεισης αυτω τοιασδε υπο της μεγαλοπρεπους δοξης ουτος εστιν ο υιος μου ο αγαπητος εις ον εγω ευδοκησα');
INSERT INTO `bible_verses_tr` VALUES ('7352', '61', '1', '18', 'και ταυτην την φωνην ημεις ηκουσαμεν εξ ουρανου ενεχθεισαν συν αυτω οντες εν τω ορει τω αγιω');
INSERT INTO `bible_verses_tr` VALUES ('7353', '61', '1', '19', 'και εχομεν βεβαιοτερον τον προφητικον λογον ω καλως ποιειτε προσεχοντες ως λυχνω φαινοντι εν αυχμηρω τοπω εως ου ημερα διαυγαση και φωσφορος ανατειλη εν ταις καρδιαις υμων');
INSERT INTO `bible_verses_tr` VALUES ('7354', '61', '1', '20', 'τουτο πρωτον γινωσκοντες οτι πασα προφητεια γραφης ιδιας επιλυσεως ου γινεται');
INSERT INTO `bible_verses_tr` VALUES ('7355', '61', '1', '21', 'ου γαρ θεληματι ανθρωπου ηνεχθη ποτε προφητεια αλλ υπο πνευματος αγιου φερομενοι ελαλησαν  {VAR1: οι } αγιοι θεου ανθρωποι');
INSERT INTO `bible_verses_tr` VALUES ('7356', '61', '2', '1', 'εγενοντο δε και ψευδοπροφηται εν τω λαω ως και εν υμιν εσονται ψευδοδιδασκαλοι οιτινες παρεισαξουσιν αιρεσεις απωλειας και τον αγορασαντα αυτους δεσποτην αρνουμενοι επαγοντες εαυτοις ταχινην απωλειαν');
INSERT INTO `bible_verses_tr` VALUES ('7357', '61', '2', '2', 'και πολλοι εξακολουθησουσιν αυτων ταις απωλειαις δι ους η οδος της αληθειας βλασφημηθησεται');
INSERT INTO `bible_verses_tr` VALUES ('7358', '61', '2', '3', 'και εν πλεονεξια πλαστοις λογοις υμας εμπορευσονται οις το κριμα εκπαλαι ουκ αργει και η απωλεια αυτων ου νυσταζει');
INSERT INTO `bible_verses_tr` VALUES ('7359', '61', '2', '4', 'ει γαρ ο θεος αγγελων αμαρτησαντων ουκ εφεισατο αλλα σειραις ζοφου ταρταρωσας παρεδωκεν εις κρισιν τετηρημενους');
INSERT INTO `bible_verses_tr` VALUES ('7360', '61', '2', '5', 'και αρχαιου κοσμου ουκ εφεισατο αλλ ογδοον νωε δικαιοσυνης κηρυκα εφυλαξεν κατακλυσμον κοσμω ασεβων επαξας');
INSERT INTO `bible_verses_tr` VALUES ('7361', '61', '2', '6', 'και πολεις σοδομων και γομορρας τεφρωσας καταστροφη κατεκρινεν υποδειγμα μελλοντων ασεβειν τεθεικως');
INSERT INTO `bible_verses_tr` VALUES ('7362', '61', '2', '7', 'και δικαιον λωτ καταπονουμενον υπο της των αθεσμων εν ασελγεια αναστροφης ερρυσατο');
INSERT INTO `bible_verses_tr` VALUES ('7363', '61', '2', '8', 'βλεμματι γαρ και ακοη ο δικαιος εγκατοικων εν αυτοις ημεραν εξ ημερας ψυχην δικαιαν ανομοις εργοις εβασανιζεν');
INSERT INTO `bible_verses_tr` VALUES ('7364', '61', '2', '9', 'οιδεν κυριος ευσεβεις εκ  {VAR1: πειρασμου } {VAR2: πειρασμων } ρυεσθαι αδικους δε εις ημεραν κρισεως κολαζομενους τηρειν');
INSERT INTO `bible_verses_tr` VALUES ('7365', '61', '2', '10', 'μαλιστα δε τους οπισω σαρκος εν επιθυμια μιασμου πορευομενους και κυριοτητος καταφρονουντας τολμηται αυθαδεις δοξας ου τρεμουσιν βλασφημουντες');
INSERT INTO `bible_verses_tr` VALUES ('7366', '61', '2', '11', 'οπου αγγελοι ισχυι και δυναμει μειζονες οντες ου φερουσιν κατ αυτων παρα κυριω βλασφημον κρισιν');
INSERT INTO `bible_verses_tr` VALUES ('7367', '61', '2', '12', 'ουτοι δε ως αλογα ζωα φυσικα γεγενημενα εις αλωσιν και φθοραν εν οις αγνοουσιν βλασφημουντες εν τη φθορα αυτων καταφθαρησονται');
INSERT INTO `bible_verses_tr` VALUES ('7368', '61', '2', '13', 'κομιουμενοι μισθον αδικιας ηδονην ηγουμενοι την εν ημερα τρυφην σπιλοι και μωμοι εντρυφωντες εν ταις απαταις αυτων συνευωχουμενοι υμιν');
INSERT INTO `bible_verses_tr` VALUES ('7369', '61', '2', '14', 'οφθαλμους εχοντες μεστους μοιχαλιδος και ακαταπαυστους αμαρτιας δελεαζοντες ψυχας αστηρικτους καρδιαν γεγυμνασμενην πλεονεξιαις εχοντες καταρας τεκνα');
INSERT INTO `bible_verses_tr` VALUES ('7370', '61', '2', '15', 'καταλιποντες την ευθειαν οδον επλανηθησαν εξακολουθησαντες τη οδω του βαλααμ του βοσορ ος μισθον αδικιας ηγαπησεν');
INSERT INTO `bible_verses_tr` VALUES ('7371', '61', '2', '16', 'ελεγξιν δε εσχεν ιδιας παρανομιας υποζυγιον αφωνον εν ανθρωπου φωνη φθεγξαμενον εκωλυσεν την του προφητου παραφρονιαν');
INSERT INTO `bible_verses_tr` VALUES ('7372', '61', '2', '17', 'ουτοι εισιν πηγαι ανυδροι νεφελαι υπο λαιλαπος ελαυνομεναι οις ο ζοφος του σκοτους εις αιωνα τετηρηται');
INSERT INTO `bible_verses_tr` VALUES ('7373', '61', '2', '18', 'υπερογκα γαρ ματαιοτητος φθεγγομενοι δελεαζουσιν εν επιθυμιαις σαρκος  {VAR2: εν } ασελγειαις τους οντως αποφυγοντας τους εν πλανη αναστρεφομενους');
INSERT INTO `bible_verses_tr` VALUES ('7374', '61', '2', '19', 'ελευθεριαν αυτοις επαγγελλομενοι αυτοι δουλοι υπαρχοντες της φθορας ω γαρ τις ηττηται τουτω και δεδουλωται');
INSERT INTO `bible_verses_tr` VALUES ('7375', '61', '2', '20', 'ει γαρ αποφυγοντες τα μιασματα του κοσμου εν επιγνωσει του κυριου και σωτηρος ιησου χριστου τουτοις δε παλιν εμπλακεντες ηττωνται γεγονεν αυτοις τα εσχατα χειρονα των πρωτων');
INSERT INTO `bible_verses_tr` VALUES ('7376', '61', '2', '21', 'κρειττον γαρ ην αυτοις μη επεγνωκεναι την οδον της δικαιοσυνης η επιγνουσιν επιστρεψαι εκ της παραδοθεισης αυτοις αγιας εντολης');
INSERT INTO `bible_verses_tr` VALUES ('7377', '61', '2', '22', 'συμβεβηκεν δε αυτοις το της αληθους παροιμιας κυων επιστρεψας επι το ιδιον εξεραμα και υς λουσαμενη εις κυλισμα βορβορου');
INSERT INTO `bible_verses_tr` VALUES ('7378', '61', '3', '1', 'ταυτην ηδη αγαπητοι δευτεραν υμιν γραφω επιστολην εν αις διεγειρω υμων εν υπομνησει την ειλικρινη διανοιαν');
INSERT INTO `bible_verses_tr` VALUES ('7379', '61', '3', '2', 'μνησθηναι των προειρημενων ρηματων υπο των αγιων προφητων και της των αποστολων ημων εντολης του κυριου και σωτηρος');
INSERT INTO `bible_verses_tr` VALUES ('7380', '61', '3', '3', 'τουτο πρωτον γινωσκοντες οτι ελευσονται επ εσχατου των ημερων εμπαικται κατα τας ιδιας αυτων επιθυμιας πορευομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7381', '61', '3', '4', 'και λεγοντες που εστιν η επαγγελια της παρουσιας αυτου αφ ης γαρ οι πατερες εκοιμηθησαν παντα ουτως διαμενει απ αρχης κτισεως');
INSERT INTO `bible_verses_tr` VALUES ('7382', '61', '3', '5', 'λανθανει γαρ αυτους τουτο θελοντας οτι ουρανοι ησαν εκπαλαι και γη εξ υδατος και δι υδατος συνεστωσα τω του θεου λογω');
INSERT INTO `bible_verses_tr` VALUES ('7383', '61', '3', '6', 'δι ων ο τοτε κοσμος υδατι κατακλυσθεις απωλετο');
INSERT INTO `bible_verses_tr` VALUES ('7384', '61', '3', '7', 'οι δε νυν ουρανοι και η γη  {VAR1: αυτου } {VAR2: τω αυτω } λογω τεθησαυρισμενοι εισιν πυρι τηρουμενοι εις ημεραν κρισεως και απωλειας των ασεβων ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('7385', '61', '3', '8', 'εν δε τουτο μη λανθανετω υμας αγαπητοι οτι μια ημερα παρα κυριω ως χιλια ετη και χιλια ετη ως ημερα μια');
INSERT INTO `bible_verses_tr` VALUES ('7386', '61', '3', '9', 'ου βραδυνει ο κυριος της επαγγελιας ως τινες βραδυτητα ηγουνται αλλα μακροθυμει εις ημας μη βουλομενος τινας απολεσθαι αλλα παντας εις μετανοιαν χωρησαι');
INSERT INTO `bible_verses_tr` VALUES ('7387', '61', '3', '10', 'ηξει δε η ημερα κυριου ως κλεπτης εν νυκτι εν η οι ουρανοι ροιζηδον παρελευσονται στοιχεια δε καυσουμενα λυθησονται και γη και τα εν αυτη εργα κατακαησεται');
INSERT INTO `bible_verses_tr` VALUES ('7388', '61', '3', '11', 'τουτων ουν παντων λυομενων ποταπους δει υπαρχειν υμας εν αγιαις αναστροφαις και ευσεβειαις');
INSERT INTO `bible_verses_tr` VALUES ('7389', '61', '3', '12', 'προσδοκωντας και σπευδοντας την παρουσιαν της του θεου ημερας δι ην ουρανοι πυρουμενοι λυθησονται και στοιχεια καυσουμενα τηκεται');
INSERT INTO `bible_verses_tr` VALUES ('7390', '61', '3', '13', 'καινους δε ουρανους και γην καινην κατα το επαγγελμα αυτου προσδοκωμεν εν οις δικαιοσυνη κατοικει');
INSERT INTO `bible_verses_tr` VALUES ('7391', '61', '3', '14', 'διο αγαπητοι ταυτα προσδοκωντες σπουδασατε ασπιλοι και αμωμητοι αυτω ευρεθηναι εν ειρηνη');
INSERT INTO `bible_verses_tr` VALUES ('7392', '61', '3', '15', 'και την του κυριου ημων μακροθυμιαν σωτηριαν ηγεισθε καθως και ο αγαπητος ημων αδελφος παυλος κατα την αυτω δοθεισαν σοφιαν εγραψεν υμιν');
INSERT INTO `bible_verses_tr` VALUES ('7393', '61', '3', '16', 'ως και εν πασαις ταις επιστολαις λαλων εν αυταις περι τουτων εν οις εστιν δυσνοητα τινα α οι αμαθεις και αστηρικτοι στρεβλουσιν ως και τας λοιπας γραφας προς την ιδιαν αυτων απωλειαν');
INSERT INTO `bible_verses_tr` VALUES ('7394', '61', '3', '17', 'υμεις ουν αγαπητοι προγινωσκοντες φυλασσεσθε ινα μη τη των αθεσμων πλανη συναπαχθεντες εκπεσητε του ιδιου στηριγμου');
INSERT INTO `bible_verses_tr` VALUES ('7395', '61', '3', '18', 'αυξανετε δε εν χαριτι και γνωσει του κυριου ημων και σωτηρος ιησου χριστου αυτω η δοξα και νυν και εις ημεραν αιωνος αμην');
INSERT INTO `bible_verses_tr` VALUES ('7396', '62', '1', '1', 'ο ην απ αρχης ο ακηκοαμεν ο εωρακαμεν τοις οφθαλμοις ημων ο εθεασαμεθα και αι χειρες ημων εψηλαφησαν περι του λογου της ζωης');
INSERT INTO `bible_verses_tr` VALUES ('7397', '62', '1', '2', 'και η ζωη εφανερωθη και εωρακαμεν και μαρτυρουμεν και απαγγελλομεν υμιν την ζωην την αιωνιον ητις ην προς τον πατερα και εφανερωθη ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7398', '62', '1', '3', 'ο εωρακαμεν και ακηκοαμεν απαγγελλομεν υμιν ινα και υμεις κοινωνιαν εχητε μεθ ημων και η κοινωνια δε η ημετερα μετα του πατρος και μετα του υιου αυτου ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7399', '62', '1', '4', 'και ταυτα γραφομεν υμιν ινα η χαρα  {VAR1: ημων } {VAR2: υμων } η πεπληρωμενη');
INSERT INTO `bible_verses_tr` VALUES ('7400', '62', '1', '5', 'και αυτη εστιν η  {VAR1: επαγγελια } {VAR2: αγγελια } ην ακηκοαμεν απ αυτου και αναγγελλομεν υμιν οτι ο θεος φως εστιν και σκοτια εν αυτω ουκ εστιν ουδεμια');
INSERT INTO `bible_verses_tr` VALUES ('7401', '62', '1', '6', 'εαν ειπωμεν οτι κοινωνιαν εχομεν μετ αυτου και εν τω σκοτει περιπατωμεν ψευδομεθα και ου ποιουμεν την αληθειαν');
INSERT INTO `bible_verses_tr` VALUES ('7402', '62', '1', '7', 'εαν δε εν τω φωτι περιπατωμεν ως αυτος εστιν εν τω φωτι κοινωνιαν εχομεν μετ αλληλων και το αιμα ιησου χριστου του υιου αυτου καθαριζει ημας απο πασης αμαρτιας');
INSERT INTO `bible_verses_tr` VALUES ('7403', '62', '1', '8', 'εαν ειπωμεν οτι αμαρτιαν ουκ εχομεν εαυτους πλανωμεν και η αληθεια ουκ εστιν εν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7404', '62', '1', '9', 'εαν ομολογωμεν τας αμαρτιας ημων πιστος εστιν και δικαιος ινα αφη ημιν τας αμαρτιας και καθαριση ημας απο πασης αδικιας');
INSERT INTO `bible_verses_tr` VALUES ('7405', '62', '1', '10', 'εαν ειπωμεν οτι ουχ ημαρτηκαμεν ψευστην ποιουμεν αυτον και ο λογος αυτου ουκ εστιν εν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7406', '62', '2', '1', 'τεκνια μου ταυτα γραφω υμιν ινα μη αμαρτητε και εαν τις αμαρτη παρακλητον εχομεν προς τον πατερα ιησουν χριστον δικαιον');
INSERT INTO `bible_verses_tr` VALUES ('7407', '62', '2', '2', 'και αυτος ιλασμος εστιν περι των αμαρτιων ημων ου περι των ημετερων δε μονον αλλα και περι ολου του κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('7408', '62', '2', '3', 'και εν τουτω γινωσκομεν οτι εγνωκαμεν αυτον εαν τας εντολας αυτου τηρωμεν');
INSERT INTO `bible_verses_tr` VALUES ('7409', '62', '2', '4', 'ο λεγων εγνωκα αυτον και τας εντολας αυτου μη τηρων ψευστης εστιν και εν τουτω η αληθεια ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7410', '62', '2', '5', 'ος δ αν τηρη αυτου τον λογον αληθως εν τουτω η αγαπη του θεου τετελειωται εν τουτω γινωσκομεν οτι εν αυτω εσμεν');
INSERT INTO `bible_verses_tr` VALUES ('7411', '62', '2', '6', 'ο λεγων εν αυτω μενειν οφειλει καθως εκεινος περιεπατησεν και αυτος ουτως περιπατειν');
INSERT INTO `bible_verses_tr` VALUES ('7412', '62', '2', '7', 'αδελφοι ουκ εντολην καινην γραφω υμιν αλλ εντολην παλαιαν ην ειχετε απ αρχης η εντολη η παλαια εστιν ο λογος ον ηκουσατε απ αρχης');
INSERT INTO `bible_verses_tr` VALUES ('7413', '62', '2', '8', 'παλιν εντολην καινην γραφω υμιν ο εστιν αληθες εν αυτω και εν υμιν οτι η σκοτια παραγεται και το φως το αληθινον ηδη φαινει');
INSERT INTO `bible_verses_tr` VALUES ('7414', '62', '2', '9', 'ο λεγων εν τω φωτι ειναι και τον αδελφον αυτου μισων εν τη σκοτια εστιν εως αρτι');
INSERT INTO `bible_verses_tr` VALUES ('7415', '62', '2', '10', 'ο αγαπων τον αδελφον αυτου εν τω φωτι μενει και σκανδαλον εν αυτω ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7416', '62', '2', '11', 'ο δε μισων τον αδελφον αυτου εν τη σκοτια εστιν και εν τη σκοτια περιπατει και ουκ οιδεν που υπαγει οτι η σκοτια ετυφλωσεν τους οφθαλμους αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7417', '62', '2', '12', 'γραφω υμιν τεκνια οτι αφεωνται υμιν αι αμαρτιαι δια το ονομα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7418', '62', '2', '13', 'γραφω υμιν πατερες οτι εγνωκατε τον απ αρχης γραφω υμιν νεανισκοι οτι νενικηκατε τον πονηρον γραφω υμιν παιδια οτι εγνωκατε τον πατερα');
INSERT INTO `bible_verses_tr` VALUES ('7419', '62', '2', '14', 'εγραψα υμιν πατερες οτι εγνωκατε τον απ αρχης εγραψα υμιν νεανισκοι οτι ισχυροι εστε και ο λογος του θεου εν υμιν μενει και νενικηκατε τον πονηρον');
INSERT INTO `bible_verses_tr` VALUES ('7420', '62', '2', '15', 'μη αγαπατε τον κοσμον μηδε τα εν τω κοσμω εαν τις αγαπα τον κοσμον ουκ εστιν η αγαπη του πατρος εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7421', '62', '2', '16', 'οτι παν το εν τω κοσμω η επιθυμια της σαρκος και η επιθυμια των οφθαλμων και η αλαζονεια του βιου ουκ εστιν εκ του πατρος αλλ εκ του κοσμου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7422', '62', '2', '17', 'και ο κοσμος παραγεται και η επιθυμια αυτου ο δε ποιων το θελημα του θεου μενει εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('7423', '62', '2', '18', 'παιδια εσχατη ωρα εστιν και καθως ηκουσατε οτι ο αντιχριστος ερχεται και νυν αντιχριστοι πολλοι γεγονασιν οθεν γινωσκομεν οτι εσχατη ωρα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7424', '62', '2', '19', 'εξ ημων εξηλθον αλλ ουκ ησαν εξ ημων ει γαρ ησαν εξ ημων μεμενηκεισαν αν μεθ ημων αλλ ινα φανερωθωσιν οτι ουκ εισιν παντες εξ ημων');
INSERT INTO `bible_verses_tr` VALUES ('7425', '62', '2', '20', 'και υμεις χρισμα εχετε απο του αγιου και οιδατε παντα');
INSERT INTO `bible_verses_tr` VALUES ('7426', '62', '2', '21', 'ουκ εγραψα υμιν οτι ουκ οιδατε την αληθειαν αλλ οτι οιδατε αυτην και οτι παν ψευδος εκ της αληθειας ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7427', '62', '2', '22', 'τις εστιν ο ψευστης ει μη ο αρνουμενος οτι ιησους ουκ εστιν ο χριστος ουτος εστιν ο αντιχριστος ο αρνουμενος τον πατερα και τον υιον');
INSERT INTO `bible_verses_tr` VALUES ('7428', '62', '2', '23', 'πας ο αρνουμενος τον υιον ουδε τον πατερα εχει  {VAR2: ο ομολογων τον υιον και τον πατερα εχει }');
INSERT INTO `bible_verses_tr` VALUES ('7429', '62', '2', '24', 'υμεις ουν ο ηκουσατε απ αρχης εν υμιν μενετω εαν εν υμιν μεινη ο απ αρχης ηκουσατε και υμεις εν τω υιω και εν τω πατρι μενειτε');
INSERT INTO `bible_verses_tr` VALUES ('7430', '62', '2', '25', 'και αυτη εστιν η επαγγελια ην αυτος επηγγειλατο ημιν την ζωην την αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('7431', '62', '2', '26', 'ταυτα εγραψα υμιν περι των πλανωντων υμας');
INSERT INTO `bible_verses_tr` VALUES ('7432', '62', '2', '27', 'και υμεις το χρισμα ο ελαβετε απ αυτου εν υμιν μενει και ου χρειαν εχετε ινα τις διδασκη υμας αλλ ως το αυτο χρισμα διδασκει υμας περι παντων και αληθες εστιν και ουκ εστιν ψευδος και καθως εδιδαξεν υμας μενειτε εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7433', '62', '2', '28', 'και νυν τεκνια μενετε εν αυτω ινα οταν φανερωθη εχωμεν παρρησιαν και μη αισχυνθωμεν απ αυτου εν τη παρουσια αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7434', '62', '2', '29', 'εαν ειδητε οτι δικαιος εστιν γινωσκετε οτι πας ο ποιων την δικαιοσυνην εξ αυτου γεγεννηται');
INSERT INTO `bible_verses_tr` VALUES ('7435', '62', '3', '1', 'ιδετε ποταπην αγαπην δεδωκεν ημιν ο πατηρ ινα τεκνα θεου κληθωμεν δια τουτο ο κοσμος ου γινωσκει ημας οτι ουκ εγνω αυτον');
INSERT INTO `bible_verses_tr` VALUES ('7436', '62', '3', '2', 'αγαπητοι νυν τεκνα θεου εσμεν και ουπω εφανερωθη τι εσομεθα οιδαμεν δε οτι εαν φανερωθη ομοιοι αυτω εσομεθα οτι οψομεθα αυτον καθως εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7437', '62', '3', '3', 'και πας ο εχων την ελπιδα ταυτην επ αυτω αγνιζει εαυτον καθως εκεινος αγνος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7438', '62', '3', '4', 'πας ο ποιων την αμαρτιαν και την ανομιαν ποιει και η αμαρτια εστιν η ανομια');
INSERT INTO `bible_verses_tr` VALUES ('7439', '62', '3', '5', 'και οιδατε οτι εκεινος εφανερωθη ινα τας αμαρτιας ημων αρη και αμαρτια εν αυτω ουκ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7440', '62', '3', '6', 'πας ο εν αυτω μενων ουχ αμαρτανει πας ο αμαρτανων ουχ εωρακεν αυτον ουδε εγνωκεν αυτον');
INSERT INTO `bible_verses_tr` VALUES ('7441', '62', '3', '7', 'τεκνια μηδεις πλανατω υμας ο ποιων την δικαιοσυνην δικαιος εστιν καθως εκεινος δικαιος εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7442', '62', '3', '8', 'ο ποιων την αμαρτιαν εκ του διαβολου εστιν οτι απ αρχης ο διαβολος αμαρτανει εις τουτο εφανερωθη ο υιος του θεου ινα λυση τα εργα του διαβολου');
INSERT INTO `bible_verses_tr` VALUES ('7443', '62', '3', '9', 'πας ο γεγεννημενος εκ του θεου αμαρτιαν ου ποιει οτι σπερμα αυτου εν αυτω μενει και ου δυναται αμαρτανειν οτι εκ του θεου γεγεννηται');
INSERT INTO `bible_verses_tr` VALUES ('7444', '62', '3', '10', 'εν τουτω φανερα εστιν τα τεκνα του θεου και τα τεκνα του διαβολου πας ο μη ποιων δικαιοσυνην ουκ εστιν εκ του θεου και ο μη αγαπων τον αδελφον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7445', '62', '3', '11', 'οτι αυτη εστιν η αγγελια ην ηκουσατε απ αρχης ινα αγαπωμεν αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('7446', '62', '3', '12', 'ου καθως καιν εκ του πονηρου ην και εσφαξεν τον αδελφον αυτου και χαριν τινος εσφαξεν αυτον οτι τα εργα αυτου πονηρα ην τα δε του αδελφου αυτου δικαια');
INSERT INTO `bible_verses_tr` VALUES ('7447', '62', '3', '13', 'μη θαυμαζετε αδελφοι μου ει μισει υμας ο κοσμος');
INSERT INTO `bible_verses_tr` VALUES ('7448', '62', '3', '14', 'ημεις οιδαμεν οτι μεταβεβηκαμεν εκ του θανατου εις την ζωην οτι αγαπωμεν τους αδελφους ο μη αγαπων τον αδελφον μενει εν τω θανατω');
INSERT INTO `bible_verses_tr` VALUES ('7449', '62', '3', '15', 'πας ο μισων τον αδελφον αυτου ανθρωποκτονος εστιν και οιδατε οτι πας ανθρωποκτονος ουκ εχει ζωην αιωνιον εν αυτω μενουσαν');
INSERT INTO `bible_verses_tr` VALUES ('7450', '62', '3', '16', 'εν τουτω εγνωκαμεν την αγαπην  {VAR2: του θεου } οτι εκεινος υπερ ημων την ψυχην αυτου εθηκεν και ημεις οφειλομεν υπερ των αδελφων τας ψυχας τιθεναι');
INSERT INTO `bible_verses_tr` VALUES ('7451', '62', '3', '17', 'ος δ αν εχη τον βιον του κοσμου και θεωρη τον αδελφον αυτου χρειαν εχοντα και κλειση τα σπλαγχνα αυτου απ αυτου πως η αγαπη του θεου μενει εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7452', '62', '3', '18', 'τεκνια μου μη αγαπωμεν λογω μηδε γλωσση αλλ εργω και αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('7453', '62', '3', '19', 'και εν τουτω γινωσκομεν οτι εκ της αληθειας εσμεν και εμπροσθεν αυτου πεισομεν τας καρδιας ημων');
INSERT INTO `bible_verses_tr` VALUES ('7454', '62', '3', '20', 'οτι εαν καταγινωσκη ημων η καρδια οτι μειζων εστιν ο θεος της καρδιας ημων και γινωσκει παντα');
INSERT INTO `bible_verses_tr` VALUES ('7455', '62', '3', '21', 'αγαπητοι εαν η καρδια ημων μη καταγινωσκη ημων παρρησιαν εχομεν προς τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('7456', '62', '3', '22', 'και ο εαν αιτωμεν λαμβανομεν παρ αυτου οτι τας εντολας αυτου τηρουμεν και τα αρεστα ενωπιον αυτου ποιουμεν');
INSERT INTO `bible_verses_tr` VALUES ('7457', '62', '3', '23', 'και αυτη εστιν η εντολη αυτου ινα πιστευσωμεν τω ονοματι του υιου αυτου ιησου χριστου και αγαπωμεν αλληλους καθως εδωκεν εντολην ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7458', '62', '3', '24', 'και ο τηρων τας εντολας αυτου εν αυτω μενει και αυτος εν αυτω και εν τουτω γινωσκομεν οτι μενει εν ημιν εκ του πνευματος ου ημιν εδωκεν');
INSERT INTO `bible_verses_tr` VALUES ('7459', '62', '4', '1', 'αγαπητοι μη παντι πνευματι πιστευετε αλλα δοκιμαζετε τα πνευματα ει εκ του θεου εστιν οτι πολλοι ψευδοπροφηται εξεληλυθασιν εις τον κοσμον');
INSERT INTO `bible_verses_tr` VALUES ('7460', '62', '4', '2', 'εν τουτω γινωσκετε το πνευμα του θεου παν πνευμα ο ομολογει ιησουν χριστον εν σαρκι εληλυθοτα εκ του θεου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7461', '62', '4', '3', 'και παν πνευμα ο μη ομολογει τον ιησουν χριστον εν σαρκι εληλυθοτα εκ του θεου ουκ εστιν και τουτο εστιν το του αντιχριστου ο ακηκοατε οτι ερχεται και νυν εν τω κοσμω εστιν ηδη');
INSERT INTO `bible_verses_tr` VALUES ('7462', '62', '4', '4', 'υμεις εκ του θεου εστε τεκνια και νενικηκατε αυτους οτι μειζων εστιν ο εν υμιν η ο εν τω κοσμω');
INSERT INTO `bible_verses_tr` VALUES ('7463', '62', '4', '5', 'αυτοι εκ του κοσμου εισιν δια τουτο εκ του κοσμου λαλουσιν και ο κοσμος αυτων ακουει');
INSERT INTO `bible_verses_tr` VALUES ('7464', '62', '4', '6', 'ημεις εκ του θεου εσμεν ο γινωσκων τον θεον ακουει ημων ος ουκ εστιν εκ του θεου ουκ ακουει ημων εκ τουτου γινωσκομεν το πνευμα της αληθειας και το πνευμα της πλανης');
INSERT INTO `bible_verses_tr` VALUES ('7465', '62', '4', '7', 'αγαπητοι αγαπωμεν αλληλους οτι η αγαπη εκ του θεου εστιν και πας ο αγαπων εκ του θεου γεγεννηται και γινωσκει τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('7466', '62', '4', '8', 'ο μη αγαπων ουκ εγνω τον θεον οτι ο θεος αγαπη εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7467', '62', '4', '9', 'εν τουτω εφανερωθη η αγαπη του θεου εν ημιν οτι τον υιον αυτου τον μονογενη απεσταλκεν ο θεος εις τον κοσμον ινα ζησωμεν δι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7468', '62', '4', '10', 'εν τουτω εστιν η αγαπη ουχ οτι ημεις ηγαπησαμεν τον θεον αλλ οτι αυτος ηγαπησεν ημας και απεστειλεν τον υιον αυτου ιλασμον περι των αμαρτιων ημων');
INSERT INTO `bible_verses_tr` VALUES ('7469', '62', '4', '11', 'αγαπητοι ει ουτως ο θεος ηγαπησεν ημας και ημεις οφειλομεν αλληλους αγαπαν');
INSERT INTO `bible_verses_tr` VALUES ('7470', '62', '4', '12', 'θεον ουδεις πωποτε τεθεαται εαν αγαπωμεν αλληλους ο θεος εν ημιν μενει και η αγαπη αυτου τετελειωμενη εστιν εν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7471', '62', '4', '13', 'εν τουτω γινωσκομεν οτι εν αυτω μενομεν και αυτος εν ημιν οτι εκ του πνευματος αυτου δεδωκεν ημιν');
INSERT INTO `bible_verses_tr` VALUES ('7472', '62', '4', '14', 'και ημεις τεθεαμεθα και μαρτυρουμεν οτι ο πατηρ απεσταλκεν τον υιον σωτηρα του κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('7473', '62', '4', '15', 'ος αν ομολογηση οτι ιησους εστιν ο υιος του θεου ο θεος εν αυτω μενει και αυτος εν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('7474', '62', '4', '16', 'και ημεις εγνωκαμεν και πεπιστευκαμεν την αγαπην ην εχει ο θεος εν ημιν ο θεος αγαπη εστιν και ο μενων εν τη αγαπη εν τω θεω μενει και ο θεος εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7475', '62', '4', '17', 'εν τουτω τετελειωται η αγαπη μεθ ημων ινα παρρησιαν εχωμεν εν τη ημερα της κρισεως οτι καθως εκεινος εστιν και ημεις εσμεν εν τω κοσμω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('7476', '62', '4', '18', 'φοβος ουκ εστιν εν τη αγαπη αλλ η τελεια αγαπη εξω βαλλει τον φοβον οτι ο φοβος κολασιν εχει ο δε φοβουμενος ου τετελειωται εν τη αγαπη');
INSERT INTO `bible_verses_tr` VALUES ('7477', '62', '4', '19', 'ημεις αγαπωμεν αυτον οτι αυτος πρωτος ηγαπησεν ημας');
INSERT INTO `bible_verses_tr` VALUES ('7478', '62', '4', '20', 'εαν τις ειπη οτι αγαπω τον θεον και τον αδελφον αυτου μιση ψευστης εστιν ο γαρ μη αγαπων τον αδελφον αυτου ον εωρακεν τον θεον ον ουχ εωρακεν πως δυναται αγαπαν');
INSERT INTO `bible_verses_tr` VALUES ('7479', '62', '4', '21', 'και ταυτην την εντολην εχομεν απ αυτου ινα ο αγαπων τον θεον αγαπα και τον αδελφον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7480', '62', '5', '1', 'πας ο πιστευων οτι ιησους εστιν ο χριστος εκ του θεου γεγεννηται και πας ο αγαπων τον γεννησαντα αγαπα και τον γεγεννημενον εξ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7481', '62', '5', '2', 'εν τουτω γινωσκομεν οτι αγαπωμεν τα τεκνα του θεου οταν τον θεον αγαπωμεν και τας εντολας αυτου τηρωμεν');
INSERT INTO `bible_verses_tr` VALUES ('7482', '62', '5', '3', 'αυτη γαρ εστιν η αγαπη του θεου ινα τας εντολας αυτου τηρωμεν και αι εντολαι αυτου βαρειαι ουκ εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7483', '62', '5', '4', 'οτι παν το γεγεννημενον εκ του θεου νικα τον κοσμον και αυτη εστιν η νικη η νικησασα τον κοσμον η πιστις ημων');
INSERT INTO `bible_verses_tr` VALUES ('7484', '62', '5', '5', 'τις εστιν ο νικων τον κοσμον ει μη ο πιστευων οτι ιησους εστιν ο υιος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7485', '62', '5', '6', 'ουτος εστιν ο ελθων δι υδατος και αιματος ιησους ο χριστος ουκ εν τω υδατι μονον αλλ εν τω υδατι και τω αιματι και το πνευμα εστιν το μαρτυρουν οτι το πνευμα εστιν η αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('7486', '62', '5', '7', 'οτι τρεις εισιν οι μαρτυρουντες εν τω ουρανω ο πατηρ ο λογος και το αγιον πνευμα και ουτοι οι τρεις εν εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7487', '62', '5', '8', 'και τρεις εισιν οι μαρτυρουντες εν τη γη το πνευμα και το υδωρ και το αιμα και οι τρεις εις το εν εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7488', '62', '5', '9', 'ει την μαρτυριαν των ανθρωπων λαμβανομεν η μαρτυρια του θεου μειζων εστιν οτι αυτη εστιν η μαρτυρια του θεου ην μεμαρτυρηκεν περι του υιου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7489', '62', '5', '10', 'ο πιστευων εις τον υιον του θεου εχει την μαρτυριαν εν εαυτω ο μη πιστευων τω θεω ψευστην πεποιηκεν αυτον οτι ου πεπιστευκεν εις την μαρτυριαν ην μεμαρτυρηκεν ο θεος περι του υιου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7490', '62', '5', '11', 'και αυτη εστιν η μαρτυρια οτι ζωην αιωνιον εδωκεν ημιν ο θεος και αυτη η ζωη εν τω υιω αυτου εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7491', '62', '5', '12', 'ο εχων τον υιον εχει την ζωην ο μη εχων τον υιον του θεου την ζωην ουκ εχει');
INSERT INTO `bible_verses_tr` VALUES ('7492', '62', '5', '13', 'ταυτα εγραψα υμιν τοις πιστευουσιν εις το ονομα του υιου του θεου ινα ειδητε οτι ζωην εχετε αιωνιον και ινα πιστευητε εις το ονομα του υιου του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7493', '62', '5', '14', 'και αυτη εστιν η παρρησια ην εχομεν προς αυτον οτι εαν τι αιτωμεθα κατα το θελημα αυτου ακουει ημων');
INSERT INTO `bible_verses_tr` VALUES ('7494', '62', '5', '15', 'και εαν οιδαμεν οτι ακουει ημων ο αν αιτωμεθα οιδαμεν οτι εχομεν τα αιτηματα α ητηκαμεν παρ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7495', '62', '5', '16', 'εαν τις ιδη τον αδελφον αυτου αμαρτανοντα αμαρτιαν μη προς θανατον αιτησει και δωσει αυτω ζωην τοις αμαρτανουσιν μη προς θανατον εστιν αμαρτια προς θανατον ου περι εκεινης λεγω ινα ερωτηση');
INSERT INTO `bible_verses_tr` VALUES ('7496', '62', '5', '17', 'πασα αδικια αμαρτια εστιν και εστιν αμαρτια ου προς θανατον');
INSERT INTO `bible_verses_tr` VALUES ('7497', '62', '5', '18', 'οιδαμεν οτι πας ο γεγεννημενος εκ του θεου ουχ αμαρτανει αλλ ο γεννηθεις εκ του θεου τηρει εαυτον και ο πονηρος ουχ απτεται αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7498', '62', '5', '19', 'οιδαμεν οτι εκ του θεου εσμεν και ο κοσμος ολος εν τω πονηρω κειται');
INSERT INTO `bible_verses_tr` VALUES ('7499', '62', '5', '20', 'οιδαμεν δε οτι ο υιος του θεου ηκει και δεδωκεν ημιν διανοιαν ινα γινωσκωμεν τον αληθινον και εσμεν εν τω αληθινω εν τω υιω αυτου ιησου χριστω ουτος εστιν ο αληθινος θεος και η ζωη αιωνιος');
INSERT INTO `bible_verses_tr` VALUES ('7500', '62', '5', '21', 'τεκνια φυλαξατε εαυτους απο των ειδωλων αμην');
INSERT INTO `bible_verses_tr` VALUES ('7501', '63', '1', '1', 'ο πρεσβυτερος εκλεκτη κυρια και τοις τεκνοις αυτης ους εγω αγαπω εν αληθεια και ουκ εγω μονος αλλα και παντες οι εγνωκοτες την αληθειαν');
INSERT INTO `bible_verses_tr` VALUES ('7502', '63', '1', '2', 'δια την αληθειαν την μενουσαν εν ημιν και μεθ ημων εσται εις τον αιωνα');
INSERT INTO `bible_verses_tr` VALUES ('7503', '63', '1', '3', 'εσται μεθ  {VAR1: ημων } {VAR2: υμων } χαρις ελεος ειρηνη παρα θεου πατρος και παρα κυριου ιησου χριστου του υιου του πατρος εν αληθεια και αγαπη');
INSERT INTO `bible_verses_tr` VALUES ('7504', '63', '1', '4', 'εχαρην λιαν οτι ευρηκα εκ των τεκνων σου περιπατουντας εν αληθεια καθως εντολην ελαβομεν παρα του πατρος');
INSERT INTO `bible_verses_tr` VALUES ('7505', '63', '1', '5', 'και νυν ερωτω σε κυρια ουχ ως εντολην  {VAR1: γραφω } {VAR2: γραφων } σοι καινην αλλα ην ειχομεν απ αρχης ινα αγαπωμεν αλληλους');
INSERT INTO `bible_verses_tr` VALUES ('7506', '63', '1', '6', 'και αυτη εστιν η αγαπη ινα περιπατωμεν κατα τας εντολας αυτου αυτη εστιν η εντολη καθως ηκουσατε απ αρχης ινα εν αυτη περιπατητε');
INSERT INTO `bible_verses_tr` VALUES ('7507', '63', '1', '7', 'οτι πολλοι πλανοι εισηλθον εις τον κοσμον οι μη ομολογουντες ιησουν χριστον ερχομενον εν σαρκι ουτος εστιν ο πλανος και ο αντιχριστος');
INSERT INTO `bible_verses_tr` VALUES ('7508', '63', '1', '8', 'βλεπετε εαυτους ινα μη απολεσωμεν α ειργασαμεθα αλλα μισθον πληρη απολαβωμεν');
INSERT INTO `bible_verses_tr` VALUES ('7509', '63', '1', '9', 'πας ο παραβαινων και μη μενων εν τη διδαχη του χριστου θεον ουκ εχει ο μενων εν τη διδαχη του χριστου ουτος και τον πατερα και τον υιον εχει');
INSERT INTO `bible_verses_tr` VALUES ('7510', '63', '1', '10', 'ει τις ερχεται προς υμας και ταυτην την διδαχην ου φερει μη λαμβανετε αυτον εις οικιαν και χαιρειν αυτω μη λεγετε');
INSERT INTO `bible_verses_tr` VALUES ('7511', '63', '1', '11', 'ο γαρ λεγων αυτω χαιρειν κοινωνει τοις εργοις αυτου τοις πονηροις');
INSERT INTO `bible_verses_tr` VALUES ('7512', '63', '1', '12', 'πολλα εχων υμιν γραφειν ουκ ηβουληθην δια χαρτου και μελανος αλλα ελπιζω ελθειν προς υμας και στομα προς στομα λαλησαι ινα η χαρα ημων η πεπληρωμενη');
INSERT INTO `bible_verses_tr` VALUES ('7513', '63', '1', '13', 'ασπαζεται σε τα τεκνα της αδελφης σου της εκλεκτης αμην');
INSERT INTO `bible_verses_tr` VALUES ('7514', '64', '1', '1', 'ο πρεσβυτερος γαιω τω αγαπητω ον εγω αγαπω εν αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('7515', '64', '1', '2', 'αγαπητε περι παντων ευχομαι σε ευοδουσθαι και υγιαινειν καθως ευοδουται σου η ψυχη');
INSERT INTO `bible_verses_tr` VALUES ('7516', '64', '1', '3', 'εχαρην γαρ λιαν ερχομενων αδελφων και μαρτυρουντων σου τη αληθεια καθως συ εν αληθεια περιπατεις');
INSERT INTO `bible_verses_tr` VALUES ('7517', '64', '1', '4', 'μειζοτεραν τουτων ουκ εχω χαραν ινα ακουω τα εμα τεκνα εν αληθεια περιπατουντα');
INSERT INTO `bible_verses_tr` VALUES ('7518', '64', '1', '5', 'αγαπητε πιστον ποιεις ο εαν εργαση εις τους αδελφους και εις τους ξενους');
INSERT INTO `bible_verses_tr` VALUES ('7519', '64', '1', '6', 'οι εμαρτυρησαν σου τη αγαπη ενωπιον εκκλησιας ους καλως ποιησεις προπεμψας αξιως του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7520', '64', '1', '7', 'υπερ γαρ του ονοματος  {VAR2: αυτου } εξηλθον μηδεν λαμβανοντες απο των εθνων');
INSERT INTO `bible_verses_tr` VALUES ('7521', '64', '1', '8', 'ημεις ουν οφειλομεν απολαμβανειν τους τοιουτους ινα συνεργοι γινωμεθα τη αληθεια');
INSERT INTO `bible_verses_tr` VALUES ('7522', '64', '1', '9', 'εγραψα τη εκκλησια αλλ ο φιλοπρωτευων αυτων διοτρεφης ουκ επιδεχεται ημας');
INSERT INTO `bible_verses_tr` VALUES ('7523', '64', '1', '10', 'δια τουτο εαν ελθω υπομνησω αυτου τα εργα α ποιει λογοις πονηροις φλυαρων ημας και μη αρκουμενος επι τουτοις ουτε αυτος επιδεχεται τους αδελφους και τους βουλομενους κωλυει και εκ της εκκλησιας εκβαλλει');
INSERT INTO `bible_verses_tr` VALUES ('7524', '64', '1', '11', 'αγαπητε μη μιμου το κακον αλλα το αγαθον ο αγαθοποιων εκ του θεου εστιν ο δε κακοποιων ουχ εωρακεν τον θεον');
INSERT INTO `bible_verses_tr` VALUES ('7525', '64', '1', '12', 'δημητριω μεμαρτυρηται υπο παντων και υπ αυτης της αληθειας και ημεις δε μαρτυρουμεν και οιδατε οτι η μαρτυρια ημων αληθης εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7526', '64', '1', '13', 'πολλα ειχον γραφειν αλλ ου θελω δια μελανος και καλαμου σοι γραψαι');
INSERT INTO `bible_verses_tr` VALUES ('7527', '64', '1', '14', 'ελπιζω δε ευθεως ιδειν σε και στομα προς στομα λαλησομεν');
INSERT INTO `bible_verses_tr` VALUES ('7528', '64', '1', '15', 'ειρηνη σοι ασπαζονται σε οι φιλοι ασπαζου τους φιλους κατ ονομα');
INSERT INTO `bible_verses_tr` VALUES ('7529', '65', '1', '1', 'ιουδας ιησου χριστου δουλος αδελφος δε ιακωβου τοις εν θεω πατρι ηγιασμενοις και ιησου χριστω τετηρημενοις κλητοις');
INSERT INTO `bible_verses_tr` VALUES ('7530', '65', '1', '2', 'ελεος υμιν και ειρηνη και αγαπη πληθυνθειη');
INSERT INTO `bible_verses_tr` VALUES ('7531', '65', '1', '3', 'αγαπητοι πασαν σπουδην ποιουμενος γραφειν υμιν περι της κοινης σωτηριας αναγκην εσχον γραψαι υμιν παρακαλων επαγωνιζεσθαι τη απαξ παραδοθειση τοις αγιοις πιστει');
INSERT INTO `bible_verses_tr` VALUES ('7532', '65', '1', '4', 'παρεισεδυσαν γαρ τινες ανθρωποι οι παλαι προγεγραμμενοι εις τουτο το κριμα ασεβεις την του θεου ημων χαριν μετατιθεντες εις ασελγειαν και τον μονον δεσποτην θεον και κυριον ημων ιησουν χριστον αρνουμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7533', '65', '1', '5', 'υπομνησαι δε υμας βουλομαι ειδοτας υμας απαξ τουτο οτι ο κυριος λαον εκ γης αιγυπτου σωσας το δευτερον τους μη πιστευσαντας απωλεσεν');
INSERT INTO `bible_verses_tr` VALUES ('7534', '65', '1', '6', 'αγγελους τε τους μη τηρησαντας την εαυτων αρχην αλλα απολιποντας το ιδιον οικητηριον εις κρισιν μεγαλης ημερας δεσμοις αιδιοις υπο ζοφον τετηρηκεν');
INSERT INTO `bible_verses_tr` VALUES ('7535', '65', '1', '7', 'ως σοδομα και γομορρα και αι περι αυτας πολεις τον ομοιον τουτοις τροπον εκπορνευσασαι και απελθουσαι οπισω σαρκος ετερας προκεινται δειγμα πυρος αιωνιου δικην υπεχουσαι');
INSERT INTO `bible_verses_tr` VALUES ('7536', '65', '1', '8', 'ομοιως μεντοι και ουτοι ενυπνιαζομενοι σαρκα μεν μιαινουσιν κυριοτητα δε αθετουσιν δοξας δε βλασφημουσιν');
INSERT INTO `bible_verses_tr` VALUES ('7537', '65', '1', '9', 'ο δε μιχαηλ ο αρχαγγελος οτε τω διαβολω διακρινομενος διελεγετο περι του μωσεως σωματος ουκ ετολμησεν κρισιν επενεγκειν βλασφημιας αλλ ειπεν επιτιμησαι σοι κυριος');
INSERT INTO `bible_verses_tr` VALUES ('7538', '65', '1', '10', 'ουτοι δε οσα μεν ουκ οιδασιν βλασφημουσιν οσα δε φυσικως ως τα αλογα ζωα επιστανται εν τουτοις φθειρονται');
INSERT INTO `bible_verses_tr` VALUES ('7539', '65', '1', '11', 'ουαι αυτοις οτι τη οδω του καιν επορευθησαν και τη πλανη του βαλααμ μισθου εξεχυθησαν και τη αντιλογια του κορε απωλοντο');
INSERT INTO `bible_verses_tr` VALUES ('7540', '65', '1', '12', 'ουτοι εισιν εν ταις αγαπαις υμων σπιλαδες συνευωχουμενοι  {VAR2: υμιν } αφοβως εαυτους ποιμαινοντες νεφελαι ανυδροι υπο ανεμων περιφερομεναι δενδρα φθινοπωρινα ακαρπα δις αποθανοντα εκριζωθεντα');
INSERT INTO `bible_verses_tr` VALUES ('7541', '65', '1', '13', 'κυματα αγρια θαλασσης επαφριζοντα τας εαυτων αισχυνας αστερες πλανηται οις ο ζοφος του σκοτους εις τον αιωνα τετηρηται');
INSERT INTO `bible_verses_tr` VALUES ('7542', '65', '1', '14', 'προεφητευσεν δε και τουτοις εβδομος απο αδαμ ενωχ λεγων ιδου ηλθεν κυριος εν μυριασιν αγιαις αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7543', '65', '1', '15', 'ποιησαι κρισιν κατα παντων και εξελεγξαι παντας τους ασεβεις αυτων περι παντων των εργων ασεβειας αυτων ων ησεβησαν και περι παντων των σκληρων ων ελαλησαν κατ αυτου αμαρτωλοι ασεβεις');
INSERT INTO `bible_verses_tr` VALUES ('7544', '65', '1', '16', 'ουτοι εισιν γογγυσται μεμψιμοιροι κατα τας επιθυμιας αυτων πορευομενοι και το στομα αυτων λαλει υπερογκα θαυμαζοντες προσωπα ωφελειας χαριν');
INSERT INTO `bible_verses_tr` VALUES ('7545', '65', '1', '17', 'υμεις δε αγαπητοι μνησθητε των ρηματων των προειρημενων υπο των αποστολων του κυριου ημων ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7546', '65', '1', '18', 'οτι ελεγον υμιν οτι εν εσχατω χρονω εσονται εμπαικται κατα τας εαυτων επιθυμιας πορευομενοι των ασεβειων');
INSERT INTO `bible_verses_tr` VALUES ('7547', '65', '1', '19', 'ουτοι εισιν οι αποδιοριζοντες  {VAR2: εαυτους } ψυχικοι πνευμα μη εχοντες');
INSERT INTO `bible_verses_tr` VALUES ('7548', '65', '1', '20', 'υμεις δε αγαπητοι τη αγιωτατη υμων πιστει εποικοδομουντες εαυτους εν πνευματι αγιω προσευχομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7549', '65', '1', '21', 'εαυτους εν αγαπη θεου τηρησατε προσδεχομενοι το ελεος του κυριου ημων ιησου χριστου εις ζωην αιωνιον');
INSERT INTO `bible_verses_tr` VALUES ('7550', '65', '1', '22', 'και ους μεν ελεειτε διακρινομενοι');
INSERT INTO `bible_verses_tr` VALUES ('7551', '65', '1', '23', 'ους δε εν φοβω σωζετε εκ του πυρος αρπαζοντες μισουντες και τον απο της σαρκος εσπιλωμενον χιτωνα');
INSERT INTO `bible_verses_tr` VALUES ('7552', '65', '1', '24', 'τω δε δυναμενω φυλαξαι  {VAR1: αυτους } {VAR2: υμας } απταιστους και στησαι κατενωπιον της δοξης αυτου αμωμους εν αγαλλιασει');
INSERT INTO `bible_verses_tr` VALUES ('7553', '65', '1', '25', 'μονω σοφω θεω σωτηρι ημων δοξα και μεγαλωσυνη κρατος και εξουσια και νυν και εις παντας τους αιωνας αμην');
INSERT INTO `bible_verses_tr` VALUES ('7554', '66', '1', '1', 'αποκαλυψις ιησου χριστου ην εδωκεν αυτω ο θεος δειξαι τοις δουλοις αυτου α δει γενεσθαι εν ταχει και εσημανεν αποστειλας δια του αγγελου αυτου τω δουλω αυτου ιωαννη');
INSERT INTO `bible_verses_tr` VALUES ('7555', '66', '1', '2', 'ος εμαρτυρησεν τον λογον του θεου και την μαρτυριαν ιησου χριστου οσα τε ειδεν');
INSERT INTO `bible_verses_tr` VALUES ('7556', '66', '1', '3', 'μακαριος ο αναγινωσκων και οι ακουοντες τους λογους της προφητειας και τηρουντες τα εν αυτη γεγραμμενα ο γαρ καιρος εγγυς');
INSERT INTO `bible_verses_tr` VALUES ('7557', '66', '1', '4', 'ιωαννης ταις επτα εκκλησιαις ταις εν τη ασια χαρις υμιν και ειρηνη απο του ο ων και ο ην και ο ερχομενος και απο των επτα πνευματων α εστιν ενωπιον του θρονου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7558', '66', '1', '5', 'και απο ιησου χριστου ο μαρτυς ο πιστος ο πρωτοτοκος εκ των νεκρων και ο αρχων των βασιλεων της γης τω αγαπησαντι ημας και λουσαντι ημας απο των αμαρτιων ημων εν τω αιματι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7559', '66', '1', '6', 'και εποιησεν ημας βασιλεις και ιερεις τω θεω και πατρι αυτου αυτω η δοξα και το κρατος εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('7560', '66', '1', '7', 'ιδου ερχεται μετα των νεφελων και οψεται αυτον πας οφθαλμος και οιτινες αυτον εξεκεντησαν και κοψονται επ αυτον πασαι αι φυλαι της γης ναι αμην');
INSERT INTO `bible_verses_tr` VALUES ('7561', '66', '1', '8', 'εγω ειμι το α και το ω αρχη και τελος λεγει ο κυριος ο ων και ο ην και ο ερχομενος ο παντοκρατωρ');
INSERT INTO `bible_verses_tr` VALUES ('7562', '66', '1', '9', 'εγω ιωαννης ο και αδελφος υμων και συγκοινωνος εν τη θλιψει και εν τη βασιλεια και υπομονη ιησου χριστου εγενομην εν τη νησω τη καλουμενη πατμω δια τον λογον του θεου και δια την μαρτυριαν ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7563', '66', '1', '10', 'εγενομην εν πνευματι εν τη κυριακη ημερα και ηκουσα οπισω μου φωνην μεγαλην ως σαλπιγγος');
INSERT INTO `bible_verses_tr` VALUES ('7564', '66', '1', '11', 'λεγουσης εγω ειμι το α και το ω ο πρωτος και ο εσχατος και ο βλεπεις γραψον εις βιβλιον και πεμψον ταις  {VAR2: επτα } εκκλησιαις ταις εν ασια εις εφεσον και εις σμυρναν και εις περγαμον και εις θυατειρα και εις σαρδεις και εις φιλαδελφειαν και εις λαοδικειαν');
INSERT INTO `bible_verses_tr` VALUES ('7565', '66', '1', '12', 'και επεστρεψα βλεπειν την φωνην ητις ελαλησεν μετ εμου και επιστρεψας ειδον επτα λυχνιας χρυσας');
INSERT INTO `bible_verses_tr` VALUES ('7566', '66', '1', '13', 'και εν μεσω των επτα λυχνιων ομοιον υιω ανθρωπου ενδεδυμενον ποδηρη και περιεζωσμενον προς τοις μαστοις ζωνην χρυσην');
INSERT INTO `bible_verses_tr` VALUES ('7567', '66', '1', '14', 'η δε κεφαλη αυτου και αι τριχες λευκαι ωσει εριον λευκον ως χιων και οι οφθαλμοι αυτου ως φλοξ πυρος');
INSERT INTO `bible_verses_tr` VALUES ('7568', '66', '1', '15', 'και οι ποδες αυτου ομοιοι χαλκολιβανω ως εν καμινω πεπυρωμενοι και η φωνη αυτου ως φωνη υδατων πολλων');
INSERT INTO `bible_verses_tr` VALUES ('7569', '66', '1', '16', 'και εχων εν τη δεξια αυτου χειρι αστερας επτα και εκ του στοματος αυτου ρομφαια διστομος οξεια εκπορευομενη και η οψις αυτου ως ο ηλιος φαινει εν τη δυναμει αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7570', '66', '1', '17', 'και οτε ειδον αυτον επεσα προς τους ποδας αυτου ως νεκρος και επεθηκεν την δεξιαν αυτου χειρα επ εμε λεγων μοι μη φοβου εγω ειμι ο πρωτος και ο εσχατος');
INSERT INTO `bible_verses_tr` VALUES ('7571', '66', '1', '18', 'και ο ζων και εγενομην νεκρος και ιδου ζων ειμι εις τους αιωνας των αιωνων αμην και εχω τας κλεις του αδου και του θανατου');
INSERT INTO `bible_verses_tr` VALUES ('7572', '66', '1', '19', 'γραψον α ειδες και α εισιν και α μελλει γινεσθαι μετα ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('7573', '66', '1', '20', 'το μυστηριον των επτα αστερων ων ειδες επι της δεξιας μου και τας επτα λυχνιας τας χρυσας οι επτα αστερες αγγελοι των επτα εκκλησιων εισιν και αι επτα λυχνιαι ας ειδες επτα εκκλησιαι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7574', '66', '2', '1', 'τω αγγελω της εφεσινης εκκλησιας γραψον ταδε λεγει ο κρατων τους επτα αστερας εν τη δεξια αυτου ο περιπατων εν μεσω των επτα λυχνιων των χρυσων');
INSERT INTO `bible_verses_tr` VALUES ('7575', '66', '2', '2', 'οιδα τα εργα σου και τον κοπον σου και την υπομονην σου και οτι ου δυνη βαστασαι κακους και επειρασω τους φασκοντας ειναι αποστολους και ουκ εισιν και ευρες αυτους ψευδεις');
INSERT INTO `bible_verses_tr` VALUES ('7576', '66', '2', '3', 'και εβαστασας και υπομονην εχεις και δια το ονομα μου κεκοπιακας και ου κεκμηκας');
INSERT INTO `bible_verses_tr` VALUES ('7577', '66', '2', '4', 'αλλ εχω κατα σου οτι την αγαπην σου την πρωτην αφηκας');
INSERT INTO `bible_verses_tr` VALUES ('7578', '66', '2', '5', 'μνημονευε ουν ποθεν εκπεπτωκας και μετανοησον και τα πρωτα εργα ποιησον ει δε μη ερχομαι σοι  {VAR1: ταχει } {VAR2: ταχυ } και κινησω την λυχνιαν σου εκ του τοπου αυτης εαν μη μετανοησης');
INSERT INTO `bible_verses_tr` VALUES ('7579', '66', '2', '6', 'αλλα τουτο εχεις οτι μισεις τα εργα των νικολαιτων α καγω μισω');
INSERT INTO `bible_verses_tr` VALUES ('7580', '66', '2', '7', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις τω νικωντι δωσω αυτω φαγειν εκ του ξυλου της ζωης ο εστιν εν μεσω του παραδεισου του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7581', '66', '2', '8', 'και τω αγγελω της εκκλησιας σμυρναιων γραψον ταδε λεγει ο πρωτος και ο εσχατος ος εγενετο νεκρος και εζησεν');
INSERT INTO `bible_verses_tr` VALUES ('7582', '66', '2', '9', 'οιδα σου τα εργα και την θλιψιν και την πτωχειαν πλουσιος δε ει και την βλασφημιαν των λεγοντων ιουδαιους ειναι εαυτους και ουκ εισιν αλλα συναγωγη του σατανα');
INSERT INTO `bible_verses_tr` VALUES ('7583', '66', '2', '10', 'μηδεν φοβου α μελλεις πασχειν ιδου μελλει βαλειν εξ υμων ο διαβολος εις φυλακην ινα πειρασθητε και εξετε θλιψιν ημερων δεκα γινου πιστος αχρι θανατου και δωσω σοι τον στεφανον της ζωης');
INSERT INTO `bible_verses_tr` VALUES ('7584', '66', '2', '11', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις ο νικων ου μη αδικηθη εκ του θανατου του δευτερου');
INSERT INTO `bible_verses_tr` VALUES ('7585', '66', '2', '12', 'και τω αγγελω της εν περγαμω εκκλησιας γραψον ταδε λεγει ο εχων την ρομφαιαν την διστομον την οξειαν');
INSERT INTO `bible_verses_tr` VALUES ('7586', '66', '2', '13', 'οιδα τα εργα σου και που κατοικεις οπου ο θρονος του σατανα και κρατεις το ονομα μου και ουκ ηρνησω την πιστιν μου και εν ταις ημεραις εν αις αντιπας ο μαρτυς μου ο πιστος ος απεκτανθη παρ υμιν οπου κατοικει ο σατανας');
INSERT INTO `bible_verses_tr` VALUES ('7587', '66', '2', '14', 'αλλ εχω κατα σου ολιγα οτι εχεις εκει κρατουντας την διδαχην βαλααμ ος εδιδασκεν  {VAR1: εν τω } {VAR2: τον } βαλακ βαλειν σκανδαλον ενωπιον των υιων ισραηλ φαγειν ειδωλοθυτα και πορνευσαι');
INSERT INTO `bible_verses_tr` VALUES ('7588', '66', '2', '15', 'ουτως εχεις και συ κρατουντας την διδαχην των νικολαιτων ο μισω');
INSERT INTO `bible_verses_tr` VALUES ('7589', '66', '2', '16', 'μετανοησον ει δε μη ερχομαι σοι ταχυ και πολεμησω μετ αυτων εν τη ρομφαια του στοματος μου');
INSERT INTO `bible_verses_tr` VALUES ('7590', '66', '2', '17', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις τω νικωντι δωσω αυτω φαγειν απο του μαννα του κεκρυμμενου και δωσω αυτω ψηφον λευκην και επι την ψηφον ονομα καινον γεγραμμενον ο ουδεις εγνω ει μη ο λαμβανων');
INSERT INTO `bible_verses_tr` VALUES ('7591', '66', '2', '18', 'και τω αγγελω της εν θυατειροις εκκλησιας γραψον ταδε λεγει ο υιος του θεου ο εχων τους οφθαλμους αυτου ως φλογα πυρος και οι ποδες αυτου ομοιοι χαλκολιβανω');
INSERT INTO `bible_verses_tr` VALUES ('7592', '66', '2', '19', 'οιδα σου τα εργα και την αγαπην και την διακονιαν και την πιστιν και την υπομονην σου και τα εργα σου και τα εσχατα πλειονα των πρωτων');
INSERT INTO `bible_verses_tr` VALUES ('7593', '66', '2', '20', 'αλλ εχω κατα σου ολιγα οτι εας την γυναικα ιεζαβηλ την λεγουσαν εαυτην προφητιν διδασκειν και πλανασθαι εμους δουλους πορνευσαι και ειδωλοθυτα φαγειν');
INSERT INTO `bible_verses_tr` VALUES ('7594', '66', '2', '21', 'και εδωκα αυτη χρονον ινα μετανοηση εκ της πορνειας αυτης και ου μετενοησεν');
INSERT INTO `bible_verses_tr` VALUES ('7595', '66', '2', '22', 'ιδου εγω βαλλω αυτην εις κλινην και τους μοιχευοντας μετ αυτης εις θλιψιν μεγαλην εαν μη μετανοησωσιν εκ των εργων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7596', '66', '2', '23', 'και τα τεκνα αυτης αποκτενω εν θανατω και γνωσονται πασαι αι εκκλησιαι οτι εγω ειμι ο ερευνων νεφρους και καρδιας και δωσω υμιν εκαστω κατα τα εργα υμων');
INSERT INTO `bible_verses_tr` VALUES ('7597', '66', '2', '24', 'υμιν δε λεγω και λοιποις τοις εν θυατειροις οσοι ουκ εχουσιν την διδαχην ταυτην και οιτινες ουκ εγνωσαν τα βαθη του σατανα ως λεγουσιν ου βαλω εφ υμας αλλο βαρος');
INSERT INTO `bible_verses_tr` VALUES ('7598', '66', '2', '25', 'πλην ο εχετε κρατησατε αχρις ου αν ηξω');
INSERT INTO `bible_verses_tr` VALUES ('7599', '66', '2', '26', 'και ο νικων και ο τηρων αχρι τελους τα εργα μου δωσω αυτω εξουσιαν επι των εθνων');
INSERT INTO `bible_verses_tr` VALUES ('7600', '66', '2', '27', 'και ποιμανει αυτους εν ραβδω σιδηρα ως τα σκευη τα κεραμικα συντριβεται ως καγω ειληφα παρα του πατρος μου');
INSERT INTO `bible_verses_tr` VALUES ('7601', '66', '2', '28', 'και δωσω αυτω τον αστερα τον πρωινον');
INSERT INTO `bible_verses_tr` VALUES ('7602', '66', '2', '29', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις');
INSERT INTO `bible_verses_tr` VALUES ('7603', '66', '3', '1', 'και τω αγγελω της εν σαρδεσιν εκκλησιας γραψον ταδε λεγει ο εχων τα  {VAR2: επτα } πνευματα του θεου και τους επτα αστερας οιδα σου τα εργα οτι το ονομα εχεις οτι ζης και νεκρος ει');
INSERT INTO `bible_verses_tr` VALUES ('7604', '66', '3', '2', 'γινου γρηγορων και στηριξον τα λοιπα α μελλει αποθανειν ου γαρ ευρηκα σου τα εργα πεπληρωμενα ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7605', '66', '3', '3', 'μνημονευε ουν πως ειληφας και ηκουσας και τηρει και μετανοησον εαν ουν μη γρηγορησης ηξω επι σε ως κλεπτης και ου μη γνως ποιαν ωραν ηξω επι σε');
INSERT INTO `bible_verses_tr` VALUES ('7606', '66', '3', '4', 'εχεις ολιγα ονοματα και εν σαρδεσιν α ουκ εμολυναν τα ιματια αυτων και περιπατησουσιν μετ εμου εν λευκοις οτι αξιοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7607', '66', '3', '5', 'ο νικων ουτος περιβαλειται εν ιματιοις λευκοις και ου μη εξαλειψω το ονομα αυτου εκ της βιβλου της ζωης και εξομολογησομαι το ονομα αυτου ενωπιον του πατρος μου και ενωπιον των αγγελων αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7608', '66', '3', '6', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις');
INSERT INTO `bible_verses_tr` VALUES ('7609', '66', '3', '7', 'και τω αγγελω της εν φιλαδελφεια εκκλησιας γραψον ταδε λεγει ο αγιος ο αληθινος ο εχων την κλειδα του δαβιδ ο ανοιγων και ουδεις κλειει και κλειει και ουδεις ανοιγει');
INSERT INTO `bible_verses_tr` VALUES ('7610', '66', '3', '8', 'οιδα σου τα εργα ιδου δεδωκα ενωπιον σου θυραν ανεωγμενην και ουδεις δυναται κλεισαι αυτην οτι μικραν εχεις δυναμιν και ετηρησας μου τον λογον και ουκ ηρνησω το ονομα μου');
INSERT INTO `bible_verses_tr` VALUES ('7611', '66', '3', '9', 'ιδου διδωμι εκ της συναγωγης του σατανα των λεγοντων εαυτους ιουδαιους ειναι και ουκ εισιν αλλα ψευδονται ιδου ποιησω αυτους ινα ηξωσιν και προσκυνησωσιν ενωπιον των ποδων σου και γνωσιν οτι εγω ηγαπησα σε');
INSERT INTO `bible_verses_tr` VALUES ('7612', '66', '3', '10', 'οτι ετηρησας τον λογον της υπομονης μου καγω σε τηρησω εκ της ωρας του πειρασμου της μελλουσης ερχεσθαι επι της οικουμενης ολης πειρασαι τους κατοικουντας επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7613', '66', '3', '11', 'ιδου ερχομαι ταχυ κρατει ο εχεις ινα μηδεις λαβη τον στεφανον σου');
INSERT INTO `bible_verses_tr` VALUES ('7614', '66', '3', '12', 'ο νικων ποιησω αυτον στυλον εν τω ναω του θεου μου και εξω ου μη εξελθη ετι και γραψω επ αυτον το ονομα του θεου μου και το ονομα της πολεως του θεου μου της καινης ιερουσαλημ η  {VAR1: καταβαινουσα } {VAR2: καταβαινει } εκ του ουρανου απο του θεου μου και το ονομα μου το καινον');
INSERT INTO `bible_verses_tr` VALUES ('7615', '66', '3', '13', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις');
INSERT INTO `bible_verses_tr` VALUES ('7616', '66', '3', '14', 'και τω αγγελω της εκκλησιας λαοδικεων γραψον ταδε λεγει ο αμην ο μαρτυς ο πιστος και αληθινος η αρχη της κτισεως του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7617', '66', '3', '15', 'οιδα σου τα εργα οτι ουτε ψυχρος ει ουτε ζεστος οφελον ψυχρος ειης η ζεστος');
INSERT INTO `bible_verses_tr` VALUES ('7618', '66', '3', '16', 'ουτως οτι χλιαρος ει και ουτε ψυχρος ουτε ζεστος μελλω σε εμεσαι εκ του στοματος μου');
INSERT INTO `bible_verses_tr` VALUES ('7619', '66', '3', '17', 'οτι λεγεις οτι πλουσιος ειμι και πεπλουτηκα και ουδενος χρειαν εχω και ουκ οιδας οτι συ ει ο ταλαιπωρος και ελεεινος και πτωχος και τυφλος και γυμνος');
INSERT INTO `bible_verses_tr` VALUES ('7620', '66', '3', '18', 'συμβουλευω σοι αγορασαι παρ εμου χρυσιον πεπυρωμενον εκ πυρος ινα πλουτησης και ιματια λευκα ινα περιβαλη και μη φανερωθη η αισχυνη της γυμνοτητος σου και κολλουριον εγχρισον τους οφθαλμους σου ινα βλεπης');
INSERT INTO `bible_verses_tr` VALUES ('7621', '66', '3', '19', 'εγω οσους εαν φιλω ελεγχω και παιδευω ζηλωσον ουν και μετανοησον');
INSERT INTO `bible_verses_tr` VALUES ('7622', '66', '3', '20', 'ιδου εστηκα επι την θυραν και κρουω εαν τις ακουση της φωνης μου και ανοιξη την θυραν εισελευσομαι προς αυτον και δειπνησω μετ αυτου και αυτος μετ εμου');
INSERT INTO `bible_verses_tr` VALUES ('7623', '66', '3', '21', 'ο νικων δωσω αυτω καθισαι μετ εμου εν τω θρονω μου ως καγω ενικησα και εκαθισα μετα του πατρος μου εν τω θρονω αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7624', '66', '3', '22', 'ο εχων ους ακουσατω τι το πνευμα λεγει ταις εκκλησιαις');
INSERT INTO `bible_verses_tr` VALUES ('7625', '66', '4', '1', 'μετα ταυτα ειδον και ιδου θυρα ηνεωγμενη εν τω ουρανω και η φωνη η πρωτη ην ηκουσα ως σαλπιγγος λαλουσης μετ εμου λεγουσα αναβα ωδε και δειξω σοι α δει γενεσθαι μετα ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('7626', '66', '4', '2', 'και ευθεως εγενομην εν πνευματι και ιδου θρονος εκειτο εν τω ουρανω και επι του θρονου καθημενος');
INSERT INTO `bible_verses_tr` VALUES ('7627', '66', '4', '3', 'και ο καθημενος ην ομοιος ορασει λιθω ιασπιδι και σαρδινω και ιρις κυκλοθεν του θρονου  {VAR1: ομοιος } {VAR2: ομοια } ορασει σμαραγδινω');
INSERT INTO `bible_verses_tr` VALUES ('7628', '66', '4', '4', 'και κυκλοθεν του θρονου θρονοι εικοσι και τεσσαρες και επι τους θρονους ειδον τους εικοσι και τεσσαρας πρεσβυτερους καθημενους περιβεβλημενους εν ιματιοις λευκοις και εσχον επι τας κεφαλας αυτων στεφανους χρυσους');
INSERT INTO `bible_verses_tr` VALUES ('7629', '66', '4', '5', 'και εκ του θρονου εκπορευονται αστραπαι και βρονται και φωναι και επτα λαμπαδες πυρος καιομεναι ενωπιον του θρονου αι εισιν τα επτα πνευματα του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7630', '66', '4', '6', 'και ενωπιον του θρονου θαλασσα υαλινη ομοια κρυσταλλω και εν μεσω του θρονου και κυκλω του θρονου τεσσαρα ζωα γεμοντα οφθαλμων εμπροσθεν και οπισθεν');
INSERT INTO `bible_verses_tr` VALUES ('7631', '66', '4', '7', 'και το ζωον το πρωτον ομοιον λεοντι και το δευτερον ζωον ομοιον μοσχω και το τριτον ζωον εχον το προσωπον ως ανθρωπος και το τεταρτον ζωον ομοιον αετω πετωμενω');
INSERT INTO `bible_verses_tr` VALUES ('7632', '66', '4', '8', 'και τεσσαρα ζωα εν καθ εαυτο ειχον ανα πτερυγας εξ κυκλοθεν και εσωθεν γεμοντα οφθαλμων και αναπαυσιν ουκ εχουσιν ημερας και νυκτος λεγοντα αγιος αγιος αγιος κυριος ο θεος ο παντοκρατωρ ο ην και ο ων και ο ερχομενος');
INSERT INTO `bible_verses_tr` VALUES ('7633', '66', '4', '9', 'και οταν δωσουσιν τα ζωα δοξαν και τιμην και ευχαριστιαν τω καθημενω επι του θρονου τω ζωντι εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7634', '66', '4', '10', 'πεσουνται οι εικοσι και τεσσαρες πρεσβυτεροι ενωπιον του καθημενου επι του θρονου και προσκυνουσιν τω ζωντι εις τους αιωνας των αιωνων και βαλλουσιν τους στεφανους αυτων ενωπιον του θρονου λεγοντες');
INSERT INTO `bible_verses_tr` VALUES ('7635', '66', '4', '11', 'αξιος ει κυριε λαβειν την δοξαν και την τιμην και την δυναμιν οτι συ εκτισας τα παντα και δια το θελημα σου εισιν και εκτισθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7636', '66', '5', '1', 'και ειδον επι την δεξιαν του καθημενου επι του θρονου βιβλιον γεγραμμενον εσωθεν και οπισθεν κατεσφραγισμενον σφραγισιν επτα');
INSERT INTO `bible_verses_tr` VALUES ('7637', '66', '5', '2', 'και ειδον αγγελον ισχυρον κηρυσσοντα φωνη μεγαλη τις εστιν αξιος ανοιξαι το βιβλιον και λυσαι τας σφραγιδας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7638', '66', '5', '3', 'και ουδεις ηδυνατο εν τω ουρανω ουδε επι της γης ουδε υποκατω της γης ανοιξαι το βιβλιον ουδε βλεπειν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('7639', '66', '5', '4', 'και εγω εκλαιον πολλα οτι ουδεις αξιος ευρεθη ανοιξαι και αναγνωναι το βιβλιον ουτε βλεπειν αυτο');
INSERT INTO `bible_verses_tr` VALUES ('7640', '66', '5', '5', 'και εις εκ των πρεσβυτερων λεγει μοι μη κλαιε ιδου ενικησεν ο λεων ο ων εκ της φυλης ιουδα η ριζα δαβιδ ανοιξαι το βιβλιον και λυσαι τας επτα σφραγιδας αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7641', '66', '5', '6', 'και ειδον και ιδου εν μεσω του θρονου και των τεσσαρων ζωων και εν μεσω των πρεσβυτερων αρνιον εστηκος ως εσφαγμενον εχον κερατα επτα και οφθαλμους επτα οι εισιν τα επτα του θεου πνευματα τα απεσταλμενα εις πασαν την γην');
INSERT INTO `bible_verses_tr` VALUES ('7642', '66', '5', '7', 'και ηλθεν και ειληφεν το βιβλιον εκ της δεξιας του καθημενου επι του θρονου');
INSERT INTO `bible_verses_tr` VALUES ('7643', '66', '5', '8', 'και οτε ελαβεν το βιβλιον τα τεσσαρα ζωα και οι εικοσιτεσσαρες πρεσβυτεροι επεσον ενωπιον του αρνιου εχοντες εκαστος κιθαρας και φιαλας χρυσας γεμουσας θυμιαματων αι εισιν αι προσευχαι των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('7644', '66', '5', '9', 'και αδουσιν ωδην καινην λεγοντες αξιος ει λαβειν το βιβλιον και ανοιξαι τας σφραγιδας αυτου οτι εσφαγης και ηγορασας τω θεω ημας εν τω αιματι σου εκ πασης φυλης και γλωσσης και λαου και εθνους');
INSERT INTO `bible_verses_tr` VALUES ('7645', '66', '5', '10', 'και εποιησας ημας τω θεω ημων βασιλεις και ιερεις και βασιλευσομεν επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7646', '66', '5', '11', 'και ειδον και ηκουσα φωνην αγγελων πολλων κυκλοθεν του θρονου και των ζωων και των πρεσβυτερων και  {VAR2: ην ο αριθμος αυτων μυριαδες μυριαδων και } χιλιαδες χιλιαδων');
INSERT INTO `bible_verses_tr` VALUES ('7647', '66', '5', '12', 'λεγοντες φωνη μεγαλη αξιον εστιν το αρνιον το εσφαγμενον λαβειν την δυναμιν και πλουτον και σοφιαν και ισχυν και τιμην και δοξαν και ευλογιαν');
INSERT INTO `bible_verses_tr` VALUES ('7648', '66', '5', '13', 'και παν κτισμα ο εστιν εν τω ουρανω και εν τη γη και υποκατω της γης και επι της θαλασσης α εστιν και τα εν αυτοις παντα ηκουσα λεγοντας τω καθημενω επι του θρονου και τω αρνιω η ευλογια και η τιμη και η δοξα και το κρατος εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7649', '66', '5', '14', 'και τα τεσσαρα ζωα ελεγον αμην και οι εικοσιτεσσαρες πρεσβυτεροι επεσαν και προσεκυνησαν ζωντι εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7650', '66', '6', '1', 'και ειδον οτε ηνοιξεν το αρνιον μιαν εκ των σφραγιδων και ηκουσα ενος εκ των τεσσαρων ζωων λεγοντος ως φωνης βροντης ερχου και βλεπε');
INSERT INTO `bible_verses_tr` VALUES ('7651', '66', '6', '2', 'και ειδον και ιδου ιππος λευκος και ο καθημενος επ αυτω εχων τοξον και εδοθη αυτω στεφανος και εξηλθεν νικων και ινα νικηση');
INSERT INTO `bible_verses_tr` VALUES ('7652', '66', '6', '3', 'και οτε ηνοιξεν την δευτεραν σφραγιδα ηκουσα του δευτερου ζωου λεγοντος ερχου και βλεπε');
INSERT INTO `bible_verses_tr` VALUES ('7653', '66', '6', '4', 'και εξηλθεν αλλος ιππος πυρρος και τω καθημενω επ αυτω εδοθη αυτω λαβειν την ειρηνην απο της γης και ινα αλληλους σφαξωσιν και εδοθη αυτω μαχαιρα μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('7654', '66', '6', '5', 'και οτε ηνοιξεν την τριτην σφραγιδα ηκουσα του τριτου ζωου λεγοντος ερχου και βλεπε και ειδον και ιδου ιππος μελας και ο καθημενος επ αυτω εχων ζυγον εν τη χειρι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7655', '66', '6', '6', 'και ηκουσα φωνην εν μεσω των τεσσαρων ζωων λεγουσαν χοινιξ σιτου δηναριου και τρεις χοινικες κριθης δηναριου και το ελαιον και τον οινον μη αδικησης');
INSERT INTO `bible_verses_tr` VALUES ('7656', '66', '6', '7', 'και οτε ηνοιξεν την σφραγιδα την τεταρτην ηκουσα φωνην του τεταρτου ζωου λεγουσαν ερχου και βλεπε');
INSERT INTO `bible_verses_tr` VALUES ('7657', '66', '6', '8', 'και ειδον και ιδου ιππος χλωρος και ο καθημενος επανω αυτου ονομα αυτω ο θανατος και ο αδης ακολουθει μετ αυτου και εδοθη αυτοις εξουσια αποκτειναι επι το τεταρτον της γης εν ρομφαια και εν λιμω και εν θανατω και υπο των θηριων της γης');
INSERT INTO `bible_verses_tr` VALUES ('7658', '66', '6', '9', 'και οτε ηνοιξεν την πεμπτην σφραγιδα ειδον υποκατω του θυσιαστηριου τας ψυχας των εσφαγμενων δια τον λογον του θεου και δια την μαρτυριαν ην ειχον');
INSERT INTO `bible_verses_tr` VALUES ('7659', '66', '6', '10', 'και εκραζον φωνη μεγαλη λεγοντες εως ποτε ο δεσποτης ο αγιος και ο αληθινος ου κρινεις και εκδικεις το αιμα ημων απο των κατοικουντων επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7660', '66', '6', '11', 'και εδοθησαν εκαστοις στολαι λευκαι και ερρεθη αυτοις ινα αναπαυσωνται ετι χρονον μικρον εως ου πληρωσονται και οι συνδουλοι αυτων και οι αδελφοι αυτων οι μελλοντες αποκτεινεσθαι ως και αυτοι');
INSERT INTO `bible_verses_tr` VALUES ('7661', '66', '6', '12', 'και ειδον οτε ηνοιξεν την σφραγιδα την εκτην και ιδου σεισμος μεγας εγενετο και ο ηλιος εγενετο μελας ως σακκος τριχινος και η σεληνη εγενετο ως αιμα');
INSERT INTO `bible_verses_tr` VALUES ('7662', '66', '6', '13', 'και οι αστερες του ουρανου επεσαν εις την γην ως συκη βαλλει τους ολυνθους αυτης υπο μεγαλου ανεμου σειομενη');
INSERT INTO `bible_verses_tr` VALUES ('7663', '66', '6', '14', 'και ουρανος απεχωρισθη ως βιβλιον ειλισσομενον και παν ορος και νησος εκ των τοπων αυτων εκινηθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7664', '66', '6', '15', 'και οι βασιλεις της γης και οι μεγιστανες και οι πλουσιοι και οι χιλιαρχοι και οι δυνατοι και πας δουλος και πας ελευθερος εκρυψαν εαυτους εις τα σπηλαια και εις τας πετρας των ορεων');
INSERT INTO `bible_verses_tr` VALUES ('7665', '66', '6', '16', 'και λεγουσιν τοις ορεσιν και ταις πετραις πεσετε εφ ημας και κρυψατε ημας απο προσωπου του καθημενου επι του θρονου και απο της οργης του αρνιου');
INSERT INTO `bible_verses_tr` VALUES ('7666', '66', '6', '17', 'οτι ηλθεν η ημερα η μεγαλη της οργης αυτου και τις δυναται σταθηναι');
INSERT INTO `bible_verses_tr` VALUES ('7667', '66', '7', '1', 'και μετα ταυτα ειδον τεσσαρας αγγελους εστωτας επι τας τεσσαρας γωνιας της γης κρατουντας τους τεσσαρας ανεμους της γης ινα μη πνεη ανεμος επι της γης μητε επι της θαλασσης μητε επι παν δενδρον');
INSERT INTO `bible_verses_tr` VALUES ('7668', '66', '7', '2', 'και ειδον αλλον αγγελον  {VAR1: αναβαντα } {VAR2: αναβαινοντα } απο ανατολης ηλιου εχοντα σφραγιδα θεου ζωντος και εκραξεν φωνη μεγαλη τοις τεσσαρσιν αγγελοις οις εδοθη αυτοις αδικησαι την γην και την θαλασσαν');
INSERT INTO `bible_verses_tr` VALUES ('7669', '66', '7', '3', 'λεγων μη αδικησητε την γην μητε την θαλασσαν μητε τα δενδρα αχρις ου  {VAR1: σφραγιζωμεν } {VAR2: σφραγισωμεν } τους δουλους του θεου ημων επι των μετωπων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7670', '66', '7', '4', 'και ηκουσα τον αριθμον των εσφραγισμενων ρμδ χιλιαδες εσφραγισμενοι εκ πασης φυλης υιων ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('7671', '66', '7', '5', 'εκ φυλης ιουδα ιβ χιλιαδες εσφραγισμενοι εκ φυλης ρουβην ιβ χιλιαδες εσφραγισμενοι εκ φυλης γαδ ιβ χιλιαδες εσφραγισμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7672', '66', '7', '6', 'εκ φυλης ασηρ ιβ χιλιαδες εσφραγισμενοι εκ φυλης νεφθαλειμ ιβ χιλιαδες εσφραγισμενοι εκ φυλης μανασση ιβ χιλιαδες εσφραγισμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7673', '66', '7', '7', 'εκ φυλης συμεων ιβ χιλιαδες εσφραγισμενοι εκ φυλης λευι ιβ χιλιαδες εσφραγισμενοι εκ φυλης ισαχαρ ιβ χιλιαδες εσφραγισμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7674', '66', '7', '8', 'εκ φυλης ζαβουλων ιβ χιλιαδες εσφραγισμενοι εκ φυλης ιωσηφ ιβ χιλιαδες εσφραγισμενοι εκ φυλης βενιαμιν ιβ χιλιαδες εσφραγισμενοι');
INSERT INTO `bible_verses_tr` VALUES ('7675', '66', '7', '9', 'μετα ταυτα ειδον και ιδου οχλος πολυς ον αριθμησαι αυτον ουδεις ηδυνατο εκ παντος εθνους και φυλων και λαων και γλωσσων εστωτες ενωπιον του θρονου και ενωπιον του αρνιου περιβεβλημενοι στολας λευκας και φοινικες εν ταις χερσιν αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7676', '66', '7', '10', 'και κραζοντες φωνη μεγαλη λεγοντες η σωτηρια τω  {VAR2: θεω ημων τω } καθημενω επι του θρονου  {VAR1: του θεου ημων } και τω αρνιω');
INSERT INTO `bible_verses_tr` VALUES ('7677', '66', '7', '11', 'και παντες οι αγγελοι εστηκεσαν κυκλω του θρονου και των πρεσβυτερων και των τεσσαρων ζωων και επεσον ενωπιον του θρονου επι προσωπον αυτων και προσεκυνησαν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('7678', '66', '7', '12', 'λεγοντες αμην η ευλογια και η δοξα και η σοφια και η ευχαριστια και η τιμη και η δυναμις και η ισχυς τω θεω ημων εις τους αιωνας των αιωνων αμην');
INSERT INTO `bible_verses_tr` VALUES ('7679', '66', '7', '13', 'και απεκριθη εις εκ των πρεσβυτερων λεγων μοι ουτοι οι περιβεβλημενοι τας στολας τας λευκας τινες εισιν και ποθεν ηλθον');
INSERT INTO `bible_verses_tr` VALUES ('7680', '66', '7', '14', 'και ειρηκα αυτω κυριε συ οιδας και ειπεν μοι ουτοι εισιν οι ερχομενοι εκ της θλιψεως της μεγαλης και επλυναν τας στολας αυτων και ελευκαναν  {VAR1: στολας αυτων } {VAR2: αυτας } εν τω αιματι του αρνιου');
INSERT INTO `bible_verses_tr` VALUES ('7681', '66', '7', '15', 'δια τουτο εισιν ενωπιον του θρονου του θεου και λατρευουσιν αυτω ημερας και νυκτος εν τω ναω αυτου και ο καθημενος επι του θρονου σκηνωσει επ αυτους');
INSERT INTO `bible_verses_tr` VALUES ('7682', '66', '7', '16', 'ου πεινασουσιν ετι ουδε διψησουσιν ετι ουδε μη πεση επ αυτους ο ηλιος ουδε παν καυμα');
INSERT INTO `bible_verses_tr` VALUES ('7683', '66', '7', '17', 'οτι το αρνιον το ανα μεσον του θρονου ποιμανει αυτους και οδηγησει αυτους επι ζωσας πηγας υδατων και εξαλειψει ο θεος παν δακρυον απο των οφθαλμων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7684', '66', '8', '1', 'και οτε ηνοιξεν την σφραγιδα την εβδομην εγενετο σιγη εν τω ουρανω ως ημιωριον');
INSERT INTO `bible_verses_tr` VALUES ('7685', '66', '8', '2', 'και ειδον τους επτα αγγελους οι ενωπιον του θεου εστηκασιν και εδοθησαν αυτοις επτα σαλπιγγες');
INSERT INTO `bible_verses_tr` VALUES ('7686', '66', '8', '3', 'και αλλος αγγελος ηλθεν και εσταθη επι το θυσιαστηριον εχων λιβανωτον χρυσουν και εδοθη αυτω θυμιαματα πολλα ινα δωση ταις προσευχαις των αγιων παντων επι το θυσιαστηριον το χρυσουν το ενωπιον του θρονου');
INSERT INTO `bible_verses_tr` VALUES ('7687', '66', '8', '4', 'και ανεβη ο καπνος των θυμιαματων ταις προσευχαις των αγιων εκ χειρος του αγγελου ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7688', '66', '8', '5', 'και ειληφεν ο αγγελος το λιβανωτον και εγεμισεν αυτο εκ του πυρος του θυσιαστηριου και εβαλεν εις την γην και εγενοντο φωναι και βρονται και αστραπαι και σεισμος');
INSERT INTO `bible_verses_tr` VALUES ('7689', '66', '8', '6', 'και οι επτα αγγελοι  {VAR2: οι } εχοντες τας επτα σαλπιγγας ητοιμασαν εαυτους ινα σαλπισωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7690', '66', '8', '7', 'και ο πρωτος αγγελος εσαλπισεν και εγενετο χαλαζα και πυρ μεμιγμενα αιματι και εβληθη εις την γην και το τριτον των δενδρων κατεκαη και πας χορτος χλωρος κατεκαη');
INSERT INTO `bible_verses_tr` VALUES ('7691', '66', '8', '8', 'και ο δευτερος αγγελος εσαλπισεν και ως ορος μεγα πυρι καιομενον εβληθη εις την θαλασσαν και εγενετο το τριτον της θαλασσης αιμα');
INSERT INTO `bible_verses_tr` VALUES ('7692', '66', '8', '9', 'και απεθανεν το τριτον των κτισματων των εν τη θαλασση τα εχοντα ψυχας και το τριτον των πλοιων διεφθαρη');
INSERT INTO `bible_verses_tr` VALUES ('7693', '66', '8', '10', 'και ο τριτος αγγελος εσαλπισεν και επεσεν εκ του ουρανου αστηρ μεγας καιομενος ως λαμπας και επεσεν επι το τριτον των ποταμων και επι τας πηγας υδατων');
INSERT INTO `bible_verses_tr` VALUES ('7694', '66', '8', '11', 'και το ονομα του αστερος λεγεται αψινθος και γινεται το τριτον  {VAR2: των υδατων } εις αψινθον και πολλοι ανθρωπων απεθανον εκ των υδατων οτι επικρανθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7695', '66', '8', '12', 'και ο τεταρτος αγγελος εσαλπισεν και επληγη το τριτον του ηλιου και το τριτον της σεληνης και το τριτον των αστερων ινα σκοτισθη το τριτον αυτων και η ημερα μη φαινη το τριτον αυτης και η νυξ ομοιως');
INSERT INTO `bible_verses_tr` VALUES ('7696', '66', '8', '13', 'και ειδον και ηκουσα ενος αγγελου πετωμενου εν μεσουρανηματι λεγοντος φωνη μεγαλη ουαι ουαι ουαι τοις κατοικουσιν επι της γης εκ των λοιπων φωνων της σαλπιγγος των τριων αγγελων των μελλοντων σαλπιζειν');
INSERT INTO `bible_verses_tr` VALUES ('7697', '66', '9', '1', 'και ο πεμπτος αγγελος εσαλπισεν και ειδον αστερα εκ του ουρανου πεπτωκοτα εις την γην και εδοθη αυτω η κλεις του φρεατος της αβυσσου');
INSERT INTO `bible_verses_tr` VALUES ('7698', '66', '9', '2', 'και ηνοιξεν το φρεαρ της αβυσσου και ανεβη καπνος εκ του φρεατος ως καπνος καμινου μεγαλης και εσκοτισθη ο ηλιος και ο αηρ εκ του καπνου του φρεατος');
INSERT INTO `bible_verses_tr` VALUES ('7699', '66', '9', '3', 'και εκ του καπνου εξηλθον ακριδες εις την γην και εδοθη αυταις εξουσια ως εχουσιν εξουσιαν οι σκορπιοι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7700', '66', '9', '4', 'και ερρεθη αυταις ινα μη αδικησωσιν τον χορτον της γης ουδε παν χλωρον ουδε παν δενδρον ει μη τους ανθρωπους μονους οιτινες ουκ εχουσιν την σφραγιδα του θεου επι των μετωπων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7701', '66', '9', '5', 'και εδοθη αυταις ινα μη αποκτεινωσιν αυτους αλλ ινα βασανισθωσιν μηνας πεντε και ο βασανισμος αυτων ως βασανισμος σκορπιου οταν παιση ανθρωπον');
INSERT INTO `bible_verses_tr` VALUES ('7702', '66', '9', '6', 'και εν ταις ημεραις εκειναις ζητησουσιν οι ανθρωποι τον θανατον και ουχ ευρησουσιν αυτον και επιθυμησουσιν αποθανειν και φευξεται ο θανατος απ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7703', '66', '9', '7', 'και τα ομοιωματα των ακριδων ομοια ιπποις ητοιμασμενοις εις πολεμον και επι τας κεφαλας αυτων ως στεφανοι ομοιοι χρυσω και τα προσωπα αυτων ως προσωπα ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('7704', '66', '9', '8', 'και ειχον τριχας ως τριχας γυναικων και οι οδοντες αυτων ως λεοντων ησαν');
INSERT INTO `bible_verses_tr` VALUES ('7705', '66', '9', '9', 'και ειχον θωρακας ως θωρακας σιδηρους και η φωνη των πτερυγων αυτων ως φωνη αρματων ιππων πολλων τρεχοντων εις πολεμον');
INSERT INTO `bible_verses_tr` VALUES ('7706', '66', '9', '10', 'και εχουσιν ουρας ομοιας σκορπιοις και κεντρα ην εν ταις ουραις αυτων και η εξουσια αυτων αδικησαι τους ανθρωπους μηνας πεντε');
INSERT INTO `bible_verses_tr` VALUES ('7707', '66', '9', '11', 'και εχουσιν  {VAR1: εφ αυτων } {VAR2: επ αυτων } βασιλεα τον αγγελον της αβυσσου ονομα αυτω εβραιστι αβαδδων και εν τη ελληνικη ονομα εχει απολλυων');
INSERT INTO `bible_verses_tr` VALUES ('7708', '66', '9', '12', 'η ουαι η μια απηλθεν ιδου ερχονται ετι δυο ουαι μετα ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('7709', '66', '9', '13', 'και ο εκτος αγγελος εσαλπισεν και ηκουσα φωνην μιαν εκ των τεσσαρων κερατων του θυσιαστηριου του χρυσου του ενωπιον του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7710', '66', '9', '14', 'λεγουσαν τω εκτω αγγελω ος ειχε την σαλπιγγα λυσον τους τεσσαρας αγγελους τους δεδεμενους επι τω ποταμω τω μεγαλω ευφρατη');
INSERT INTO `bible_verses_tr` VALUES ('7711', '66', '9', '15', 'και ελυθησαν οι τεσσαρες αγγελοι οι ητοιμασμενοι εις την ωραν και ημεραν και μηνα και ενιαυτον ινα αποκτεινωσιν το τριτον των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('7712', '66', '9', '16', 'και ο αριθμος στρατευματων του ιππικου δυο μυριαδες μυριαδων και ηκουσα τον αριθμον αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7713', '66', '9', '17', 'και ουτως ειδον τους ιππους εν τη ορασει και τους καθημενους επ αυτων εχοντας θωρακας πυρινους και υακινθινους και θειωδεις και αι κεφαλαι των ιππων ως κεφαλαι λεοντων και εκ των στοματων αυτων εκπορευεται πυρ και καπνος και θειον');
INSERT INTO `bible_verses_tr` VALUES ('7714', '66', '9', '18', 'υπο των τριων τουτων απεκτανθησαν το τριτον των ανθρωπων εκ του πυρος και εκ του καπνου και εκ του θειου του εκπορευομενου εκ των στοματων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7715', '66', '9', '19', '{VAR1: αι γαρ εξουσιαι } {VAR2: η γαρ εξουσια } αυτων εν τω στοματι αυτων  {VAR1: εισιν } {VAR2: εστιν και εν ταις ουραις αυτων } αι γαρ ουραι αυτων ομοιαι οφεσιν εχουσαι κεφαλας και εν αυταις αδικουσιν');
INSERT INTO `bible_verses_tr` VALUES ('7716', '66', '9', '20', 'και οι λοιποι των ανθρωπων οι ουκ απεκτανθησαν εν ταις πληγαις ταυταις  {VAR1: ουτε } {VAR2: ου } μετενοησαν εκ των εργων των χειρων αυτων ινα μη προσκυνησωσιν τα δαιμονια και ειδωλα τα χρυσα και τα αργυρα και τα χαλκα και τα λιθινα και τα ξυλινα α ουτε βλεπειν δυναται ουτε ακουειν ουτε περιπατειν');
INSERT INTO `bible_verses_tr` VALUES ('7717', '66', '9', '21', 'και ου μετενοησαν εκ των φονων αυτων ουτε εκ των φαρμακειων αυτων ουτε εκ της πορνειας αυτων ουτε εκ των κλεμματων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7718', '66', '10', '1', 'και ειδον αλλον αγγελον ισχυρον καταβαινοντα εκ του ουρανου περιβεβλημενον νεφελην και ιρις επι της κεφαλης και το προσωπον αυτου ως ο ηλιος και οι ποδες αυτου ως στυλοι πυρος');
INSERT INTO `bible_verses_tr` VALUES ('7719', '66', '10', '2', 'και ειχεν εν τη χειρι αυτου βιβλαριδιον ανεωγμενον και εθηκεν τον ποδα αυτου τον δεξιον επι την θαλασσαν τον δε ευωνυμον επι την γην');
INSERT INTO `bible_verses_tr` VALUES ('7720', '66', '10', '3', 'και εκραξεν φωνη μεγαλη ωσπερ λεων μυκαται και οτε εκραξεν ελαλησαν αι επτα βρονται τας εαυτων φωνας');
INSERT INTO `bible_verses_tr` VALUES ('7721', '66', '10', '4', 'και οτε ελαλησαν αι επτα βρονται τας φωνας εαυτων εμελλον γραφειν και ηκουσα φωνην εκ του ουρανου λεγουσαν μοι σφραγισον α ελαλησαν αι επτα βρονται και μη ταυτα γραψης');
INSERT INTO `bible_verses_tr` VALUES ('7722', '66', '10', '5', 'και ο αγγελος ον ειδον εστωτα επι της θαλασσης και επι της γης ηρεν την χειρα αυτου εις τον ουρανον');
INSERT INTO `bible_verses_tr` VALUES ('7723', '66', '10', '6', 'και ωμοσεν εν τω ζωντι εις τους αιωνας των αιωνων ος εκτισεν τον ουρανον και τα εν αυτω και την γην και τα εν αυτη και την θαλασσαν και τα εν αυτη οτι χρονος ουκ εσται ετι');
INSERT INTO `bible_verses_tr` VALUES ('7724', '66', '10', '7', 'αλλα εν ταις ημεραις της φωνης του εβδομου αγγελου οταν μελλη σαλπιζειν και τελεσθη το μυστηριον του θεου ως ευηγγελισεν τοις εαυτου δουλοις τοις προφηταις');
INSERT INTO `bible_verses_tr` VALUES ('7725', '66', '10', '8', 'και η φωνη ην ηκουσα εκ του ουρανου παλιν λαλουσα μετ εμου και λεγουσα υπαγε λαβε το βιβλαριδιον το ηνεωγμενον εν τη χειρι αγγελου του εστωτος επι της θαλασσης και επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7726', '66', '10', '9', 'και απηλθον προς τον αγγελον λεγων αυτω δος μοι το βιβλαριδιον και λεγει μοι λαβε και καταφαγε αυτο και πικρανει σου την κοιλιαν αλλ εν τω στοματι σου εσται γλυκυ ως μελι');
INSERT INTO `bible_verses_tr` VALUES ('7727', '66', '10', '10', 'και ελαβον το βιβλαριδιον εκ της χειρος του αγγελου και κατεφαγον αυτο και ην εν τω στοματι μου ως μελι γλυκυ και οτε εφαγον αυτο επικρανθη η κοιλια μου');
INSERT INTO `bible_verses_tr` VALUES ('7728', '66', '10', '11', 'και λεγει μοι δει σε παλιν προφητευσαι επι λαοις και εθνεσιν και γλωσσαις και βασιλευσιν πολλοις');
INSERT INTO `bible_verses_tr` VALUES ('7729', '66', '11', '1', 'και εδοθη μοι καλαμος ομοιος ραβδω  {VAR2: και ο αγγελος ειστηκει } λεγων εγειραι και μετρησον τον ναον του θεου και το θυσιαστηριον και τους προσκυνουντας εν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7730', '66', '11', '2', 'και την αυλην την  {VAR1: εσωθεν } {VAR2: εξωθεν } του ναου εκβαλε εξω και μη αυτην μετρησης οτι εδοθη τοις εθνεσιν και την πολιν την αγιαν πατησουσιν μηνας τεσσαρακοντα δυο');
INSERT INTO `bible_verses_tr` VALUES ('7731', '66', '11', '3', 'και δωσω τοις δυσιν μαρτυσιν μου και προφητευσουσιν ημερας χιλιας διακοσιας εξηκοντα περιβεβλημενοι σακκους');
INSERT INTO `bible_verses_tr` VALUES ('7732', '66', '11', '4', 'ουτοι εισιν αι δυο ελαιαι και  {VAR2: αι } δυο λυχνιαι αι ενωπιον του θεου της γης εστωσαι');
INSERT INTO `bible_verses_tr` VALUES ('7733', '66', '11', '5', 'και ει τις αυτους θελη αδικησαι πυρ εκπορευεται εκ του στοματος αυτων και κατεσθιει τους εχθρους αυτων και ει τις αυτους θελη αδικησαι ουτως δει αυτον αποκτανθηναι');
INSERT INTO `bible_verses_tr` VALUES ('7734', '66', '11', '6', 'ουτοι εχουσιν εξουσιαν κλεισαι τον ουρανον ινα μη βρεχη υετος εν ημεραις αυτων της προφητειας και εξουσιαν εχουσιν επι των υδατων στρεφειν αυτα εις αιμα και παταξαι την γην παση πληγη οσακις εαν θελησωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7735', '66', '11', '7', 'και οταν τελεσωσιν την μαρτυριαν αυτων το θηριον το αναβαινον εκ της αβυσσου ποιησει πολεμον μετ αυτων και νικησει αυτους και αποκτενει αυτους');
INSERT INTO `bible_verses_tr` VALUES ('7736', '66', '11', '8', 'και τα πτωματα αυτων επι της πλατειας πολεως της μεγαλης ητις καλειται πνευματικως σοδομα και αιγυπτος οπου και ο κυριος ημων εσταυρωθη');
INSERT INTO `bible_verses_tr` VALUES ('7737', '66', '11', '9', 'και βλεψουσιν εκ των λαων και φυλων και γλωσσων και εθνων τα πτωματα αυτων ημερας τρεις και ημισυ και τα πτωματα αυτων ουκ αφησουσιν τεθηναι εις μνηματα');
INSERT INTO `bible_verses_tr` VALUES ('7738', '66', '11', '10', 'και οι κατοικουντες επι της γης χαρουσιν επ αυτοις και ευφρανθησονται και δωρα πεμψουσιν αλληλοις οτι ουτοι οι δυο προφηται εβασανισαν τους κατοικουντας επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7739', '66', '11', '11', 'και μετα τας τρεις ημερας και ημισυ πνευμα ζωης εκ του θεου εισηλθεν επ αυτους και εστησαν επι τους ποδας αυτων και φοβος μεγας επεσεν επι τους θεωρουντας αυτους');
INSERT INTO `bible_verses_tr` VALUES ('7740', '66', '11', '12', 'και ηκουσαν φωνην μεγαλην εκ του ουρανου λεγουσαν αυτοις αναβητε ωδε και ανεβησαν εις τον ουρανον εν τη νεφελη και εθεωρησαν αυτους οι εχθροι αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7741', '66', '11', '13', 'και εν εκεινη τη ωρα εγενετο σεισμος μεγας και το δεκατον της πολεως επεσεν και απεκτανθησαν εν τω σεισμω ονοματα ανθρωπων χιλιαδες επτα και οι λοιποι εμφοβοι εγενοντο και εδωκαν δοξαν τω θεω του ουρανου');
INSERT INTO `bible_verses_tr` VALUES ('7742', '66', '11', '14', 'η ουαι η δευτερα απηλθεν  {VAR2: και } ιδου η ουαι η τριτη ερχεται ταχυ');
INSERT INTO `bible_verses_tr` VALUES ('7743', '66', '11', '15', 'και ο εβδομος αγγελος εσαλπισεν και εγενοντο φωναι μεγαλαι εν τω ουρανω λεγουσαι εγενοντο αι βασιλειαι του κοσμου του κυριου ημων και του χριστου αυτου και βασιλευσει εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7744', '66', '11', '16', 'και οι εικοσι και τεσσαρες πρεσβυτεροι οι ενωπιον του θεου καθημενοι επι τους θρονους αυτων επεσαν επι τα προσωπα αυτων και προσεκυνησαν τω θεω');
INSERT INTO `bible_verses_tr` VALUES ('7745', '66', '11', '17', 'λεγοντες ευχαριστουμεν σοι κυριε ο θεος ο παντοκρατωρ ο ων και ο ην και ο ερχομενος οτι ειληφας την δυναμιν σου την μεγαλην και εβασιλευσας');
INSERT INTO `bible_verses_tr` VALUES ('7746', '66', '11', '18', 'και τα εθνη ωργισθησαν και ηλθεν η οργη σου και ο καιρος των νεκρων κριθηναι και δουναι τον μισθον τοις δουλοις σου τοις προφηταις και τοις αγιοις και τοις φοβουμενοις το ονομα σου τοις μικροις και τοις μεγαλοις και διαφθειραι τους διαφθειροντας την γην');
INSERT INTO `bible_verses_tr` VALUES ('7747', '66', '11', '19', 'και ηνοιγη ο ναος του θεου εν τω ουρανω και ωφθη η κιβωτος της διαθηκης αυτου εν τω ναω αυτου και εγενοντο αστραπαι και φωναι και βρονται και σεισμος και χαλαζα μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('7748', '66', '12', '1', 'και σημειον μεγα ωφθη εν τω ουρανω γυνη περιβεβλημενη τον ηλιον και η σεληνη υποκατω των ποδων αυτης και επι της κεφαλης αυτης στεφανος αστερων δωδεκα');
INSERT INTO `bible_verses_tr` VALUES ('7749', '66', '12', '2', 'και εν γαστρι εχουσα κραζει ωδινουσα και βασανιζομενη τεκειν');
INSERT INTO `bible_verses_tr` VALUES ('7750', '66', '12', '3', 'και ωφθη αλλο σημειον εν τω ουρανω και ιδου δρακων μεγας πυρρος εχων κεφαλας επτα και κερατα δεκα και επι τας κεφαλας αυτου διαδηματα επτα');
INSERT INTO `bible_verses_tr` VALUES ('7751', '66', '12', '4', 'και η ουρα αυτου συρει το τριτον των αστερων του ουρανου και εβαλεν αυτους εις την γην και ο δρακων εστηκεν ενωπιον της γυναικος της μελλουσης τεκειν ινα οταν τεκη το τεκνον αυτης καταφαγη');
INSERT INTO `bible_verses_tr` VALUES ('7752', '66', '12', '5', 'και ετεκεν υιον αρρενα ος μελλει ποιμαινειν παντα τα εθνη εν ραβδω σιδηρα και ηρπασθη το τεκνον αυτης προς τον θεον και τον θρονον αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7753', '66', '12', '6', 'και η γυνη εφυγεν εις την ερημον οπου εχει τοπον ητοιμασμενον απο του θεου ινα εκει τρεφωσιν αυτην ημερας χιλιας διακοσιας εξηκοντα');
INSERT INTO `bible_verses_tr` VALUES ('7754', '66', '12', '7', 'και εγενετο πολεμος εν τω ουρανω ο μιχαηλ και οι αγγελοι αυτου επολεμησαν κατα του δρακοντος και ο δρακων επολεμησεν και οι αγγελοι αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7755', '66', '12', '8', 'και ουκ ισχυσαν ουτε τοπος ευρεθη αυτων ετι εν τω ουρανω');
INSERT INTO `bible_verses_tr` VALUES ('7756', '66', '12', '9', 'και εβληθη ο δρακων ο μεγας ο οφις ο αρχαιος ο καλουμενος διαβολος και ο σατανας ο πλανων την οικουμενην ολην εβληθη εις την γην και οι αγγελοι αυτου μετ αυτου εβληθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7757', '66', '12', '10', 'και ηκουσα φωνην μεγαλην λεγουσαν εν τω ουρανω αρτι εγενετο η σωτηρια και η δυναμις και η βασιλεια του θεου ημων και η εξουσια του χριστου αυτου οτι κατεβληθη ο κατηγορος των αδελφων ημων ο κατηγορων αυτων ενωπιον του θεου ημων ημερας και νυκτος');
INSERT INTO `bible_verses_tr` VALUES ('7758', '66', '12', '11', 'και αυτοι ενικησαν αυτον δια το αιμα του αρνιου και δια τον λογον της μαρτυριας αυτων και ουκ ηγαπησαν την ψυχην αυτων αχρι θανατου');
INSERT INTO `bible_verses_tr` VALUES ('7759', '66', '12', '12', 'δια τουτο ευφραινεσθε οι ουρανοι και οι εν αυτοις σκηνουντες ουαι τοις κατοικουσιν την γην και την θαλασσαν οτι κατεβη ο διαβολος προς υμας εχων θυμον μεγαν ειδως οτι ολιγον καιρον εχει');
INSERT INTO `bible_verses_tr` VALUES ('7760', '66', '12', '13', 'και οτε ειδεν ο δρακων οτι εβληθη εις την γην εδιωξεν την γυναικα ητις ετεκεν τον αρρενα');
INSERT INTO `bible_verses_tr` VALUES ('7761', '66', '12', '14', 'και εδοθησαν τη γυναικι δυο πτερυγες του αετου του μεγαλου ινα πετηται εις την ερημον εις τον τοπον αυτης οπου τρεφεται εκει καιρον και καιρους και ημισυ καιρου απο προσωπου του οφεως');
INSERT INTO `bible_verses_tr` VALUES ('7762', '66', '12', '15', 'και εβαλεν ο οφις οπισω της γυναικος εκ του στοματος αυτου υδωρ ως ποταμον ινα ταυτην ποταμοφορητον ποιηση');
INSERT INTO `bible_verses_tr` VALUES ('7763', '66', '12', '16', 'και εβοηθησεν η γη τη γυναικι και ηνοιξεν η γη το στομα αυτης και κατεπιεν τον ποταμον ον εβαλεν ο δρακων εκ του στοματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7764', '66', '12', '17', 'και ωργισθη ο δρακων επι τη γυναικι και απηλθεν ποιησαι πολεμον μετα των λοιπων του σπερματος αυτης των τηρουντων τας εντολας του θεου και εχοντων την μαρτυριαν του ιησου χριστου');
INSERT INTO `bible_verses_tr` VALUES ('7765', '66', '12', '18', 'και εσταθην επι την αμμον της θαλασσης');
INSERT INTO `bible_verses_tr` VALUES ('7766', '66', '13', '1', 'και ειδον εκ της θαλασσης θηριον αναβαινον εχον κεφαλας επτα και κερατα δεκα και επι των κερατων αυτου δεκα διαδηματα και επι τας κεφαλας αυτου ονομα βλασφημιας');
INSERT INTO `bible_verses_tr` VALUES ('7767', '66', '13', '2', 'και το θηριον ο ειδον ην ομοιον παρδαλει και οι ποδες αυτου ως αρκτου και το στομα αυτου ως στομα λεοντος και εδωκεν αυτω ο δρακων την δυναμιν αυτου και τον θρονον αυτου και εξουσιαν μεγαλην');
INSERT INTO `bible_verses_tr` VALUES ('7768', '66', '13', '3', 'και ειδον μιαν των κεφαλων αυτου ως εσφαγμενην εις θανατον και η πληγη του θανατου αυτου εθεραπευθη και  {VAR1: εθαυμασθη εν ολη τη γη } {VAR2: εθαυμασεν ολη η γη } οπισω του θηριου');
INSERT INTO `bible_verses_tr` VALUES ('7769', '66', '13', '4', 'και προσεκυνησαν τον δρακοντα ος εδωκεν εξουσιαν τω θηριω και προσεκυνησαν το θηριον λεγοντες τις ομοιος τω θηριω τις δυναται πολεμησαι μετ αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7770', '66', '13', '5', 'και εδοθη αυτω στομα λαλουν μεγαλα και βλασφημιας και εδοθη αυτω εξουσια ποιησαι μηνας τεσσαρακοντα δυο');
INSERT INTO `bible_verses_tr` VALUES ('7771', '66', '13', '6', 'και ηνοιξεν το στομα αυτου εις βλασφημιαν προς τον θεον βλασφημησαι το ονομα αυτου και την σκηνην αυτου και τους εν τω ουρανω σκηνουντας');
INSERT INTO `bible_verses_tr` VALUES ('7772', '66', '13', '7', 'και εδοθη αυτω πολεμον ποιησαι μετα των αγιων και νικησαι αυτους και εδοθη αυτω εξουσια επι πασαν φυλην και γλωσσαν και εθνος');
INSERT INTO `bible_verses_tr` VALUES ('7773', '66', '13', '8', 'και προσκυνησουσιν αυτω παντες οι κατοικουντες επι της γης ων ου γεγραπται τα ονοματα εν τη βιβλω της ζωης του αρνιου εσφαγμενου απο καταβολης κοσμου');
INSERT INTO `bible_verses_tr` VALUES ('7774', '66', '13', '9', 'ει τις εχει ους ακουσατω');
INSERT INTO `bible_verses_tr` VALUES ('7775', '66', '13', '10', 'ει τις αιχμαλωσιαν συναγει εις αιχμαλωσιαν υπαγει ει τις εν μαχαιρα αποκτενει δει αυτον εν μαχαιρα αποκτανθηναι ωδε εστιν η υπομονη και η πιστις των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('7776', '66', '13', '11', 'και ειδον αλλο θηριον αναβαινον εκ της γης και ειχεν κερατα δυο ομοια αρνιω και ελαλει ως δρακων');
INSERT INTO `bible_verses_tr` VALUES ('7777', '66', '13', '12', 'και την εξουσιαν του πρωτου θηριου πασαν ποιει ενωπιον αυτου και ποιει την γην και τους κατοικουντας εν αυτη ινα προσκυνησωσιν το θηριον το πρωτον ου εθεραπευθη η πληγη του θανατου αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7778', '66', '13', '13', 'και ποιει σημεια μεγαλα ινα και πυρ ποιη καταβαινειν εκ του ουρανου εις την γην ενωπιον των ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('7779', '66', '13', '14', 'και πλανα τους κατοικουντας επι της γης δια τα σημεια α εδοθη αυτω ποιησαι ενωπιον του θηριου λεγων τοις κατοικουσιν επι της γης ποιησαι εικονα τω θηριω ο εχει την πληγην της μαχαιρας και εζησεν');
INSERT INTO `bible_verses_tr` VALUES ('7780', '66', '13', '15', 'και εδοθη αυτω δουναι πνευμα τη εικονι του θηριου ινα και λαληση η εικων του θηριου και ποιηση οσοι αν μη προσκυνησωσιν την εικονα του θηριου ινα αποκτανθωσιν');
INSERT INTO `bible_verses_tr` VALUES ('7781', '66', '13', '16', 'και ποιει παντας τους μικρους και τους μεγαλους και τους πλουσιους και τους πτωχους και τους ελευθερους και τους δουλους ινα δωση αυτοις χαραγμα επι της χειρος αυτων της δεξιας η επι των μετωπων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7782', '66', '13', '17', 'και ινα μη τις δυνηται αγορασαι η πωλησαι ει μη ο εχων το χαραγμα η το ονομα του θηριου η τον αριθμον του ονοματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7783', '66', '13', '18', 'ωδε η σοφια εστιν ο εχων τον νουν ψηφισατω τον αριθμον του θηριου αριθμος γαρ ανθρωπου εστιν και ο αριθμος αυτου χξς');
INSERT INTO `bible_verses_tr` VALUES ('7784', '66', '14', '1', 'και ειδον και ιδου αρνιον εστηκος επι το ορος σιων και μετ αυτου εκατον τεσσαρακοντα τεσσαρες χιλιαδες εχουσαι το ονομα του πατρος αυτου γεγραμμενον επι των μετωπων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7785', '66', '14', '2', 'και ηκουσα φωνην εκ του ουρανου ως φωνην υδατων πολλων και ως φωνην βροντης μεγαλης και φωνην ηκουσα κιθαρωδων κιθαριζοντων εν ταις κιθαραις αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7786', '66', '14', '3', 'και αδουσιν ως ωδην καινην ενωπιον του θρονου και ενωπιον των τεσσαρων ζωων και των πρεσβυτερων και ουδεις ηδυνατο μαθειν την ωδην ει μη αι εκατον τεσσαρακοντα τεσσαρες χιλιαδες οι ηγορασμενοι απο της γης');
INSERT INTO `bible_verses_tr` VALUES ('7787', '66', '14', '4', 'ουτοι εισιν οι μετα γυναικων ουκ εμολυνθησαν παρθενοι γαρ εισιν ουτοι εισιν οι ακολουθουντες τω αρνιω οπου αν υπαγη ουτοι ηγορασθησαν απο των ανθρωπων απαρχη τω θεω και τω αρνιω');
INSERT INTO `bible_verses_tr` VALUES ('7788', '66', '14', '5', 'και εν τω στοματι αυτων ουχ ευρεθη δολος αμωμοι γαρ εισιν ενωπιον του θρονου του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7789', '66', '14', '6', 'και ειδον αλλον αγγελον πετωμενον εν μεσουρανηματι εχοντα ευαγγελιον αιωνιον ευαγγελισαι τους κατοικουντας επι της γης και παν εθνος και φυλην και γλωσσαν και λαον');
INSERT INTO `bible_verses_tr` VALUES ('7790', '66', '14', '7', 'λεγοντα εν φωνη μεγαλη φοβηθητε τον θεον και δοτε αυτω δοξαν οτι ηλθεν η ωρα της κρισεως αυτου και προσκυνησατε τω ποιησαντι τον ουρανον και την γην και  {VAR2: την } θαλασσαν και πηγας υδατων');
INSERT INTO `bible_verses_tr` VALUES ('7791', '66', '14', '8', 'και αλλος αγγελος ηκολουθησεν λεγων επεσεν επεσεν βαβυλων η πολις η μεγαλη οτι εκ του οινου του θυμου της πορνειας αυτης πεποτικεν παντα εθνη');
INSERT INTO `bible_verses_tr` VALUES ('7792', '66', '14', '9', 'και τριτος αγγελος ηκολουθησεν αυτοις λεγων εν φωνη μεγαλη ει τις το θηριον προσκυνει και την εικονα αυτου και λαμβανει χαραγμα επι του μετωπου αυτου η επι την χειρα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7793', '66', '14', '10', 'και αυτος πιεται εκ του οινου του θυμου του θεου του κεκερασμενου ακρατου εν τω ποτηριω της οργης αυτου και βασανισθησεται εν πυρι και θειω ενωπιον των αγιων αγγελων και ενωπιον του αρνιου');
INSERT INTO `bible_verses_tr` VALUES ('7794', '66', '14', '11', 'και ο καπνος του βασανισμου αυτων αναβαινει εις αιωνας αιωνων και ουκ εχουσιν αναπαυσιν ημερας και νυκτος οι προσκυνουντες το θηριον και την εικονα αυτου και ει τις λαμβανει το χαραγμα του ονοματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7795', '66', '14', '12', 'ωδε υπομονη των αγιων εστιν ωδε οι τηρουντες τας εντολας του θεου και την πιστιν ιησου');
INSERT INTO `bible_verses_tr` VALUES ('7796', '66', '14', '13', 'και ηκουσα φωνης εκ του ουρανου λεγουσης μοι γραψον μακαριοι οι νεκροι οι εν κυριω αποθνησκοντες απαρτι ναι λεγει το πνευμα ινα αναπαυσωνται εκ των κοπων αυτων τα δε εργα αυτων ακολουθει μετ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7797', '66', '14', '14', 'και ειδον και ιδου νεφελη λευκη και επι την νεφελην καθημενος ομοιος υιω ανθρωπου εχων επι της κεφαλης αυτου στεφανον χρυσουν και εν τη χειρι αυτου δρεπανον οξυ');
INSERT INTO `bible_verses_tr` VALUES ('7798', '66', '14', '15', 'και αλλος αγγελος εξηλθεν εκ του ναου κραζων εν μεγαλη φωνη τω καθημενω επι της νεφελης πεμψον το δρεπανον σου και θερισον οτι ηλθεν σοι η ωρα του θερισαι οτι εξηρανθη ο θερισμος της γης');
INSERT INTO `bible_verses_tr` VALUES ('7799', '66', '14', '16', 'και εβαλεν ο καθημενος επι την νεφελην το δρεπανον αυτου επι την γην και εθερισθη η γη');
INSERT INTO `bible_verses_tr` VALUES ('7800', '66', '14', '17', 'και αλλος αγγελος εξηλθεν εκ του ναου του εν τω ουρανω εχων και αυτος δρεπανον οξυ');
INSERT INTO `bible_verses_tr` VALUES ('7801', '66', '14', '18', 'και αλλος αγγελος εξηλθεν εκ του θυσιαστηριου εχων εξουσιαν επι του πυρος και εφωνησεν κραυγη μεγαλη τω εχοντι το δρεπανον το οξυ λεγων πεμψον σου το δρεπανον το οξυ και τρυγησον τους βοτρυας της  {VAR2: αμπελου της } γης οτι ηκμασαν αι σταφυλαι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7802', '66', '14', '19', 'και εβαλεν ο αγγελος το δρεπανον αυτου εις την γην και ετρυγησεν την αμπελον της γης και εβαλεν εις την ληνον του θυμου του θεου την μεγαλην');
INSERT INTO `bible_verses_tr` VALUES ('7803', '66', '14', '20', 'και επατηθη η ληνος εξω της πολεως και εξηλθεν αιμα εκ της ληνου αχρι των χαλινων των ιππων απο σταδιων χιλιων εξακοσιων');
INSERT INTO `bible_verses_tr` VALUES ('7804', '66', '15', '1', 'και ειδον αλλο σημειον εν τω ουρανω μεγα και θαυμαστον αγγελους επτα εχοντας πληγας επτα τας εσχατας οτι εν αυταις ετελεσθη ο θυμος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7805', '66', '15', '2', 'και ειδον ως θαλασσαν υαλινην μεμιγμενην πυρι και τους νικωντας εκ του θηριου και εκ της εικονος αυτου και εκ του χαραγματος αυτου εκ του αριθμου του ονοματος αυτου εστωτας επι την θαλασσαν την υαλινην εχοντας κιθαρας του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7806', '66', '15', '3', 'και αδουσιν την ωδην μωσεως  {VAR2: του } δουλου του θεου και την ωδην του αρνιου λεγοντες μεγαλα και θαυμαστα τα εργα σου κυριε ο θεος ο παντοκρατωρ δικαιαι και αληθιναι αι οδοι σου ο βασιλευς των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('7807', '66', '15', '4', 'τις ου μη φοβηθη σε κυριε και δοξαση το ονομα σου οτι μονος οσιος οτι παντα τα εθνη ηξουσιν και προσκυνησουσιν ενωπιον σου οτι τα δικαιωματα σου εφανερωθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7808', '66', '15', '5', 'και μετα ταυτα ειδον και ιδου ηνοιγη ο ναος της σκηνης του μαρτυριου εν τω ουρανω');
INSERT INTO `bible_verses_tr` VALUES ('7809', '66', '15', '6', 'και εξηλθον οι επτα αγγελοι εχοντες τας επτα πληγας εκ του ναου ενδεδυμενοι λινον καθαρον και λαμπρον και περιεζωσμενοι περι τα στηθη ζωνας χρυσας');
INSERT INTO `bible_verses_tr` VALUES ('7810', '66', '15', '7', 'και εν εκ των τεσσαρων ζωων εδωκεν τοις επτα αγγελοις επτα φιαλας χρυσας γεμουσας του θυμου του θεου του ζωντος εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7811', '66', '15', '8', 'και εγεμισθη ο ναος καπνου εκ της δοξης του θεου και εκ της δυναμεως αυτου και ουδεις ηδυνατο εισελθειν εις τον ναον αχρι τελεσθωσιν αι επτα πληγαι των επτα αγγελων');
INSERT INTO `bible_verses_tr` VALUES ('7812', '66', '16', '1', 'και ηκουσα φωνης μεγαλης εκ του ναου λεγουσης τοις επτα αγγελοις υπαγετε και εκχεατε τας φιαλας του θυμου του θεου εις την γην');
INSERT INTO `bible_verses_tr` VALUES ('7813', '66', '16', '2', 'και απηλθεν ο πρωτος και εξεχεεν την φιαλην αυτου επι την γην και εγενετο ελκος κακον και πονηρον εις τους ανθρωπους τους εχοντας το χαραγμα του θηριου και τους τη εικονι αυτου προσκυνουντας');
INSERT INTO `bible_verses_tr` VALUES ('7814', '66', '16', '3', 'και ο δευτερος αγγελος εξεχεεν την φιαλην αυτου εις την θαλασσαν και εγενετο αιμα ως νεκρου και πασα ψυχη ζωσα απεθανεν εν τη θαλασση');
INSERT INTO `bible_verses_tr` VALUES ('7815', '66', '16', '4', 'και ο τριτος αγγελος εξεχεεν την φιαλην αυτου εις τους ποταμους και εις τας πηγας των υδατων και εγενετο αιμα');
INSERT INTO `bible_verses_tr` VALUES ('7816', '66', '16', '5', 'και ηκουσα του αγγελου των υδατων λεγοντος δικαιος κυριε ει ο ων και ο ην και ο  {VAR1: οσιος } {VAR2: εσομενος } οτι ταυτα εκρινας');
INSERT INTO `bible_verses_tr` VALUES ('7817', '66', '16', '6', 'οτι αιμα αγιων και προφητων εξεχεαν και αιμα αυτοις εδωκας πιειν αξιοι γαρ εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7818', '66', '16', '7', 'και ηκουσα αλλου εκ του θυσιαστηριου λεγοντος ναι κυριε ο θεος ο παντοκρατωρ αληθιναι και δικαιαι αι κρισεις σου');
INSERT INTO `bible_verses_tr` VALUES ('7819', '66', '16', '8', 'και ο τεταρτος αγγελος εξεχεεν την φιαλην αυτου επι τον ηλιον και εδοθη αυτω καυματισαι τους ανθρωπους εν πυρι');
INSERT INTO `bible_verses_tr` VALUES ('7820', '66', '16', '9', 'και εκαυματισθησαν οι ανθρωποι καυμα μεγα και εβλασφημησαν το ονομα του θεου του εχοντος εξουσιαν επι τας πληγας ταυτας και ου μετενοησαν δουναι αυτω δοξαν');
INSERT INTO `bible_verses_tr` VALUES ('7821', '66', '16', '10', 'και ο πεμπτος αγγελος εξεχεεν την φιαλην αυτου επι τον θρονον του θηριου και εγενετο η βασιλεια αυτου εσκοτωμενη και εμασσωντο τας γλωσσας αυτων εκ του πονου');
INSERT INTO `bible_verses_tr` VALUES ('7822', '66', '16', '11', 'και εβλασφημησαν τον θεον του ουρανου εκ των πονων αυτων και εκ των ελκων αυτων και ου μετενοησαν εκ των εργων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7823', '66', '16', '12', 'και ο εκτος αγγελος εξεχεεν την φιαλην αυτου επι τον ποταμον τον μεγαν τον ευφρατην και εξηρανθη το υδωρ αυτου ινα ετοιμασθη η οδος των βασιλεων των απο ανατολων ηλιου');
INSERT INTO `bible_verses_tr` VALUES ('7824', '66', '16', '13', 'και ειδον εκ του στοματος του δρακοντος και εκ του στοματος του θηριου και εκ του στοματος του ψευδοπροφητου πνευματα τρια ακαθαρτα ομοια βατραχοις');
INSERT INTO `bible_verses_tr` VALUES ('7825', '66', '16', '14', 'εισιν γαρ πνευματα δαιμονων ποιουντα σημεια  {VAR1: εκπορευεσθαι } {VAR2: α εκπορευεται } επι τους βασιλεις της γης και της οικουμενης ολης συναγαγειν αυτους εις  {VAR2: τον } πολεμον της ημερας εκεινης της μεγαλης του θεου του παντοκρατορος');
INSERT INTO `bible_verses_tr` VALUES ('7826', '66', '16', '15', 'ιδου ερχομαι ως κλεπτης μακαριος ο γρηγορων και τηρων τα ιματια αυτου ινα μη γυμνος περιπατη και βλεπωσιν την ασχημοσυνην αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7827', '66', '16', '16', 'και συνηγαγεν αυτους εις τον τοπον τον καλουμενον εβραιστι αρμαγεδδων');
INSERT INTO `bible_verses_tr` VALUES ('7828', '66', '16', '17', 'και ο εβδομος αγγελος εξεχεεν την φιαλην αυτου εις τον αερα και εξηλθεν φωνη μεγαλη απο του ναου του ουρανου απο του θρονου λεγουσα γεγονεν');
INSERT INTO `bible_verses_tr` VALUES ('7829', '66', '16', '18', 'και εγενοντο φωναι και βρονται και αστραπαι και σεισμος εγενετο μεγας οιος ουκ εγενετο αφ ου οι ανθρωποι εγενοντο επι της γης τηλικουτος σεισμος ουτως μεγας');
INSERT INTO `bible_verses_tr` VALUES ('7830', '66', '16', '19', 'και εγενετο η πολις η μεγαλη εις τρια μερη και αι πολεις των εθνων επεσον και βαβυλων η μεγαλη εμνησθη ενωπιον του θεου δουναι αυτη το ποτηριον του οινου του θυμου της οργης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7831', '66', '16', '20', 'και πασα νησος εφυγεν και ορη ουχ ευρεθησαν');
INSERT INTO `bible_verses_tr` VALUES ('7832', '66', '16', '21', 'και χαλαζα μεγαλη ως ταλαντιαια καταβαινει εκ του ουρανου επι τους ανθρωπους και εβλασφημησαν οι ανθρωποι τον θεον εκ της πληγης της χαλαζης οτι μεγαλη εστιν η πληγη αυτης σφοδρα');
INSERT INTO `bible_verses_tr` VALUES ('7833', '66', '17', '1', 'και ηλθεν εις εκ των επτα αγγελων των εχοντων τας επτα φιαλας και ελαλησεν μετ εμου λεγων μοι δευρο δειξω σοι το κριμα της πορνης της μεγαλης της καθημενης επι των υδατων των πολλων');
INSERT INTO `bible_verses_tr` VALUES ('7834', '66', '17', '2', 'μεθ ης επορνευσαν οι βασιλεις της γης και εμεθυσθησαν εκ του οινου της πορνειας αυτης οι κατοικουντες την γην');
INSERT INTO `bible_verses_tr` VALUES ('7835', '66', '17', '3', 'και απηνεγκεν με εις ερημον εν πνευματι και ειδον γυναικα καθημενην επι θηριον κοκκινον γεμον ονοματων βλασφημιας εχον κεφαλας επτα και κερατα δεκα');
INSERT INTO `bible_verses_tr` VALUES ('7836', '66', '17', '4', 'και η γυνη  {VAR1: η } {VAR2: ην } περιβεβλημενη πορφυρα και κοκκινω και κεχρυσωμενη χρυσω και λιθω τιμιω και μαργαριταις εχουσα χρυσουν ποτηριον εν τη χειρι αυτης γεμον βδελυγματων και ακαθαρτητος πορνειας αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7837', '66', '17', '5', 'και επι το μετωπον αυτης ονομα γεγραμμενον μυστηριον βαβυλων η μεγαλη η μητηρ των πορνων και των βδελυγματων της γης');
INSERT INTO `bible_verses_tr` VALUES ('7838', '66', '17', '6', 'και ειδον την γυναικα μεθυουσαν εκ του αιματος των αγιων και εκ του αιματος των μαρτυρων ιησου και εθαυμασα ιδων αυτην θαυμα μεγα');
INSERT INTO `bible_verses_tr` VALUES ('7839', '66', '17', '7', 'και ειπεν μοι ο αγγελος δια τι εθαυμασας εγω σοι ερω το μυστηριον της γυναικος και του θηριου του βασταζοντος αυτην του εχοντος τας επτα κεφαλας και τα δεκα κερατα');
INSERT INTO `bible_verses_tr` VALUES ('7840', '66', '17', '8', '{VAR2: το } θηριον ο ειδες ην και ουκ εστιν και μελλει αναβαινειν εκ της αβυσσου και εις απωλειαν υπαγειν και θαυμασονται οι κατοικουντες επι της γης ων ου γεγραπται τα ονοματα επι το βιβλιον της ζωης απο καταβολης κοσμου βλεποντες το θηριον ο τι ην και ουκ εστιν καιπερ εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7841', '66', '17', '9', 'ωδε ο νους ο εχων σοφιαν αι επτα κεφαλαι ορη εισιν επτα οπου η γυνη καθηται επ αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7842', '66', '17', '10', 'και βασιλεις επτα εισιν οι πεντε επεσαν και ο εις εστιν ο αλλος ουπω ηλθεν και οταν ελθη ολιγον αυτον δει μειναι');
INSERT INTO `bible_verses_tr` VALUES ('7843', '66', '17', '11', 'και το θηριον ο ην και ουκ εστιν και αυτος ογδοος εστιν και εκ των επτα εστιν και εις απωλειαν υπαγει');
INSERT INTO `bible_verses_tr` VALUES ('7844', '66', '17', '12', 'και τα δεκα κερατα α ειδες δεκα βασιλεις εισιν οιτινες βασιλειαν ουπω ελαβον αλλ εξουσιαν ως βασιλεις μιαν ωραν λαμβανουσιν μετα του θηριου');
INSERT INTO `bible_verses_tr` VALUES ('7845', '66', '17', '13', 'ουτοι μιαν γνωμην εχουσιν και την δυναμιν και την εξουσιαν εαυτων τω θηριω διαδιδωσουσιν');
INSERT INTO `bible_verses_tr` VALUES ('7846', '66', '17', '14', 'ουτοι μετα του αρνιου πολεμησουσιν και το αρνιον νικησει αυτους οτι κυριος κυριων εστιν και βασιλευς βασιλεων και οι μετ αυτου κλητοι και εκλεκτοι και πιστοι');
INSERT INTO `bible_verses_tr` VALUES ('7847', '66', '17', '15', 'και λεγει μοι τα υδατα α ειδες ου η πορνη καθηται λαοι και οχλοι εισιν και εθνη και γλωσσαι');
INSERT INTO `bible_verses_tr` VALUES ('7848', '66', '17', '16', 'και τα δεκα κερατα α ειδες επι το θηριον ουτοι μισησουσιν την πορνην και ηρημωμενην ποιησουσιν αυτην και γυμνην και τας σαρκας αυτης φαγονται και αυτην κατακαυσουσιν εν πυρι');
INSERT INTO `bible_verses_tr` VALUES ('7849', '66', '17', '17', 'ο γαρ θεος εδωκεν εις τας καρδιας αυτων ποιησαι την γνωμην αυτου και ποιησαι μιαν γνωμην και δουναι την βασιλειαν αυτων τω θηριω αχρι τελεσθη τα ρηματα του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7850', '66', '17', '18', 'και η γυνη ην ειδες εστιν η πολις η μεγαλη η εχουσα βασιλειαν επι των βασιλεων της γης');
INSERT INTO `bible_verses_tr` VALUES ('7851', '66', '18', '1', 'και μετα ταυτα ειδον  {VAR2: αλλον } αγγελον καταβαινοντα εκ του ουρανου εχοντα εξουσιαν μεγαλην και η γη εφωτισθη εκ της δοξης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7852', '66', '18', '2', 'και εκραξεν εν ισχυι φωνη μεγαλη λεγων επεσεν επεσεν βαβυλων η μεγαλη και εγενετο κατοικητηριον δαιμονων και φυλακη παντος πνευματος ακαθαρτου και φυλακη παντος ορνεου ακαθαρτου και μεμισημενου');
INSERT INTO `bible_verses_tr` VALUES ('7853', '66', '18', '3', 'οτι εκ του οινου του θυμου της πορνειας αυτης πεπωκεν παντα τα εθνη και οι βασιλεις της γης μετ αυτης επορνευσαν και οι εμποροι της γης εκ της δυναμεως του στρηνους αυτης επλουτησαν');
INSERT INTO `bible_verses_tr` VALUES ('7854', '66', '18', '4', 'και ηκουσα αλλην φωνην εκ του ουρανου λεγουσαν εξελθετε εξ αυτης ο λαος μου ινα μη συγκοινωνησητε ταις αμαρτιαις αυτης και ινα μη λαβητε εκ των πληγων αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7855', '66', '18', '5', 'οτι  {VAR1: ηκολουθησαν } {VAR2: εκολληθησαν } αυτης αι αμαρτιαι αχρι του ουρανου και εμνημονευσεν ο θεος τα αδικηματα αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7856', '66', '18', '6', 'αποδοτε αυτη ως και αυτη απεδωκεν υμιν και διπλωσατε αυτη διπλα κατα τα εργα αυτης εν τω ποτηριω ω εκερασεν κερασατε αυτη διπλουν');
INSERT INTO `bible_verses_tr` VALUES ('7857', '66', '18', '7', 'οσα εδοξασεν εαυτην και εστρηνιασεν τοσουτον δοτε αυτη βασανισμον και πενθος οτι εν τη καρδια αυτης λεγει καθημαι βασιλισσα και χηρα ουκ ειμι και πενθος ου μη ιδω');
INSERT INTO `bible_verses_tr` VALUES ('7858', '66', '18', '8', 'δια τουτο εν μια ημερα ηξουσιν αι πληγαι αυτης θανατος και πενθος και λιμος και εν πυρι κατακαυθησεται οτι ισχυρος κυριος ο θεος ο κρινων αυτην');
INSERT INTO `bible_verses_tr` VALUES ('7859', '66', '18', '9', 'και κλαυσονται αυτην και κοψονται επ αυτη οι βασιλεις της γης οι μετ αυτης πορνευσαντες και στρηνιασαντες οταν βλεπωσιν τον καπνον της πυρωσεως αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7860', '66', '18', '10', 'απο μακροθεν εστηκοτες δια τον φοβον του βασανισμου αυτης λεγοντες ουαι ουαι η πολις η μεγαλη βαβυλων η πολις η ισχυρα οτι εν μια ωρα ηλθεν η κρισις σου');
INSERT INTO `bible_verses_tr` VALUES ('7861', '66', '18', '11', 'και οι εμποροι της γης κλαιουσιν και πενθουσιν επ αυτη οτι τον γομον αυτων ουδεις αγοραζει ουκετι');
INSERT INTO `bible_verses_tr` VALUES ('7862', '66', '18', '12', 'γομον χρυσου και αργυρου και λιθου τιμιου και μαργαριτου και βυσσου και πορφυρας και σηρικου και κοκκινου και παν ξυλον θυινον και παν σκευος ελεφαντινον και παν σκευος εκ ξυλου τιμιωτατου και χαλκου και σιδηρου και μαρμαρου');
INSERT INTO `bible_verses_tr` VALUES ('7863', '66', '18', '13', 'και κιναμωμον και θυμιαματα και μυρον και λιβανον και οινον και ελαιον και σεμιδαλιν και σιτον και κτηνη και προβατα και ιππων και ρεδων και σωματων και ψυχας ανθρωπων');
INSERT INTO `bible_verses_tr` VALUES ('7864', '66', '18', '14', 'και η οπωρα της επιθυμιας της ψυχης σου απηλθεν απο σου και παντα τα λιπαρα και τα λαμπρα απηλθεν απο σου και ουκετι ου μη ευρησης αυτα');
INSERT INTO `bible_verses_tr` VALUES ('7865', '66', '18', '15', 'οι εμποροι τουτων οι πλουτησαντες απ αυτης απο μακροθεν στησονται δια τον φοβον του βασανισμου αυτης κλαιοντες και πενθουντες');
INSERT INTO `bible_verses_tr` VALUES ('7866', '66', '18', '16', 'και λεγοντες ουαι ουαι η πολις η μεγαλη η περιβεβλημενη βυσσινον και πορφυρουν και κοκκινον και κεχρυσωμενη εν χρυσω και λιθω τιμιω και μαργαριταις');
INSERT INTO `bible_verses_tr` VALUES ('7867', '66', '18', '17', 'οτι μια ωρα ηρημωθη ο τοσουτος πλουτος και πας κυβερνητης και πας επι των πλοιων ο ομιλος και ναυται και οσοι την θαλασσαν εργαζονται απο μακροθεν εστησαν');
INSERT INTO `bible_verses_tr` VALUES ('7868', '66', '18', '18', 'και εκραζον ορωντες τον καπνον της πυρωσεως αυτης λεγοντες τις ομοια τη πολει τη μεγαλη');
INSERT INTO `bible_verses_tr` VALUES ('7869', '66', '18', '19', 'και εβαλον χουν επι τας κεφαλας αυτων και εκραζον κλαιοντες και πενθουντες λεγοντες ουαι ουαι η πολις η μεγαλη εν η επλουτησαν παντες οι εχοντες πλοια εν τη θαλασση εκ της τιμιοτητος αυτης οτι μια ωρα ηρημωθη');
INSERT INTO `bible_verses_tr` VALUES ('7870', '66', '18', '20', 'ευφραινου επ αυτην ουρανε και οι αγιοι αποστολοι και οι προφηται οτι εκρινεν ο θεος το κριμα υμων εξ αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7871', '66', '18', '21', 'και ηρεν εις αγγελος ισχυρος λιθον ως μυλον μεγαν και εβαλεν εις την θαλασσαν λεγων ουτως ορμηματι βληθησεται βαβυλων η μεγαλη πολις και ου μη ευρεθη ετι');
INSERT INTO `bible_verses_tr` VALUES ('7872', '66', '18', '22', 'και φωνη κιθαρωδων και μουσικων και αυλητων και σαλπιστων ου μη ακουσθη εν σοι ετι και πας τεχνιτης πασης τεχνης ου μη ευρεθη εν σοι ετι και φωνη μυλου ου μη ακουσθη εν σοι ετι');
INSERT INTO `bible_verses_tr` VALUES ('7873', '66', '18', '23', 'και φως λυχνου ου μη φανη εν σοι ετι και φωνη νυμφιου και νυμφης ου μη ακουσθη εν σοι ετι οτι οι εμποροι σου ησαν οι μεγιστανες της γης οτι εν τη φαρμακεια σου επλανηθησαν παντα τα εθνη');
INSERT INTO `bible_verses_tr` VALUES ('7874', '66', '18', '24', 'και εν αυτη αιμα προφητων και αγιων ευρεθη και παντων των εσφαγμενων επι της γης');
INSERT INTO `bible_verses_tr` VALUES ('7875', '66', '19', '1', 'και μετα ταυτα ηκουσα φωνην οχλου πολλου μεγαλην εν τω ουρανω λεγοντος αλληλουια η σωτηρια και η δοξα και η τιμη και η δυναμις κυριω τω θεω ημων');
INSERT INTO `bible_verses_tr` VALUES ('7876', '66', '19', '2', 'οτι αληθιναι και δικαιαι αι κρισεις αυτου οτι εκρινεν την πορνην την μεγαλην ητις εφθειρεν την γην εν τη πορνεια αυτης και εξεδικησεν το αιμα των δουλων αυτου εκ της χειρος αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7877', '66', '19', '3', 'και δευτερον ειρηκαν αλληλουια και ο καπνος αυτης αναβαινει εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7878', '66', '19', '4', 'και επεσαν οι πρεσβυτεροι οι εικοσι και τεσσαρες και τα τεσσαρα ζωα και προσεκυνησαν τω θεω τω καθημενω επι του θρονου λεγοντες αμην αλληλουια');
INSERT INTO `bible_verses_tr` VALUES ('7879', '66', '19', '5', 'και φωνη εκ του θρονου εξηλθεν λεγουσα αινειτε τον θεον ημων παντες οι δουλοι αυτου και οι φοβουμενοι αυτον και οι μικροι και οι μεγαλοι');
INSERT INTO `bible_verses_tr` VALUES ('7880', '66', '19', '6', 'και ηκουσα ως φωνην οχλου πολλου και ως φωνην υδατων πολλων και ως φωνην βροντων ισχυρων λεγοντας αλληλουια οτι εβασιλευσεν κυριος ο θεος ο παντοκρατωρ');
INSERT INTO `bible_verses_tr` VALUES ('7881', '66', '19', '7', 'χαιρωμεν και αγαλλιωμεθα και δωμεν την δοξαν αυτω οτι ηλθεν ο γαμος του αρνιου και η γυνη αυτου ητοιμασεν εαυτην');
INSERT INTO `bible_verses_tr` VALUES ('7882', '66', '19', '8', 'και εδοθη αυτη ινα περιβαληται βυσσινον καθαρον και λαμπρον το γαρ βυσσινον τα δικαιωματα εστιν των αγιων');
INSERT INTO `bible_verses_tr` VALUES ('7883', '66', '19', '9', 'και λεγει μοι γραψον μακαριοι οι εις το δειπνον του γαμου του αρνιου κεκλημενοι και λεγει μοι ουτοι οι λογοι αληθινοι εισιν του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7884', '66', '19', '10', 'και επεσον εμπροσθεν των ποδων αυτου προσκυνησαι αυτω και λεγει μοι ορα μη συνδουλος σου ειμι και των αδελφων σου των εχοντων την μαρτυριαν του ιησου τω θεω προσκυνησον η γαρ μαρτυρια του ιησου εστιν το πνευμα της προφητειας');
INSERT INTO `bible_verses_tr` VALUES ('7885', '66', '19', '11', 'και ειδον τον ουρανον ανεωγμενον και ιδου ιππος λευκος και ο καθημενος επ αυτον καλουμενος πιστος και αληθινος και εν δικαιοσυνη κρινει και πολεμει');
INSERT INTO `bible_verses_tr` VALUES ('7886', '66', '19', '12', 'οι δε οφθαλμοι αυτου ως φλοξ πυρος και επι την κεφαλην αυτου διαδηματα πολλα εχων ονομα γεγραμμενον ο ουδεις οιδεν ει μη αυτος');
INSERT INTO `bible_verses_tr` VALUES ('7887', '66', '19', '13', 'και περιβεβλημενος ιματιον βεβαμμενον αιματι και καλειται το ονομα αυτου ο λογος του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7888', '66', '19', '14', 'και τα στρατευματα  {VAR2: τα } εν τω ουρανω ηκολουθει αυτω εφ ιπποις λευκοις ενδεδυμενοι βυσσινον λευκον και καθαρον');
INSERT INTO `bible_verses_tr` VALUES ('7889', '66', '19', '15', 'και εκ του στοματος αυτου εκπορευεται ρομφαια οξεια ινα εν αυτη πατασση τα εθνη και αυτος ποιμανει αυτους εν ραβδω σιδηρα και αυτος πατει την ληνον του οινου του θυμου και της οργης του θεου του παντοκρατορος');
INSERT INTO `bible_verses_tr` VALUES ('7890', '66', '19', '16', 'και εχει επι το ιματιον και επι τον μηρον  {VAR1: αυτου το ονομα } {VAR2: αυτου ονομα } γεγραμμενον βασιλευς βασιλεων και κυριος κυριων');
INSERT INTO `bible_verses_tr` VALUES ('7891', '66', '19', '17', 'και ειδον ενα αγγελον εστωτα εν τω ηλιω και εκραξεν φωνη μεγαλη λεγων πασιν τοις ορνεοις τοις πετωμενοις εν μεσουρανηματι δευτε και συναγεσθε εις το δειπνον του μεγαλου θεου');
INSERT INTO `bible_verses_tr` VALUES ('7892', '66', '19', '18', 'ινα φαγητε σαρκας βασιλεων και σαρκας χιλιαρχων και σαρκας ισχυρων και σαρκας ιππων και των καθημενων επ αυτων και σαρκας παντων ελευθερων  {VAR2: τε } και δουλων και μικρων και μεγαλων');
INSERT INTO `bible_verses_tr` VALUES ('7893', '66', '19', '19', 'και ειδον το θηριον και τους βασιλεις της γης και τα στρατευματα αυτων συνηγμενα ποιησαι πολεμον μετα του καθημενου επι του ιππου και μετα του στρατευματος αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7894', '66', '19', '20', 'και επιασθη το θηριον και μετα τουτου ο ψευδοπροφητης ο ποιησας τα σημεια ενωπιον αυτου εν οις επλανησεν τους λαβοντας το χαραγμα του θηριου και τους προσκυνουντας τη εικονι αυτου ζωντες εβληθησαν οι δυο εις την λιμνην του πυρος την καιομενην εν τω θειω');
INSERT INTO `bible_verses_tr` VALUES ('7895', '66', '19', '21', 'και οι λοιποι απεκτανθησαν εν τη ρομφαια του καθημενου επι του ιππου τη εκπορευομενη εκ του στοματος αυτου και παντα τα ορνεα εχορτασθησαν εκ των σαρκων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7896', '66', '20', '1', 'και ειδον αγγελον καταβαινοντα εκ του ουρανου εχοντα την κλειδα της αβυσσου και αλυσιν μεγαλην επι την χειρα αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7897', '66', '20', '2', 'και εκρατησεν τον δρακοντα τον οφιν τον αρχαιον ος εστιν διαβολος και σατανας και εδησεν αυτον χιλια ετη');
INSERT INTO `bible_verses_tr` VALUES ('7898', '66', '20', '3', 'και εβαλεν αυτον εις την αβυσσον και εκλεισεν αυτον και εσφραγισεν επανω αυτου ινα μη πλανηση τα εθνη ετι αχρι τελεσθη τα χιλια ετη και μετα ταυτα δει αυτον λυθηναι μικρον χρονον');
INSERT INTO `bible_verses_tr` VALUES ('7899', '66', '20', '4', 'και ειδον θρονους και εκαθισαν επ αυτους και κριμα εδοθη αυτοις και τας ψυχας των πεπελεκισμενων δια την μαρτυριαν ιησου και δια τον λογον του θεου και οιτινες ου προσεκυνησαν τω θηριω ουτε την εικονα αυτου και ουκ ελαβον το χαραγμα επι το μετωπον αυτων και επι την χειρα αυτων και εζησαν και εβασιλευσαν μετα χριστου  {VAR1: τα } χιλια ετη');
INSERT INTO `bible_verses_tr` VALUES ('7900', '66', '20', '5', 'οι δε λοιποι των νεκρων ουκ ανεζησαν εως τελεσθη τα χιλια ετη αυτη η αναστασις η πρωτη');
INSERT INTO `bible_verses_tr` VALUES ('7901', '66', '20', '6', 'μακαριος και αγιος ο εχων μερος εν τη αναστασει τη πρωτη επι τουτων ο θανατος ο δευτερος ουκ εχει εξουσιαν αλλ εσονται ιερεις του θεου και του χριστου και βασιλευσουσιν μετ αυτου χιλια ετη');
INSERT INTO `bible_verses_tr` VALUES ('7902', '66', '20', '7', 'και οταν τελεσθη τα χιλια ετη λυθησεται ο σατανας εκ της φυλακης αυτου');
INSERT INTO `bible_verses_tr` VALUES ('7903', '66', '20', '8', 'και εξελευσεται πλανησαι τα εθνη τα εν ταις τεσσαρσιν γωνιαις της γης τον γωγ και τον μαγωγ συναγαγειν αυτους εις πολεμον ων ο αριθμος ως η αμμος της θαλασσης');
INSERT INTO `bible_verses_tr` VALUES ('7904', '66', '20', '9', 'και ανεβησαν επι το πλατος της γης και εκυκλωσαν την παρεμβολην των αγιων και την πολιν την ηγαπημενην και κατεβη πυρ απο του θεου εκ του ουρανου και κατεφαγεν αυτους');
INSERT INTO `bible_verses_tr` VALUES ('7905', '66', '20', '10', 'και ο διαβολος ο πλανων αυτους εβληθη εις την λιμνην του πυρος και θειου οπου το θηριον και ο ψευδοπροφητης και βασανισθησονται ημερας και νυκτος εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7906', '66', '20', '11', 'και ειδον θρονον λευκον μεγαν και τον καθημενον επ αυτου ου απο προσωπου εφυγεν η γη και ο ουρανος και τοπος ουχ ευρεθη αυτοις');
INSERT INTO `bible_verses_tr` VALUES ('7907', '66', '20', '12', 'και ειδον τους νεκρους μικρους και μεγαλους εστωτας ενωπιον του θεου και βιβλια ηνεωχθησαν και βιβλιον αλλο ηνεωχθη ο εστιν της ζωης και εκριθησαν οι νεκροι εκ των γεγραμμενων εν τοις βιβλιοις κατα τα εργα αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7908', '66', '20', '13', 'και εδωκεν η θαλασσα τους εν αυτη νεκρους και ο θανατος και ο αδης εδωκαν τους εν αυτοις νεκρους και εκριθησαν εκαστος κατα τα εργα αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7909', '66', '20', '14', 'και ο θανατος και ο αδης εβληθησαν εις την λιμνην του πυρος ουτος εστιν ο δευτερος θανατος');
INSERT INTO `bible_verses_tr` VALUES ('7910', '66', '20', '15', 'και ει τις ουχ ευρεθη εν τη βιβλω της ζωης γεγραμμενος εβληθη εις την λιμνην του πυρος');
INSERT INTO `bible_verses_tr` VALUES ('7911', '66', '21', '1', 'και ειδον ουρανον καινον και γην καινην ο γαρ πρωτος ουρανος και η πρωτη γη παρηλθεν και η θαλασσα ουκ εστιν ετι');
INSERT INTO `bible_verses_tr` VALUES ('7912', '66', '21', '2', 'και εγω ιωαννης ειδον την πολιν την αγιαν ιερουσαλημ καινην καταβαινουσαν απο του θεου εκ του ουρανου ητοιμασμενην ως νυμφην κεκοσμημενην τω ανδρι αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7913', '66', '21', '3', 'και ηκουσα φωνης μεγαλης εκ του ουρανου λεγουσης ιδου η σκηνη του θεου μετα των ανθρωπων και σκηνωσει μετ αυτων και αυτοι λαοι αυτου εσονται και αυτος ο θεος εσται μετ αυτων θεος αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7914', '66', '21', '4', 'και εξαλειψει ο θεος παν δακρυον απο των οφθαλμων αυτων και ο θανατος ουκ εσται ετι ουτε πενθος ουτε κραυγη ουτε πονος ουκ εσται ετι οτι τα πρωτα απηλθον');
INSERT INTO `bible_verses_tr` VALUES ('7915', '66', '21', '5', 'και ειπεν ο καθημενος επι του θρονου ιδου καινα παντα ποιω και λεγει μοι γραψον οτι ουτοι οι λογοι αληθινοι και πιστοι εισιν');
INSERT INTO `bible_verses_tr` VALUES ('7916', '66', '21', '6', 'και ειπεν μοι γεγονεν εγω ειμι το α και το ω η αρχη και το τελος εγω τω διψωντι δωσω εκ της πηγης του υδατος της ζωης δωρεαν');
INSERT INTO `bible_verses_tr` VALUES ('7917', '66', '21', '7', 'ο νικων κληρονομησει παντα και εσομαι αυτω θεος και αυτος εσται μοι ο υιος');
INSERT INTO `bible_verses_tr` VALUES ('7918', '66', '21', '8', 'δειλοις δε και απιστοις και εβδελυγμενοις και φονευσιν και πορνοις και φαρμακευσιν και ειδωλολατραις και πασιν τοις ψευδεσιν το μερος αυτων εν τη λιμνη τη καιομενη πυρι και θειω ο εστιν δευτερος θανατος');
INSERT INTO `bible_verses_tr` VALUES ('7919', '66', '21', '9', 'και ηλθεν προς με εις των επτα αγγελων των εχοντων τας επτα φιαλας τας γεμουσας των επτα πληγων των εσχατων και ελαλησεν μετ εμου λεγων δευρο δειξω σοι την νυμφην του αρνιου την γυναικα');
INSERT INTO `bible_verses_tr` VALUES ('7920', '66', '21', '10', 'και απηνεγκεν με εν πνευματι επ ορος μεγα και υψηλον και εδειξεν μοι την πολιν την μεγαλην την αγιαν ιερουσαλημ καταβαινουσαν εκ του ουρανου απο του θεου');
INSERT INTO `bible_verses_tr` VALUES ('7921', '66', '21', '11', 'εχουσαν την δοξαν του θεου και ο φωστηρ αυτης ομοιος λιθω τιμιωτατω ως λιθω ιασπιδι κρυσταλλιζοντι');
INSERT INTO `bible_verses_tr` VALUES ('7922', '66', '21', '12', 'εχουσαν τε τειχος μεγα και υψηλον εχουσαν πυλωνας δωδεκα και επι τοις πυλωσιν αγγελους δωδεκα και ονοματα επιγεγραμμενα α εστιν των δωδεκα φυλων των υιων ισραηλ');
INSERT INTO `bible_verses_tr` VALUES ('7923', '66', '21', '13', 'απ ανατολης πυλωνες τρεις απο βορρα πυλωνες τρεις απο νοτου πυλωνες τρεις  {VAR2: και } απο δυσμων πυλωνες τρεις');
INSERT INTO `bible_verses_tr` VALUES ('7924', '66', '21', '14', 'και το τειχος της πολεως εχον θεμελιους δωδεκα και εν αυτοις ονοματα των δωδεκα αποστολων του αρνιου');
INSERT INTO `bible_verses_tr` VALUES ('7925', '66', '21', '15', 'και ο λαλων μετ εμου ειχεν καλαμον χρυσουν ινα μετρηση την πολιν και τους πυλωνας αυτης και το τειχος αυτης');
INSERT INTO `bible_verses_tr` VALUES ('7926', '66', '21', '16', 'και η πολις τετραγωνος κειται και το μηκος αυτης τοσουτον εστιν οσον και το πλατος και εμετρησεν την πολιν τω καλαμω επι σταδιων δωδεκα χιλιαδων το μηκος και το πλατος και το υψος αυτης ισα εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7927', '66', '21', '17', 'και εμετρησεν το τειχος αυτης εκατον τεσσαρακοντα τεσσαρων πηχων μετρον ανθρωπου ο εστιν αγγελου');
INSERT INTO `bible_verses_tr` VALUES ('7928', '66', '21', '18', 'και ην η ενδομησις του τειχους αυτης ιασπις και η πολις χρυσιον καθαρον ομοια υαλω καθαρω');
INSERT INTO `bible_verses_tr` VALUES ('7929', '66', '21', '19', 'και οι θεμελιοι του τειχους της πολεως παντι λιθω τιμιω κεκοσμημενοι ο θεμελιος ο πρωτος ιασπις ο δευτερος σαπφειρος ο τριτος χαλκηδων ο τεταρτος σμαραγδος');
INSERT INTO `bible_verses_tr` VALUES ('7930', '66', '21', '20', 'ο πεμπτος σαρδονυξ ο εκτος σαρδιος ο εβδομος χρυσολιθος ο ογδοος βηρυλλος ο  {VAR1: ενατος } {VAR2: εννατος } τοπαζιον ο δεκατος χρυσοπρασος ο ενδεκατος υακινθος ο δωδεκατος αμεθυστος');
INSERT INTO `bible_verses_tr` VALUES ('7931', '66', '21', '21', 'και οι δωδεκα πυλωνες δωδεκα μαργαριται ανα εις εκαστος των πυλωνων ην εξ ενος μαργαριτου και η πλατεια της πολεως χρυσιον καθαρον ως υαλος διαφανης');
INSERT INTO `bible_verses_tr` VALUES ('7932', '66', '21', '22', 'και ναον ουκ ειδον εν αυτη ο γαρ κυριος ο θεος ο παντοκρατωρ ναος αυτης εστιν και το αρνιον');
INSERT INTO `bible_verses_tr` VALUES ('7933', '66', '21', '23', 'και η πολις ου χρειαν εχει του ηλιου ουδε της σεληνης ινα φαινωσιν εν αυτη η γαρ δοξα του θεου εφωτισεν αυτην και ο λυχνος αυτης το αρνιον');
INSERT INTO `bible_verses_tr` VALUES ('7934', '66', '21', '24', 'και τα εθνη των σωζομενων εν τω φωτι αυτης περιπατησουσιν και οι βασιλεις της γης φερουσιν την δοξαν και την τιμην αυτων εις αυτην');
INSERT INTO `bible_verses_tr` VALUES ('7935', '66', '21', '25', 'και οι πυλωνες αυτης ου μη κλεισθωσιν ημερας νυξ γαρ ουκ εσται εκει');
INSERT INTO `bible_verses_tr` VALUES ('7936', '66', '21', '26', 'και οισουσιν την δοξαν και την τιμην των εθνων εις αυτην');
INSERT INTO `bible_verses_tr` VALUES ('7937', '66', '21', '27', 'και ου μη εισελθη εις αυτην παν κοινουν και ποιουν βδελυγμα και ψευδος ει μη οι γεγραμμενοι εν τω βιβλιω της ζωης του αρνιου');
INSERT INTO `bible_verses_tr` VALUES ('7938', '66', '22', '1', 'και εδειξεν μοι καθαρον ποταμον υδατος ζωης λαμπρον ως κρυσταλλον εκπορευομενον εκ του θρονου του θεου και του αρνιου');
INSERT INTO `bible_verses_tr` VALUES ('7939', '66', '22', '2', 'εν μεσω της πλατειας αυτης και του ποταμου εντευθεν και εντευθεν ξυλον ζωης ποιουν καρπους δωδεκα κατα μηνα ενα εκαστον αποδιδουν τον καρπον αυτου και τα φυλλα του ξυλου εις θεραπειαν των εθνων');
INSERT INTO `bible_verses_tr` VALUES ('7940', '66', '22', '3', 'και παν καταναθεμα ουκ εσται ετι και ο θρονος του θεου και του αρνιου εν αυτη εσται και οι δουλοι αυτου λατρευσουσιν αυτω');
INSERT INTO `bible_verses_tr` VALUES ('7941', '66', '22', '4', 'και οψονται το προσωπον αυτου και το ονομα αυτου επι των μετωπων αυτων');
INSERT INTO `bible_verses_tr` VALUES ('7942', '66', '22', '5', 'και νυξ ουκ εσται εκει και χρειαν ουκ εχουσιν λυχνου και φωτος ηλιου οτι κυριος ο θεος φωτιζει αυτους και βασιλευσουσιν εις τους αιωνας των αιωνων');
INSERT INTO `bible_verses_tr` VALUES ('7943', '66', '22', '6', 'και ειπεν μοι ουτοι οι λογοι πιστοι και αληθινοι και κυριος ο θεος των αγιων προφητων απεστειλεν τον αγγελον αυτου δειξαι τοις δουλοις αυτου α δει γενεσθαι εν ταχει');
INSERT INTO `bible_verses_tr` VALUES ('7944', '66', '22', '7', 'ιδου ερχομαι ταχυ μακαριος ο τηρων τους λογους της προφητειας του βιβλιου τουτου');
INSERT INTO `bible_verses_tr` VALUES ('7945', '66', '22', '8', 'και εγω ιωαννης ο βλεπων ταυτα και ακουων και οτε ηκουσα και εβλεψα επεσα προσκυνησαι εμπροσθεν των ποδων του αγγελου του δεικνυοντος μοι ταυτα');
INSERT INTO `bible_verses_tr` VALUES ('7946', '66', '22', '9', 'και λεγει μοι ορα μη συνδουλος σου γαρ ειμι και των αδελφων σου των προφητων και των τηρουντων τους λογους του βιβλιου τουτου τω θεω προσκυνησον');
INSERT INTO `bible_verses_tr` VALUES ('7947', '66', '22', '10', 'και λεγει μοι μη σφραγισης τους λογους της προφητειας του βιβλιου τουτου οτι ο καιρος εγγυς εστιν');
INSERT INTO `bible_verses_tr` VALUES ('7948', '66', '22', '11', 'ο αδικων αδικησατω ετι και ο ρυπων ρυπωσατω ετι και ο δικαιος δικαιωθητω ετι και ο αγιος αγιασθητω ετι');
INSERT INTO `bible_verses_tr` VALUES ('7949', '66', '22', '12', 'και ιδου ερχομαι ταχυ και ο μισθος μου μετ εμου αποδουναι εκαστω ως το εργον αυτου εσται');
INSERT INTO `bible_verses_tr` VALUES ('7950', '66', '22', '13', 'εγω ειμι το α και το ω αρχη και τελος ο πρωτος και ο εσχατος');
INSERT INTO `bible_verses_tr` VALUES ('7951', '66', '22', '14', 'μακαριοι οι ποιουντες τας εντολας αυτου ινα εσται η εξουσια αυτων επι το ξυλον της ζωης και τοις πυλωσιν εισελθωσιν εις την πολιν');
INSERT INTO `bible_verses_tr` VALUES ('7952', '66', '22', '15', 'εξω δε οι κυνες και οι φαρμακοι και οι πορνοι και οι φονεις και οι ειδωλολατραι και πας ο φιλων και ποιων ψευδος');
INSERT INTO `bible_verses_tr` VALUES ('7953', '66', '22', '16', 'εγω ιησους επεμψα τον αγγελον μου μαρτυρησαι υμιν ταυτα επι ταις εκκλησιαις εγω ειμι η ριζα και το γενος του δαβιδ ο αστηρ ο λαμπρος και ορθρινος');
INSERT INTO `bible_verses_tr` VALUES ('7954', '66', '22', '17', 'και το πνευμα και η νυμφη λεγουσιν ελθε και ο ακουων ειπατω ελθε και ο διψων ελθετω και ο θελων λαμβανετω το υδωρ ζωης δωρεαν');
INSERT INTO `bible_verses_tr` VALUES ('7955', '66', '22', '18', 'συμμαρτυρουμαι γαρ παντι ακουοντι τους λογους της προφητειας του βιβλιου τουτου εαν τις επιτιθη προς ταυτα επιθησει ο θεος επ αυτον τας πληγας τας γεγραμμενας εν βιβλιω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('7956', '66', '22', '19', 'και εαν τις αφαιρη απο των λογων βιβλου της προφητειας ταυτης αφαιρησει ο θεος το μερος αυτου απο βιβλου της ζωης και εκ της πολεως της αγιας και των γεγραμμενων εν βιβλιω τουτω');
INSERT INTO `bible_verses_tr` VALUES ('7957', '66', '22', '20', 'λεγει ο μαρτυρων ταυτα ναι ερχομαι ταχυ αμην ναι ερχου κυριε ιησου');
INSERT INTO `bible_verses_tr` VALUES ('7958', '66', '22', '21', 'η χαρις του κυριου ημων ιησου χριστου μετα παντων υμων αμην');
